  display: flex;
  justify-content: center;
  align-items: center;
}

.cube {
  width: 200px;
  height: 100%;
  position: absolute;
  transform-style: preserve-3d;
}

.cube .side {
  width: 200px;
  height: 200px;
  position: absolute;
  overflow: hidden;
}

.cube .front {
  transform: translateZ(100px);
}

.cube .back {
  transform: rotateX(180deg) translateZ(100px);
}

.cube .top {
  transform: rotateX(90deg) translateZ(100px);
}

.cube .bottom {
  transform: rotateX(-90deg) translateZ(100px);
}

.front, .back {
  background: #EDEDED;
}

.top, .bottom {
  background: #cecece;
}

.cube p {
  width: max-content;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-transform: uppercase;
  font-size: 40px;
  font-weight: 600;
  letter-spacing: -0.02em;
  line-height: 100%;
}
