code.styles.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  cursor: none;
}

.cursor {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10000;
  pointer-events: none;
}

.cursor img {
  width: 60px;
  height: 60px;
}

.gallery {
  position: absolute;
  width: 100vw;
  height: 100vh;
  background: #000;
  transform-style: preserve-3d;
  perspective: 2000px;
}

.block {
  position: absolute;
  width: 200px;
  height: 100px;
  background: #c0c0c0;
  transform-style: preserve-3d;
  transform: translateZ(1750px);
}

