.header {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 2;
  pointer-events: none;
}

h1 {
  color: #75fe97;
  text-transform: uppercase;
  font-family: "Arame";
  font-weight: 500;
  font-size: 100px;
  text-align: center;
  line-height: 1;
  pointer-events: none;
}

p {
  color: #fff;
  text-transform: uppercase;
  font-family: "Arame";
  font-weight: 400;
  font-size: 20px;
  text-align: center;
  line-height: 1.5;
  letter-spacing: 8px;
  margin: 1em 0;
  pointer-events: none;
}
