Step 2: Add custom Javascript

gsap.registerPlugin(Draggable, InertiaPlugin, Observer, ScrollTrigger);

function init3dImageCarousel() {
  let radius;
  let draggableInstance;
  let observerInstance;
  let spin;
  let intro;
  let lastWidth = window.innerWidth;

  const wrap = document.querySelector('[data-3d-carousel-wrap]');
  if (!wrap) return;

  // Define the radius of your cylinder here
  const calcRadius = () => {
    radius = window.innerWidth * 0.5;
  }

  // Destroy function to reset everything on resize
  const destroy = () => {
    draggableInstance && draggableInstance.kill();
    observerInstance && observerInstance.kill();
    spin && spin.kill();
    intro && intro.kill();
    ScrollTrigger.getAll().forEach(st => st.kill());
    const panels = wrap.querySelectorAll('[data-3d-carousel-panel]');
    gsap.set(panels, { clearProps: 'transform' });
  };

  // Create function that sets the spin, drag, and rotation
  const create = () => {
    calcRadius();

    const panels = wrap.querySelectorAll('[data-3d-carousel-panel]');
    const content = wrap.querySelectorAll('[data-3d-carousel-content]');
    const proxy = document.createElement('div');
    const wrapProgress = gsap.utils.wrap(0, 1);
    const dragDistance = window.innerWidth * 3; // Control the snapiness on drag
    let startProg;

    // Position panels in 3D space
    panels.forEach(p =>
      p.style.transformOrigin = `50% 50% ${-radius}px`
    );

    // Infinite rotation of all panels
    spin = gsap.fromTo(
      panels,
      { rotationY: i => (i * 360) / panels.length },
      { rotationY: '-=360', duration: 30, ease: 'none', repeat: -1 }
    );

    // cheeky workaround to create some 'buffer' when scrolling back up
    spin.progress(1000)

    draggableInstance = Draggable.create(proxy, {
      trigger: wrap,
      type: 'x',
      inertia: true,
      allowNativeTouchScrolling: true,
      onPress() {
        // Subtle feedback on touch/mousedown of the wrap
        gsap.to(content, { 
          clipPath: 'inset(5%)',
          duration: 0.3,
          ease: 'power4.out',
          overwrite: 'auto'
        });
        // Stop automatic spinning to prepare for drag
        gsap.killTweensOf(spin);
        spin.timeScale(0);
        startProg = spin.progress();
      },
      onDrag() {
        const p = startProg + (this.startX - this.x) / dragDistance;
        spin.progress(wrapProgress(p));
      },
      onThrowUpdate() {
        const p = startProg + (this.startX - this.x) / dragDistance;
        spin.progress(wrapProgress(p));
      },
      onRelease() {
        if (!this.tween || !this.tween.isActive()) {
          gsap.to(spin, { timeScale: 1, duration: 0.1 });
        }
        gsap.to(content, {
          clipPath: 'inset(0%)',
          duration: 0.5,
          ease: 'power4.out',
          overwrite: 'auto'
        });
      },
      onThrowComplete() {
        gsap.to(spin, { timeScale: 1, duration: 0.1 });
      }
    })[0];

    // Scroll-into-view animation
    intro = gsap.timeline({
      scrollTrigger: {
        trigger: wrap,
        start: 'top 80%',
        end: 'bottom top',
        scrub: false,
        toggleActions: 'play resume play play'
      },
      defaults: { ease: 'expo.inOut' }
    });
    intro
      .fromTo(spin, { timeScale: 15 }, { timeScale: 1, duration: 2 })
      .fromTo(wrap, { scale: 0.5, rotation: 12 }, { scale: 1, rotation: 5, duration: 1.2 }, '<')
      .fromTo(content, { autoAlpha: 0 }, { autoAlpha: 1, stagger: { amount: 0.8, from: 'random' } }, '<');

    // While-scrolling feedback
    observerInstance = Observer.create({
      target: window,
      type: 'wheel,scroll,touch',
      onChangeY: self => {
        // Control how much scroll speed affects the rotation on scroll
        let v = gsap.utils.clamp(-60, 60, self.velocityY * 0.005); 
        spin.timeScale(v);
        const resting = v < 0 ? -1 : 1;

        gsap.fromTo(
          { value: v },
          { value: v },
          {
            value: resting,
            duration: 1.2,
            onUpdate() {
              spin.timeScale(this.targets()[0].value);
            }
          }
        );
      }
    });

  };

  // First create on function call
  create();

  // Debounce function to use on resize events
  const debounce = (fn, ms) => {
    let t;
    return () => {
      clearTimeout(t);
      t = setTimeout(fn, ms);
    };
  };

  // Whenever window resizes, first destroy, then re-init it all
  window.addEventListener('resize', debounce(() => {
    const newWidth = window.innerWidth;
    if (newWidth !== lastWidth) {
      lastWidth = newWidth;
      destroy();
      create();
      ScrollTrigger.refresh();
    }
  }, 200));

}

// Initialize 3D Image Carousel
document.addEventListener("DOMContentLoaded", () =>{
  init3dImageCarousel();
})

Implementation

Required HTML Structure & Attributes
<div data-3d-carousel-wrap> 
  <div data-3d-carousel-panel> 
    <div data-3d-carousel-content>

    div>
  div>

div>- data-3d-carousel-wrap: identifies the main 3D carousel wrapper.
- data-3d-carousel-panel: each 'slide' in the 3D circle. This can be styled however you like!
- data-3d-carousel-content: inner container used for press/animation effects. Put your images etc inside of this. If you leave this out, the carousel will still work, it's optional.

Carousel panels
We've used our panels to align some image containers inside. But technically, these panels can be almost anything. Feel free to experiment and try out different types of content and designs!

Carousel perspective
The CSS perspective property is used to define the, well, perspective. Default value is 90vw, it's best to change this to understand and see how this affects the look of your carousel.

Carousel size
The carousel size is not defined with CSS, but in a JS function to be fully dynamic. On default, the radius is defined as half of the window width, but you can change this to whatever you want.
const calcRadius = () => {
  radius = window.innerWidth * 0.5;
}
Drag sensitivity
Defines how 'sensitive' the carousel responds to your drag gestures. A smaller number equals a very sensitive drag, a higher number will make it 'slow' or hard to drag. We also base this off of the window width:
const dragDistance = window.innerWidth * 3; // Control the snapiness on drag
Intro animation
We've used a standard GSAP Timeline to create an 'intro' animation for when the carousel scrolls into view. A nice trick is that you can 'timeScale' the spin animation, to really create a dynamic entrance effect. Since this is a normal GSAP timeline, you can change this however you want.

Scroll behaviour and speed
We've also used GSAP Observer to speed up the spin animation as you're scrolling past the wrapper. You can either completely remove the observerInstance part if you don't want this, or tweak the speed factor in the below variable by changing the 0.005 part. Lower is slower, higher number makes it more sensitive.
// Control how much scroll speed affects the rotation on scroll
let v = gsap.utils.clamp(-60, 60, self.velocityY * 0.005);
Content editing in Webflow
The 2 bits in our 'custom CSS' part (Step 3) of this resource make sure that your panels sit side-by-side inside the Webflow designer, and the wrapper has overflow: auto, so you can scroll the carousel horizontally as it's flat. Our tip to make this easily editable for you client would be to make a component out of the wrapper, so that you can hook all the images up to component props. Technically, it would be possible to make this CMS-powered as well, but because our specific example has even and uneven panels, it's a bit more tricky.

Credits
This CodePen here from the official GSAP account was used as a base for the 3D rotational + drag functionalities. We've adapted it slightly and added the scroll behaviour, intro animation, and made it responsive.

code.script.js:
document.addEventListener("DOMContentLoaded", function () {
  const sliderContainer = document.querySelector(".slider");
  const indicators = document.querySelectorAll(".index");
  const path = document.querySelector("#link-svg");
  const line1 = document.querySelector(".line-1");
  const line2 = document.querySelector(".line-2");
  const link = document.querySelector(".link");
  const linkWrapper = document.querySelector(".link-wrapper");

  const totalSlides = 5;
  const linkUrls = [
    "https://www.mukta.com/",
    "https://www.youtube.com/@mukta/",
    "https://www.instagram.com/muktaweb/",
    "https://x.com/muktaweb/",
    "https://codepen.io/mukta/",
  ];

  let currentSlideIndex = 1;
  let isAnimating = false;
  let currentTopValue = 0;

  window.addEventListener("wheel", (e) => {
    if (isAnimating) return;
    if (e.deltaY > 0) showNextSlide();
    else if (e.deltaY < 0) showPrevSlide();
  });

  const slides = document.querySelectorAll(".slide");
  slides.forEach((slide, idx) => {
    const img = slide.querySelector("img");
    if (idx === 0) {
      gsap.set(slide, { zIndex: 1 });
      gsap.set(img, { scale: 1, top: "0" });
    } else {
      gsap.set(slide, {
        clipPath: "polygon(0 100%, 100% 100%, 100% 100%, 0 100%)",
        zIndex: 0,
      });
      gsap.set(img, { scale: 2, top: "4em" });
    }
  });

  function showNextSlide() {
    const currentSlide = document.querySelector(`#slide-${currentSlideIndex}`);
    currentSlideIndex++;

    let nextSlide = document.querySelector(`#slide-${currentSlideIndex}`);
    if (!nextSlide) {
      nextSlide = createSlide(currentSlideIndex);
      sliderContainer.appendChild(nextSlide);
    }

    animateSlideTransition(currentSlide, nextSlide, "down");
  }

  function showPrevSlide() {
    const currentSlide = document.querySelector(`#slide-${currentSlideIndex}`);
    const prevSlideIndex = currentSlideIndex - 1;

    let prevSlide = document.querySelector(`#slide-${prevSlideIndex}`);
    if (!prevSlide) {
      prevSlide = createSlide(prevSlideIndex);
      sliderContainer.insertBefore(prevSlide, currentSlide);
    }

    animateSlideTransition(currentSlide, prevSlide, "up");
    currentSlideIndex--;
  }

  function createSlide(slideNumber) {
    const slide = document.createElement("div");
    slide.className = "slide";
    slide.id = `slide-${slideNumber}`;

    const img = document.createElement("img");
    img.src = getImageSource(slideNumber);
    img.alt = "";

    slide.appendChild(img);

    gsap.set(slide, {
      clipPath: "polygon(0 100%, 100% 100%, 100% 100%, 0 100%)",
      zIndex: 0,
    });
    gsap.set(img, { scale: 2, top: "4em" });

    return slide;
  }

  function animateSlideTransition(currentSlide, nextSlide, direction) {
    if (isAnimating) return;
    isAnimating = true;

    const currentImg = currentSlide.querySelector("img");
    const nextImg = nextSlide.querySelector("img");

    gsap.set(nextSlide, {
      clipPath:
        direction === "up"
          ? "polygon(0 0, 100% 0, 100% 0, 0 0)"
          : "polygon(0 100%, 100% 100%, 100% 100%, 0 100%)",
    });
    gsap.set(nextImg, { scale: 2, top: "4em" });
    gsap.set(currentSlide, { zIndex: 1 });
    gsap.set(nextSlide, { zIndex: 2 });

    updateSlideTitle(
      direction === "up" ? currentSlideIndex - 1 : currentSlideIndex
    );
    updateIndicators(
      direction === "up" ? currentSlideIndex - 1 : currentSlideIndex
    );
    updateLink(direction === "up" ? currentSlideIndex - 1 : currentSlideIndex);

    const timeline = gsap.timeline({
      onComplete: () => {
        gsap.set(currentSlide, { zIndex: 0 });
        gsap.set(nextSlide, { zIndex: 1 });
        cleanupSlides();
        isAnimating = false;
      },
    });

    timeline.add(animateCircle(), 0);
    timeline.add(animateText(), 0);

    timeline
      .to(
        currentImg,
        {
          scale: 2,
          top: "4em",
          duration: 2,
          ease: "power3.inOut",
        },
        0
      )
      .to(
        nextSlide,
        {
          clipPath: "polygon(0 0%, 100% 0%, 100% 100%, 0 100%)",
          duration: 2,
          ease: "power4.inOut",
        },
        0
      )
      .to(
        nextImg,
        {
          scale: 1,
          top: "0",
          duration: 2,
          ease: "power3.inOut",
        },
        0
      );
  }

  function updateSlideTitle(index) {
    const displayNumber = normalizeSlideTitle(index);
    const multiplier = window.innerWidth < 900 ? 42 : 150;
    currentTopValue = -(displayNumber - 1) * multiplier;

    gsap.to(document.querySelector(".postfix"), {
      y: `${currentTopValue}px`,
      duration: 2,
      ease: "power4.inOut",
    });
  }

  function cleanupSlides() {
    const slides = document.querySelectorAll(".slide");
    slides.forEach((slide) => {
      const slideNumber = parseInt(slide.id.split("-")[1]);
      if (Math.abs(slideNumber - currentSlideIndex) > 2) {
        slide.remove();
      }
    });
  }

  function normalizeSlideTitle(number) {
    let normalized = number;
    while (normalized <= 0) normalized += totalSlides;
    return ((normalized - 1) % totalSlides) + 1;
  }

  function getImageSource(slideNumber) {
    return `./images/img-${normalizeSlideTitle(slideNumber)}.jpg`;
  }

  function updateIndicators(index) {
    const normalizedIndex = normalizeSlideTitle(index);

    indicators.forEach((indicator) => {
      gsap.to(indicator, {
        scaleX: 0.5,
        duration: 2,
        ease: "power4.inOut",
      });
    });

    gsap.to(indicators[normalizedIndex - 1], {
      scaleX: 1,
      duration: 2,
      ease: "power4.inOut",
    });
  }

  function createNewText() {
    const line1Text = document.createElement("p");
    const line2Text = document.createElement("p");

    line1Text.textContent = "View";
    line2Text.textContent = "Project";

    gsap.set([line1Text, line2Text], {
      y: 30,
    });

    return { line1Text, line2Text };
  }

  function animateText() {
    const tl = gsap.timeline();
    const currentLine1 = line1.querySelector("p");
    const currentLine2 = line2.querySelector("p");
    const { line1Text, line2Text } = createNewText();

    tl.to([currentLine1, currentLine2], {
      y: -30,
      stagger: 0.1,
      delay: 0.25,
      duration: 1,
      ease: "power3.inOut",
      onComplete: () => {
        currentLine1.remove();
        currentLine2.remove();
      },
    });

    line1.appendChild(line1Text);
    line2.appendChild(line2Text);

    tl.to(
      [line1Text, line2Text],
      {
        y: 0,
        stagger: 0.1,
        delay: 0.75,
        duration: 1,
        ease: "power3.inOut",
      },
      "<"
    );

    return tl;
  }

  function updateLink(index) {
    const normalizedIndex = normalizeSlideTitle(index) - 1;
    const linkElement = document.querySelector(".link-wrapper a");
    linkElement.href = linkUrls[normalizedIndex];
  }

  const length = path.getTotalLength();
  gsap.set(path, {
    strokeDasharray: length,
    strokeDashoffset: 0,
    rotation: -90,
    transformOrigin: "center center",
  });

  function animateCircle() {
    const tl = gsap.timeline();

    tl.set(path, {
      strokeDashoffset: 0,
      strokeDasharray: length,
      scale: 1,
    })
      .to(path, {
        strokeDashoffset: -length,
        duration: 1,
        ease: "power2.inOut",
      })
      .set(path, {
        strokeDashoffset: length,
      })
      .to(path, {
        strokeDashoffset: 0,
        duration: 1,
        ease: "power2.inOut",
      });
    return tl;
  }

  let xTo = gsap.quickTo(linkWrapper, "x", { duration: 0.4, ease: "power3" }),
    yTo = gsap.quickTo(linkWrapper, "y", { duration: 0.4, ease: "power3" });

  link.addEventListener("mousemove", (e) => {
    const rect = link.getBoundingClientRect();
    const relX = e.clientX - rect.left - rect.width / 2;
    const relY = e.clientY - rect.top - rect.height / 2;

    xTo(relX * 0.5);
    yTo(relY * 0.5);
  });

  link.addEventListener("mouseleave", () => {
    xTo(0);
    yTo(0);
  });

  gsap.set(linkWrapper, {
    x: 0,
    y: 0,
    xPercent: -50,
    yPercent: -50,
  });

  updateLink(1);
});

code.styles.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  height: 100%;
  overflow: hidden;
  font-family: "PP Rader";
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  will-change: transform;
}

.slider-container {
  position: relative;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

.slider {
  position: relative;
  width: 100vw;
  height: 100vh;
}

.slide {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  clip-path: polygon(0 100%, 100% 100%, 100% 100%, 0 100%);
}

#slide-1 {
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
}

.slider-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: 10000;
}

.slide-title {
  position: absolute;
  top: 85%;
  left: 45%;
  transform: translate(-50%, -50%);
  display: flex;
  gap: 0.25em;
  text-transform: uppercase;
  color: #f9b165;
  font-size: 120px;
  line-height: 150px;
  clip-path: polygon(0 0, 100% 0, 100% 150px, 0 150px);
  will-change: transform;
}

.prefix span {
  padding: 0 0.25em;
}

.postfix {
  position: relative;
  top: 0;
  will-change: transform;
}

.slider-indicator {
  position: fixed;
  top: 0;
  right: 1em;
  width: 60px;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1em;
  z-index: 10;
}

.index {
  width: 100%;
  height: 2px;
  transform: scaleX(0.5);
  background-color: #f9b165;
  opacity: 1;
  transform-origin: right center;
}

.index.active {
  transform: scaleX(1);
}

.link {
  position: fixed;
  top: 75%;
  left: 75%;
  transform: translate(-50%, -50%);
  width: 350px;
  height: 350px;
  z-index: 10000;
}

.link-wrapper {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 300px;
  height: 300px;
  will-change: transform;
  pointer-events: auto;
  cursor: pointer;
}

.link-wrapper a {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.link-label {
  position: absolute;
  top: 47.5%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.link-label p {
  text-transform: uppercase;
  color: #f9b165;
  font-size: 24px;
}

.line {
  height: 28px;
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
}

.line-1 {
  position: relative;
  width: 80px;
  left: -4px;
}

.line-2 {
  position: relative;
  width: 120px;
  left: 32px;
}

.line p {
  position: absolute;
  transform: translateY(0);
  will-change: transform;
}

svg,
.link-label {
  pointer-events: none;
}

@media (max-width: 900px) {
  .slide-title {
    top: 60%;
    left: 55%;
    font-size: 40px;
    line-height: 42px;
    clip-path: polygon(0 0, 100% 0, 100% 42px, 0 42px);
  }

  .slider-indicator {
    width: 40px;
    padding-bottom: 2em;
    justify-content: flex-end;
  }

  .link {
    left: 50%;
  }
}

