indow = true; } } function onTouchMove(event) { event.preventDefault(); // Prevent scrolling if (event.touches.length === 1) { const touch = event.touches[0]; updateMousePosition(touch.clientX, touch.clientY); isMouseInWindow = true; } } function onTouchEnd(event) { isMouseInWindow = false; lastMouseMoveTime = Date.now(); } function updateMousePosition(clientX, clientY) { mousePosition.x = clientX / window.innerWidth; mousePosition.y = 1.0 - clientY / window.innerHeight; isMouseMoving = true; isMouseInWindow = true; lastMouseMoveTime = Date.now(); } function animate() { requestAnimationFrame(animate); const now = performance.now(); const elapsedTime = now / 1000; const timeSinceLastMove = now - lastMouseMoveTime; // Adjust timing for mobile const FADE_DELAY = isMouseInWindow ? (deviceInfo.isMobile ? 500 : 1000) : 0; const FADE_DURATION = deviceInfo.isMobile ? 1000 : 1500; const currentlyMoving = isMouseMoving && isMouseInWindow; effects.forEach((effect) => { if (!effect || !effect.composer) return; const { composer, fluidMaterial, prevMousePositions, options } = effect; if (currentlyMoving) { effect.mouseVelocity.set( mousePosition.x - effect.currentMouse.x, mousePosition.y - effect.currentMouse.y ); effect.currentMouse.copy(mousePosition); effect.fadeOpacity = 1.0; } else { // Apply inertia only if mouse/touch is active if (isMouseInWindow) { effect.mouseVelocity.multiplyScalar(options.inertia); if (effect.mouseVelocity.lengthSq() > 0.000001) { effect.currentMouse.add(effect.mouseVelocity); } } // Fade out logic if (timeSinceLastMove > FADE_DELAY) { const fadeProgress = Math.min( 1.0, (timeSinceLastMove - FADE_DELAY) / FADE_DURATION ); effect.fadeOpacity = Math.max(0.0, 1.0 - fadeProgress); } } // Update trail positions prevMousePositions.pop(); prevMousePositions.unshift(effect.currentMouse.clone()); // Update shader uniforms for (let i = 0; i < options.trailLength; i++) { if (fluidMaterial.uniforms.iPrevMouse.value[i]) { fluidMaterial.uniforms.iPrevMouse.value[i]