  padding-top: 0.8em;
}

.item {
  padding: 4em 0 0 0;
}

.contact-info {
  display: flex;
}

.address {
  padding-left: 4em;
}

input {
  background: none;
  outline: none;
  border: none;
  border-bottom: 1px solid #747474;
  font-size: 24px;
  padding: 0.4em;
