  font-size: 60px;
  font-weight: 600;
  text-transform: uppercase;
}

.send-icon {
  font-size: 60px;
}

@media (min-width: 900px) {
  html,
  body {
    height: 100%;
  }

  .contact-section {
    width: 100%;
    display: flex;
  }
