script.js: // Register GSAP plugins gsap.registerPlugin(Draggable, InertiaPlugin, CustomEase, Flip); class PreloaderManager { constructor() { this.overlay = null; this.canvas = null; this.ctx = null; this.animationId = null; this.startTime = null; this.duration = 2000; // 2 seconds this.createLoadingScreen(); } createLoadingScreen() { this.overlay = document.getElementById("preloader-overlay"); this.overlay.style.cssText = ` position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: #000; display: flex; justify-content: center; align-items: center; z-index: 100000; `; this.canvas = document.createElement("canvas"); this.canvas.width = 300; this.canvas.height = 300; this.ctx = this.canvas.getContext("2d"); this.overlay.appendChild(this.canvas); this.startAnimation(); } startAnimation() { const centerX = this.canvas.width / 2; const centerY = this.canvas.height / 2; let time = 0; let lastTime = 0; const dotRings = [ { radius: 20, count: 8 }, { radius: 35, count: 12 }, { radius: 50, count: 16 }, { radius: 65, count: 20 }, { radius: 80, count: 24 } ]; const colors = { primary: "#2C1B14", accent: "#A64B23" }; const hexToRgb = (hex) => { return [ parseInt(hex.slice(1, 3), 16), parseInt(hex.slice(3, 5), 16), parseInt(hex.slice(5, 7), 16) ]; }; const animate = (timestamp) => { if (!this.startTime) this.startTime = timestamp; if (!lastTime) lastTime = timestamp; const deltaTime = timestamp - lastTime; lastTime = timestamp; time += deltaTime * 0.001; this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height); // Draw center dot this.ctx.beginPath(); this.ctx.arc(centerX, centerY, 3, 0, Math.PI * 2); const rgb = hexToRgb(colors.primary); this.ctx.fillStyle = `rgba(${rgb[0]}, ${rgb[1]}, ${rgb[2]}, 0.9)`; this.ctx.fill(); // Draw Line Pulse Wave animation dotRings.forEach((ring, ringIndex) => { for (let i = 0; i < ring.count; i++) { const angle = (i / ring.count) * Math.PI * 2; const radiusPulse = Math.sin(time * 2 - ringIndex * 0.4) * 3; const x = centerX + Math.cos(angle) * (ring.radius + radiusPulse); const y = centerY + Math.sin(angle) * (ring.radius + radiusPulse); const opacityWave = 0.4 + Math.sin(time * 2 - ringIndex * 0.4 + i * 0.2) * 0.6; const isActive = Math.sin(time * 2 - ringIndex * 0.4 + i * 0.2) > 0.6; // Draw line from center to point this.ctx.beginPath(); this.ctx.moveTo(centerX, centerY); this.ctx.lineTo(x, y); this.ctx.lineWidth = 0.8; if (isActive) { const accentRgb = hexToRgb(colors.accent); this.ctx.strokeStyle = `rgba(${accentRgb[0]}, ${accentRgb[1]}, ${ accentRgb[2] }, ${opacityWave * 0.7})`; } else { const primaryRgb = hexToRgb(colors.primary); this.ctx.strokeStyle = `rgba(${primaryRgb[0]}, ${primaryRgb[1]}, ${ primaryRgb[2] }, ${opacityWave * 0.5})`; } this.ctx.stroke(); // Draw dot at the end of the line this.ctx.beginPath(); this.ctx.arc(x, y, 2.5, 0, Math.PI * 2); if (isActive) { const accentRgb = hexToRgb(colors.accent); this.ctx.fillStyle = `rgba(${accentRgb[0]}, ${accentRgb[1]}, ${accentRgb[2]}, ${opacityWave})`; } else { const primaryRgb = hexToRgb(colors.primary); this.ctx.fillStyle = `rgba(${primaryRgb[0]}, ${primaryRgb[1]}, ${primaryRgb[2]}, ${opacityWave})`; } this.ctx.fill(); } }); // Check if we should complete the loading if (timestamp - this.startTime >= this.duration) { this.complete(); return; } this.animationId = requestAnimationFrame(animate); }; this.animationId = requestAnimationFrame(animate); } complete(onComplete) { if (this.animationId) { cancelAnimationFrame(this.animationId); } if (this.overlay) { this.overlay.style.opacity = "0"; this.overlay.style.transition = "opacity 0.8s ease"; setTimeout(() => { this.overlay?.remove(); if (onComplete) onComplete(); }, 800); } } } class FashionGallery { constructor() { // DOM elements this.viewport = document.getElementById("viewport"); this.canvasWrapper = document.getElementById("canvasWrapper"); this.gridContainer = document.getElementById("gridContainer"); this.splitScreenContainer = document.getElementById("splitScreenContainer"); this.imageTitleOverlay = document.getElementById("imageTitleOverlay"); this.closeButton = document.getElementById("closeButton"); this.controlsContainer = document.getElementById("controlsContainer"); this.soundToggle = document.getElementById("soundToggle"); // Create custom eases this.customEase = CustomEase.create("smooth", ".87,0,.13,1"); this.centerEase = CustomEase.create("center", ".25,.46,.45,.94"); // Configuration this.config = { itemSize: 320, baseGap: 16, rows: 8, cols: 12, currentZoom: 0.6, currentGap: 32 }; // State this.zoomState = { isActive: false, selectedItem: null, flipAnimation: null, scalingOverlay: null }; this.gridItems = []; this.gridDimensions = {}; this.lastValidPosition = { x: 0, y: 0 }; this.draggable = null; this.viewportObserver = null; // Initialize sound system this.initSoundSystem(); // Initialize image data this.initImageData(); } initSoundSystem() { this.soundSystem = { enabled: false, sounds: { click: new Audio("https://assets.codepen.io/7558/glitch-fx-001.mp3"), open: new Audio("https://assets.codepen.io/7558/click-glitch-001.mp3"), close: new Audio("https://assets.codepen.io/7558/click-glitch-001.mp3"), "zoom-in": new Audio( "https://assets.codepen.io/7558/whoosh-fx-001.mp3" ), "zoom-out": new Audio( "https://assets.codepen.io/7558/whoosh-fx-001.mp3" ), "drag-start": new Audio( "https://assets.codepen.io/7558/preloader-2s-001.mp3" ), "drag-end": new Audio( "https://assets.codepen.io/7558/preloader-2s-001.mp3" ) }, play: (soundName) => { if (!this.soundSystem.enabled || !this.soundSystem.sounds[soundName]) return; try { const audio = this.soundSystem.sounds[soundName]; audio.currentTime = 0; audio.play().catch(() => {}); } catch (e) { // Silently handle audio errors } }, toggle: () => { this.soundSystem.enabled = !this.soundSystem.enabled; this.soundToggle.classList.toggle("active", this.soundSystem.enabled); // Prevent visual conflicts during sound toggle if (this.zoomState.isActive) return; if (this.soundSystem.enabled)