style.css: @import url("https://fonts.cdnfonts.com/css/thegoodmonolith"); @font-face { src: url("https://fonts.cdnfonts.com/css/pp-neue-montreal") format("woff2"); font-family: "PP Neue Montreal", sans-serif; font-weight: 400; } :root { --spacing-base: 1rem; --spacing-md: 1.5rem; --spacing-lg: 2rem; --color-text: #ffffff; --color-text-dim: 0.6; --transition-medium: 0.3s ease; --font-size-base: 14px; } * { margin: 0; padding: 0; box-sizing: border-box; user-select: none; } body { font-family: "PP Neue Montreal", sans-serif; background-color: #000000; color: #ffffff; overflow: hidden; position: relative; } /* Animated noise effect */ body::before { content: ""; position: fixed; top: -50%; left: -50%; width: 200%; height: 200%; background: transparent url("http://assets.iceable.com/img/noise-transparent.png") repeat 0 0; background-size: 300px 300px; animation: noise-animation 0.3s steps(5) infinite; opacity: 0.9; will-change: transform; z-index: 100; pointer-events: none; } @keyframes noise-animation { 0% { transform: translate(0, 0); } 10% { transform: translate(-2%, -3%); } 20% { transform: translate(-4%, 2%); } 30% { transform: translate(2%, -4%); } 40% { transform: translate(-2%, 5%); } 50% { transform: translate(-4%, 2%); } 60% { transform: translate(3%, 0); } 70% { transform: translate(0, 3%); } 80% { transform: translate(-3%, 0); } 90% { transform: translate(2%, 2%); } 100% { transform: translate(1%, 0); } } .coordinates-section { grid-column: 1 / span 3; font-family: "TheGoodMonolith", monospace; } /* Key hint styling */ .key-hint { display: inline-flex; align-items: center; justify-content: center; padding: 0 5px; border: 1px solid var(--color-text); border-radius: 3px; font-size: 12px; margin: 0 3px; min-width: 20px; height: 20px; } /* Footer text styling */ .footer p { font-family: "TheGoodMonolith", monospace; } /* Global link styling */ a { position: relative; cursor: pointer; color: var(--color-text); padding: 0; display: inline-block; z-index: 1; text-decoration: none; font-size: var(--font-size-base); opacity: 1; transition: color var(--transition-medium); font-weight: 700; } a::after { content: ""; position: absolute; top: 0; left: 0; width: 0; height: 100%; background-color: var(--color-text); z-index: -1; transition: width 0.3s cubic-bezier(0.34, 1.56, 0.64, 1); } a:hover::after, .links span:hover::after { width: 100%; } a:hover, .links span:hover { color: black; mix-blend-mode: difference; opacity: 1; } p { display: block; text-decoration: none; color: #ffffff; font-size: 14px; font-weight: 600; letter-spacing: -0.01rem; -webkit-font-smoothing: antialiased; } ul { list-style: none; } h3 { font-size: 14px; margin-bottom: var(--spacing-base); font-weight: 600; } /* Header and Footer */ .header, .footer { position: absolute; left: 0; width: 100vw; padding: 1.5rem; z-index: 10000; display: grid; grid-template-columns: repeat(12, 1fr); column-gap: var(--spacing-base); } .header { top: 0; } .footer { bottom: 0; } /* Grid column assignments */ .nav-section { grid-column: 1 / span 3; } .values-section { grid-column: 5 / span 2; } .location-section { grid-column: 7 / span 2; } .contact-section { grid-column: 9 / span 2; } .social-section { grid-column: 11 / span 2; text-align: right; } /* Bottom bar */ .coordinates-section { grid-column: 1 / span 3; } .links-section { grid-column: 5 / span 4; text-align: center; display: flex; justify-content: center; align-items: center; gap: var(--spacing-base); } .info-section { grid-column: 9 / span 4; text-align: right; } /* ===== LOGO COMPONENT ===== */ .logo-container { margin-bottom: var(--spacing-md); display: block; width: 3rem; height: 1.5rem; position: relative; } .logo-circles { position: relative; width: 100%; height: 100%; } .circle { position: absolute; border-radius: 50%; transition: transform var(--transition-medium); width: 1.4rem; height: 1.4rem; background-color: var(--color-text); top: 50%; } .circle-1 { left: 0; transform: translate(0, -50%); } .circle-2 { left: 0.8rem; transform: translate(0, -50%); mix-blend-mode: exclusion; } .logo-container:hover .circle-1 { transform: translate(-0.5rem, -50%); } .logo-container:hover .circle-2 { transform: translate(0.5rem, -50%); } .container { position: relative; width: 100vw; height: 100vh; overflow: hidden; cursor: grab; } .canvas { position: absolute; will-change: transform; } .item { position: absolute; overflow: hidden; background-color: #000000; cursor: pointer; border-radius: var(--border-radius, 0px); } .item-image-container { width: 100%; height: 100%; overflow: hidden; position: relative; } .item-image-container::after { content: ""; position: absolute; top: 0; left: 0; right: 0; bottom: 0; pointer-events: none; box-shadow: inset 0 0 var(--vignette-size, 0px) rgba(0, 0, 0, 0.5); z-index: 1; } .item img { width: 100%; height: 100%; object-fit: cover; pointer-events: none; transition: transform 0.3s ease; } .item:hover img { transform: scale(var(--hover-scale, 1.05)); } .item-caption { position: absolute; bottom: 0; left: 0; width: 100%; padding: 10px; z-index: 2; } /* Project title needs to be above everything */ .project-title { position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%); width: 100%; text-align: center; pointer-events: none; z-index: 10002; /* Increased to be above expanded-item */ } .project-title p { position: relative; height: 42px; color: #ffffff; clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%); overflow: hidden; } .project-title p .word { position: relative; display: inline-block; font-family: "PP Neue Montreal", sans-serif; font-size: 36px; letter-spacing: -0.03em; text-transform: uppercase; transform: translateY(0%); will-change: transform; } .item-name { font-family: "PP Neue Montreal", sans-serif; color: #ffffff; font-size: 12px; font-weight: 500; text-transform: uppercase; letter-spacing: -0.03em; margin-bottom: 2px; position: relative; overflow: hidden; height: 16px; } .item-name .word { position: relative; display: inline-block; will-change: transform; } .item-number { font-family: "TheGoodMonolith", monospace; color: #888888; font-size: 10px; font-weight: 400; position: relative; overflow: hidden; height: 14px; } .item-number .char { position: relative; display: inline-block; will-change: transform; } /* Expanded item should be above overlay but below titles and captions */ .expanded-item { position: fixed; z-index: 10000; /* Lower than project-title and caption-clone */ top: 50%; left: 50%; transform: translate(-50%, -50%); background-color: #000000; overflow: hidden; cursor: pointer; border-radius: var(--border-radius, 0px); } .expanded-item img { width: 100%; height: 100%; object-fit: cover; pointer-events: none; } .overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background-color: #000000; pointer-events: none; opacity: 0; z-index: 9999; /* Below expanded-item but above other content */ } .overlay.active { pointer-events: auto; } /* Page vignette effect */ .page-vignette-container { position: fixed; top: 0; left: 0; width: 100%; height: 100%; pointer-events: none; z-index: 9998; /* Below overlay */ } .page-vignette { position: absolute; top: 0; left: 0; width: 100%; height: 100%; box-shadow: inset 0 0 var(--page-vignette-size, 0px) var(--page-vignette-color, rgba(0, 0, 0, 0.9)); } .page-vignette-strong { position: absolute; top: 0; left: 0; width: 100%; height: 100%; box-shadow: inset 0 0 var(--page-vignette-strong-size, 0px) var(--page-vignette-strong-color, rgba(0, 0, 0, 0.95)); } .page-vignette-extreme { position: absolute; top: 0; left: 0; width: 100%; height: 100%; box-shadow: inset 0 0 var(--page-vignette-extreme-size, 0px) var(--page-vignette-extreme-color, rgba(0, 0, 0, 1)); } /* Tweakpane styles */ .tp-dfwv { z-index: 10000 !important; } /* Make sure the Tweakpane tab is visible when collapsed */ .tp-rotv { opacity: 0.5 !important; } .tp-rotv.tp-rotv-expanded { opacity: 1 !important; } .tp-rotv.tp-rotv-collapsed { opacity: 0.7 !important; } /* Caption clone needs to be above expanded item */ .caption-clone { position: fixed; z-index: 10002; /* Same as project-title to be above expanded-item */ } /* Clone styles for animation */ .caption-clone { position: fixed; z-index: 10001; } .caption-clone .item-name, .caption-clone .item-number { overflow: hidden; }
script.js: import { Pane } from "https://cdn.skypack.dev/tweakpane@4.0.4"; window.Pane = Pane; // Register GSAP plugins gsap.registerPlugin(CustomEase); CustomEase.create("hop", "0.9, 0, 0.1, 1"); // Project titles const items = [ "Chromatic Loopscape", "Solar Bloom", "Neon Handscape", "Echo Discs", "Void Gaze", "Gravity Sync", "Heat Core", "Fractal Mirage", "Nova Pulse", "Sonic Horizon", "Dream Circuit", "Lunar Mesh", "Radiant Dusk", "Pixel Drift", "Vortex Bloom", "Shadow Static", "Crimson Phase", "Retro Cascade", "Photon Fold", "Zenith Flow" ]; // Image URLs - replace with your actual image URLs const imageUrls = [ "https://cdn.cosmos.so/0f164449-f65e-4584-9d62-a9b3e1f4a90a?format=jpeg", "https://cdn.cosmos.so/74ccf6cc-7672-4deb-ba13-1727b7dc6146?format=jpeg", "https://cdn.cosmos.so/2f49a117-05e7-4ae9-9e95-b9917f970adb?format=jpeg", "https://cdn.cosmos.so/7b5340f5-b4dc-4c08-8495-c507fa81480b?format=jpeg", "https://cdn.cosmos.so/f733585a-081e-48e7-a30e-e636446f2168?format=jpeg", "https://cdn.cosmos.so/47caf8a0-f456-41c5-98ea-6d0476315731?format=jpeg", "https://cdn.cosmos.so/f99f8445-6a19-4a9a-9de3-ac382acc1a3f?format=jpeg" ]; const container = document.querySelector(".container"); const canvas = document.getElementById("canvas"); const overlay = document.getElementById("overlay"); const projectTitleElement = document.querySelector(".project-title p"); // Settings object for Tweakpane const settings = { // Item sizes baseWidth: 400, smallHeight: 330, largeHeight: 500, itemGap: 65, hoverScale: 1.05, expandedScale: 0.4, // Percentage of viewport width dragEase: 0.075, momentumFactor: 200, bufferZone: 3, borderRadius: 0, vignetteSize: 0, // Page vignette settings - simplified to two main controls vignetteStrength: 0.7, // Controls opacity of all layers vignetteSize: 200, // Controls size of all layers // Overlay settings overlayOpacity: 0.9, overlayEaseDuration: 0.8, // Animation durations zoomDuration: 0.6 // Reduced from 1.0 to 0.6 for faster zoom }; // Define item sizes based on settings let itemSizes = [ { width: settings.baseWidth, height: settings.smallHeight }, { width: settings.baseWidth, height: settings.largeHeight } ]; let itemGap = settings.itemGap; let columns = 4; const itemCount = items.length; // Calculate grid cell size based on the largest possible item let cellWidth = settings.baseWidth + settings.itemGap; let cellHeight = Math.max(settings.smallHeight, settings.largeHeight) + settings.itemGap; let isDragging = false; let startX, startY; let targetX = 0, targetY = 0; let currentX = 0, currentY = 0; let dragVelocityX = 0, dragVelocityY = 0; let lastDragTime = 0; let mouseHasMoved = false; let visibleItems = new Set(); let lastUpdateTime = 0; let lastX = 0, lastY = 0; let isExpanded = false; let activeItem = null; let activeItemId = null; let canDrag = true; let originalPosition = null; let expandedItem = null; let overlayAnimation = null; let titleSplit = null; let activeCaptionNameSplit = null; let activeCaptionNumberSplit = null; let paneInstance = null; // Initialize Tweakpane function initTweakpane() { // Wait for Pane to be available (imported via ES module) if (!window.Pane) { setTimeout(initTweakpane, 100); return; } // Create the pane with a title and make it visible even when collapsed paneInstance = new window.Pane({ title: "Gallery Settings", expanded: false }); // Make sure the pane is visible by styling its container const paneElement = paneInstance.element; paneElement.style.position = "fixed"; paneElement.style.top = "10px"; paneElement.style.right = "10px"; paneElement.style.zIndex = "10000"; // Item size settings const sizeFolder = paneInstance.addFolder({ title: "Item Sizes", expanded: false }); sizeFolder .addBinding(settings, "baseWidth", { min: 100, max: 600, step: 10 }) .on("change", updateSettings); sizeFolder .addBinding(settings, "smallHeight", { min: 100, max: 400, step: 10 }) .on("change", updateSettings); sizeFolder .addBinding(settings, "largeHeight", { min: 100, max: 600, step: 10 }) .on("change", updateSettings); // Layout settings const layoutFolder = paneInstance.addFolder({ title: "Layout", expanded: false }); layoutFolder .addBinding(settings, "itemGap", { min: 0, max: 100, step: 5 }) .on("change", updateSettings); layoutFolder .addBinding(settings, "bufferZone", { min: 1, max: 5, step: 0.5 }) .on("change", updateSettings); // Style settings const styleFolder = paneInstance.addFolder({ title: "Style", expanded: false }); styleFolder .addBinding(settings, "borderRadius", { min: 0, max: 16, step: 1 }) .on("change", updateBorderRadius); // Item vignette settings const itemVignetteFolder = paneInstance.addFolder({ title: "Item Vignette", expanded: false }); itemVignetteFolder .addBinding(settings, "vignetteSize", { min: 0, max: 50, step: 1 }) .on("change", updateVignetteSize); // Page vignette settings - simplified to two controls const pageVignetteFolder = paneInstance.addFolder({ title: "Page Vignette", expanded: false }); pageVignetteFolder .addBinding(settings, "vignetteStrength", { min: 0, max: 1, step: 0.05 }) .on("change", updatePageVignette); pageVignetteFolder .addBinding(settings, "vignetteSize", { min: 0, max: 500, step: 10 }) .on("change", updatePageVignette); // Overlay settings const overlayFolder = paneInstance.addFolder({ title: "Overlay Animation", expanded: false }); overlayFolder.addBinding(settings, "overlayOpacity", { min: 0, max: 1, step: 0.05 }); overlayFolder.addBinding(settings, "overlayEaseDuration", { min: 0.2, max: 2, step: 0.1 }); // Animation settings const animationFolder = paneInstance.addFolder({ title: "Animation", expanded: false }); animationFolder .addBinding(settings, "hoverScale", { min: 1, max: 1.5, step: 0.05 }) .on("change", updateHoverScale); animationFolder.addBinding(settings, "expandedScale", { min: 0.2, max: 0.8, step: 0.05 }); animationFolder.addBinding(settings, "dragEase", { min: 0.01, max: 0.2, step: 0.01 }); animationFolder.addBinding(settings, "momentumFactor", { min: 50, max: 500, step: 10 }); animationFolder.addBinding(settings, "zoomDuration", { min: 0.2, max: 1.5, step: 0.1 }); // Add a button to reset the view paneInstance .addButton({ title: "Reset View" }) .on("click", () => { targetX = 0; targetY = 0; }); // Add keyboard shortcut for toggling the panel window.addEventListener("keydown", (e) => { // Toggle panel visibility with the 'h' key if (e.key === "h" || e.key === "H") { togglePaneVisibility(); } }); } // Function to toggle the panel visibility function togglePaneVisibility() { if (!paneInstance) return; const element = paneInstance.element; if (element.style.display === "none") { element.style.display = ""; } else { element.style.display = "none"; } } // Update CSS variable for border radius function updateBorderRadius() { document.documentElement.style.setProperty( "--border-radius", `${settings.borderRadius}px` ); } // Update CSS variable for item vignette function updateVignetteSize() { document.documentElement.style.setProperty( "--vignette-size", `${settings.vignetteSize}px` ); } // Update CSS variable for page vignette - now using the simplified controls function updatePageVignette() { const strength = settings.vignetteStrength; const size = settings.vignetteSize; // Regular vignette (outer layer) const regularOpacity = strength * 0.7; const regularSize = size * 1.5; document.documentElement.style.setProperty( "--page-vignette-size", `${regularSize}px` ); document.documentElement.style.setProperty( "--page-vignette-color", `rgba(0,0,0,${regularOpacity})` ); // Strong vignette (middle layer) const strongOpacity = strength * 0.85; const strongSize = size * 0.75; document.documentElement.style.setProperty( "--page-vignette-strong-size", `${strongSize}px` ); document.documentElement.style.setProperty( "--page-vignette-strong-color", `rgba(0,0,0,${strongOpacity})` ); // Extreme vignette (inner layer) const extremeOpacity = strength; const extremeSize = size * 0.4; document.documentElement.style.setProperty( "--page-vignette-extreme-size", `${extremeSize}px` ); document.documentElement.style.setProperty( "--page-vignette-extreme-color", `rgba(0,0,0,${extremeOpacity})` ); } // Update CSS variable for hover scale function updateHoverScale() { // Use CSS variables instead of directly modifying CSS rules document.documentElement.style.setProperty( "--hover-scale", settings.hoverScale ); // Update all existing items with the new hover scale document.querySelectorAll(".item").forEach((item) => { const img = item.querySelector("img"); if (img) { img.style.transition = "transform 0.3s ease"; } }); } // Update settings and rebuild the grid function updateSettings() { // Update item sizes itemSizes = [ { width: settings.baseWidth, height: settings.smallHeight }, { width: settings.baseWidth, height: settings.largeHeight } ]; itemGap = settings.itemGap; // Remove columns dependency - use a fixed value columns = 4; // Recalculate cell dimensions cellWidth = settings.baseWidth + settings.itemGap; cellHeight = Math.max(settings.smallHeight, settings.largeHeight) + settings.itemGap; // Clear existing items and rebuild visibleItems.forEach((itemId) => { const item = document.getElementById(itemId); if (item && item.parentNode === canvas) { canvas.removeChild(item); } }); visibleItems.clear(); updateVisibleItems(); // Update visual styles updateBorderRadius(); updateVignetteSize(); updateHoverScale(); updatePageVignette(); } function setAndAnimateTitle(title) { if (titleSplit) titleSplit.revert(); projectTitleElement.textContent = title; titleSplit = new SplitType(projectTitleElement, { types: "words" }); gsap.set(titleSplit.words, { y: "100%" }); } function animateTitleIn() { gsap.fromTo( titleSplit.words, { y: "100%", opacity: 0 }, { y: "0%", opacity: 1, duration: 1, stagger: 0.1, ease: "power3.out" } ); } function animateTitleOut() { gsap.to(titleSplit.words, { y: "-100%", opacity: 0, duration: 1, stagger: 0.1, ease: "power3.out" }); } // Animate overlay in function animateOverlayIn() { if (overlayAnimation) overlayAnimation.kill(); // Make sure we're using the current setting value overlayAnimation = gsap.to(overlay, { opacity: settings.overlayOpacity, duration: settings.overlayEaseDuration, ease: "power2.inOut", overwrite: true }); } // Animate overlay out function animateOverlayOut() { if (overlayAnimation) overlayAnimation.kill(); overlayAnimation = gsap.to(overlay, { opacity: 0, duration: settings.overlayEaseDuration, ease: "power2.inOut" }); } // Determine item size based on position function getItemSize(row, col) { // Use a consistent pattern for size selection // This ensures the same position always gets the same size const sizeIndex = Math.abs((row * columns + col) % itemSizes.length); return itemSizes[sizeIndex]; } // Generate a unique ID for each grid position function getItemId(col, row) { return `${col},${row}`; } // Get the absolute position for an item function getItemPosition(col, row) { const xPos = col * cellWidth; const yPos = row * cellHeight; return { x: xPos, y: yPos }; } function updateVisibleItems() { const buffer = settings.bufferZone; const viewWidth = window.innerWidth * (1 + buffer); const viewHeight = window.innerHeight * (1 + buffer); // Calculate visible range based on current position and buffer const startCol = Math.floor((-currentX - viewWidth / 2) / cellWidth); const endCol = Math.ceil((-currentX + viewWidth * 1.5) / cellWidth); const startRow = Math.floor((-currentY - viewHeight / 2) / cellHeight); const endRow = Math.ceil((-currentY + viewHeight * 1.5) / cellHeight); const currentItems = new Set(); // Create or update visible items for (let row = startRow; row <= endRow; row++) { for (let col = startCol; col <= endCol; col++) { const itemId = getItemId(col, row); currentItems.add(itemId); if (visibleItems.has(itemId)) continue; if (activeItemId === itemId && isExpanded) continue; // Get size and position for this item const itemSize = getItemSize(row, col); const position = getItemPosition(col, row); // Create the item element const ite