s ease; opacity: 1; position: relative; } .project-item.active { opacity: 1; box-shadow: inset 0 1px 0 rgba(200, 255, 200, 0.2), inset 0 -1px 0 rgba(200, 255, 200, 0.2); } .portfolio-container.has-active .project-item { opacity: 0.3; } .portfolio-container.has-active .project-item.active { opacity: 1; } .project-item::before { content: counter(project-counter, decimal-leading-zero); counter-increment: project-counter; padding: 4px 0; line-height: 0.8; opacity: 0.6; justify-self: start; z-index: 10; position: relative; transition: opacity 0.1s ease; } .project-item.counter-hidden::before { opacity: 0.05; } .project-data { font-family: var(--font-primary); position: relative; white-space: nowrap; text-decoration: none; color: var(--color-foreground); z-index: 1; transition: all 0.3s ease; overflow: hidden; text-overflow: ellipsis; line-height: 1; padding: 2px; } .project-data.artist { justify-self: start; } .project-data.album { justify-self: start; } .project-data.category { justify-self: start; } .project-data.label { justify-self: start; } .project-data.year { justify-self: end; text-align: right; } .project-data::after { content: ""; position: absolute; top: 0; left: -8px; width: calc(100% + 16px); height: calc(100% - 2px); background: var(--color-accent); transition: transform 0.3s ease; transform: scaleX(0); transform-origin: left; z-index: -1; } .project-item:hover .project-data::after { transform: scaleX(1); } .project-item:hover .project-data { color: rgb(10, 10, 10) !important; } .background-image { position: fixed; top: 50%; left: 50%; width: 800px; height: 600px; transform: translate(-50%, -50%) scale(1); background-size: cover; background-position: center; background-repeat: no-repeat; opacity: 0; transition: opacity 0.6s ease, transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94); z-index: -2; } .corner-elements { position: fixed; display: grid; grid-template-columns: 1fr 1fr; grid-template-rows: 1fr 1fr; gap: 0; width: 100%; height: 100%; pointer-events: none; padding: 2rem; z-index: 200; } .corner-item { font-family: var(--font-primary); color: var(--color-foreground); font-size: 12px; padding: 8px; pointer-events: auto; } .corner-item a { color: var(--color-foreground); text-decoration: none; } .corner-item a:hover { text-decoration: underline; } .top-left { grid-column: 1 / 2; grid-row: 1 / 2; align-self: start; justify-self: start; } .top-right { grid-column: 2 / 3; grid-row: 1 / 2; align-self: start; justify-self: end; } .bottom-left { grid-column: 1 / 2; grid-row: 2 / 3; align-self: end; justify-self: start; } .bottom-right { grid-column: 2 / 3; grid-row: 2 / 3; align-self: end; justify-self: end; } .corner-square { background-color: var(--color-foreground); width: 8px; height: 8px; } .time-blink { animation: blink 1s steps(1) infinite; } @keyframes blink { 50% { opacity: 0; } } @media (max-width: 1200px) { .project-item { grid-template-columns: 30px 1fr 1.5fr 0.8fr 0.8fr 60px; grid-column-gap: 1rem; } .background-image { width: 600px; height: 450px; } } @media (max-width: 768px) { .portfolio-container { padding: 0 1rem; } .project-item { grid-template-columns: 30px 1fr 1fr; grid-column-gap: 1rem; } .project-data:not(.artist):not(.album) { display: none; } .background-image { width: 400px; height: 300px; } }
script.js: const CONFIG = { timeZone: "Europe/Zagreb", timeUpdateInterval: 1000 }; class AnimationManager { constructor() { this.backgroundImage = document.getElementById("backgroundImage"); this.projectItems = document.querySelectorAll(".project-item"); this.portfolioContainer = document.querySelector(".portfolio-container"); this.currentActiveIndex = -1; this.originalTexts = new Map(); this.debounceTimeout = null; this.idleAnimation = null; this.isIdle = true; this.idleTimer = null; this.projectItems.forEach((item) => { const textElements = item.querySelectorAll(".hover-text"); const texts = Array.from(textElements).map((el) => el.textContent); this.originalTexts.set(item, texts); }); } initializeAnimations() { this.preloadImages(); this.projectItems.forEach((item, index) => { this.addEventListeners(item, index); }); const container = document.querySelector(".portfolio-container"); container.addEventListener("mouseleave", () => { if (this.debounceTimeout) { clearTimeout(this.debounceTimeout); } this.clearActiveStates(); this.hideBackgroundImage(); this.startIdleTimer(); }); this.startIdleTimer(); } preloadImages() { this.projectItems.forEach((item) => { const imageUrl = item.dataset.image; if (imageUrl) { const img = new Image(); img.crossOrigin = "anonymous"; img.src = imageUrl; } }); } addEventListeners(item, index) { const textElements = item.querySelectorAll(".hover-text"); const imageUrl = item.dataset.image; const originalTexts = this.originalTexts.get(item); const handleMouseEnter = () => { this.stopIdleAnimation(); this.stopIdleTimer(); this.isIdle = false; if (this.debounceTimeout) { clearTimeout(this.debounceTimeout); } if (this.currentActiveIndex === index) return; this.updateActiveStates(index); textElements.forEach((element, i) => { gsap.killTweensOf(element); gsap.to(element, { duration: 0.8, scrambleText: { text: originalTexts[i], chars: "qwerty1337h@ck3r", revealDelay: 0.3, speed: 0.4 } }); }); if (imageUrl) { this.showBackgroundImage(imageUrl); } }; const handleMouseLeave = () => { this.debounceTimeout = setTimeout(() => { textElements.forEach((element, i) => { gsap.killTweensOf(element); element.textContent = originalTexts[i]; }); }, 50); }; item.addEventListener("mouseenter", handleMouseEnter); item.addEventListener("mouseleave", handleMouseLeave); } updateActiveStates(activeIndex) { this.currentActiveIndex = activeIndex; this.portfolioContainer.classList.add("has-active"); this.projectItems.forEach((item, index) => { if (index === activeIndex) { item.classList.add("active"); } else { item.classList.remove("active"); } }); } clearActiveStates() { this.currentActiveIndex = -1; this.portfolioContainer.classList.remove("has-active"); this.projectItems.forEach((item) => { item.classList.remove("active"); const textElements = item.querySelectorAll(".hover-text"); const originalTexts = this.originalTexts.get(item); textElements.forEach((element, i) => { gsap.killTweensOf(element); element.textContent = originalTexts[i]; }); }); this.startIdleTimer(); } showBackgroundImage(imageUrl) { // Reset transform and transition this.backgroundImage.style.transition = "none"; this.backgroundImage.style.transform = "translate(-50%, -50%) scale(1.2)"; // Set the background image and show it this.backgroundImage.style.backgroundImage = `url(${imageUrl})`; this.backgroundImage.style.opacity = "1"; // Force browser to acknowledge the scale reset before animating requestAnimationFrame(() => { requestAnimationFrame(() => { // Re-enable transition and animate to scale 1.0 this.backgroundImage.style.transition = "opacity 0.6s ease, transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94)"; this.backgroundImage.style.transform = "translate(-50%, -50%) scale(1.0)"; }); }); } hideBackgroundImage() { this.backgroundImage.style.opacity = "0"; } startIdleTimer() { this.stopIdleTimer(); this.idleTimer = setTimeout(() => { if (this.currentActiveIndex === -1) { this.isIdle = true; this.startIdleAnimation(); } }, 3000); } stopIdleTimer() { if (this.idleTimer) { clearTimeout(this.idleTimer); this.idleTimer = null; } } startIdleAnimation() { if (this.idleAnimation) return; this.idleAnimation = gsap.timeline({ repeat: -1, repeatDelay: 2 }); const columnElements = { artists: [...this.projectItems].map((item) => item.querySelector(".artist") ), albums: [...this.projectItems].map((item) => item.querySelector(".album") ), categories: [...this.projectItems].map((item) => item.querySelector(".category") ), labels: [...this.projectItems].map((item) => item.querySelector(".label") ), years: [...this.projectItems].map((item) => item.querySelector(".year")) }; const totalRows = this.projectItems.length; const columnStartDelay = 0.25; const rowDelay = 0.05; const hideShowGap = totalRows * rowDelay * 0.5; this.projectItems.forEach((item, rowIndex) => { const hideTime = 0 + rowIndex * rowDelay; const showTime = 0 + hideShowGap + rowIndex * rowDelay; this.idleAnimation.call( () => { item.classList.add("counter-hidden"); }, [], hideTime ); this.idleAnimation.call( () => { item.classList.remove("counter-hidden"); }, [], showTime ); }); Object.keys(columnElements).forEach((columnName, columnIndex) => { const elements = columnElements[columnName]; const columnStart = (columnIndex + 1) * columnStartDelay; elements.forEach((element, rowIndex) => { const hideTime = columnStart + rowIndex * rowDelay; this.idleAnimation.to( element, { duration: 0.1, opacity: 0.05, ease: "power2.inOut" }, hideTime ); }); elements.forEach((element, rowIndex) => { const showTime = columnStart + hideShowGap + rowIndex * rowDelay; this.idleAnimation.to( element, { duration: 0.1, opacity: 1, ease: "power2.inOut" }, showTime ); }); }); } stopIdleAnimation() { if (this.idleAnimation) { this.idleAnimation.kill(); this.idleAnimation = null; gsap.set([...document.querySelectorAll(".project-data")], { opacity: 1 }); this.projectItems.forEach((item) => { item.classList.remove("counter-hidden"); }); } } } class TimeDisplay { constructor(elementId) { this.element = document.getElementById(elementId); if (!this.element) { throw new Error(`Element with id '${elementId}' not found.`); } } start() { this.updateDisplay(); setInterval(() => this.updateDisplay(), CONFIG.timeUpdateInterval); } updateDisplay() { const { hours, minutes, dayPeriod } = this.getCurrentTime(); const timeString = `${hours}<span class="time-blink">:</span>${minutes} ${dayPeriod}`; this.element.innerHTML = timeString; } getCurrentTime() { const now = new Date(); const options = { timeZone: CONFIG.timeZone, hour12: true, hour: "numeric", minute: "numeric", second: "numeric" }; const formatter = new Intl.DateTimeFormat("en-US", options); const parts = formatter.formatToParts(now); return { hours: parts.find((part) => part.type === "hour").value, minutes: parts.find((part) => part.type === "minute").value, dayPeriod: parts.find((part) => part.type === "dayPeriod").value }; } } document.addEventListener("DOMContentLoaded", () => { const animationManager = new AnimationManager(); animationManager.initializeAnimations(); const timeDisplay = new TimeDisplay("current-time"); timeDisplay.start(); });

