eft: 1rem; width: 48px; aspect-ratio: 1; display: grid; place-items: center; opacity: 0.8; } :where(.x-link, .bear-link):is(:hover, :focus-visible) { opacity: 1; } .bear-link svg { width: 75%; } /* Utilities */ .sr-only { position: absolute; width: 1px; height: 1px; padding: 0; margin: -1px; overflow: hidden; clip: rect(0, 0, 0, 0); white-space: nowrap; border-width: 0; } } div.tp-dfwv { width: 256px; position: fixed; }
script.js: import gsap from 'https://cdn.skypack.dev/gsap@3.13.0' import Draggable from 'https://cdn.skypack.dev/gsap@3.13.0/Draggable' import { Pane } from 'https://cdn.skypack.dev/tweakpane@4.0.4' gsap.registerPlugin(Draggable) const main = document.querySelector('main') const iconButton = document.querySelector('.icon') const config = { theme: 'dark', pointer: true, angle: 325, explode: false, } const ctrl = new Pane({ title: 'config', expanded: true, }) const update = async () => { document.documentElement.dataset.theme = config.theme document.documentElement.dataset.pointer = config.pointer document.documentElement.dataset.explode = config.explode iconButton.style.setProperty('--pointer-angle', config.angle) if (!config.explode) { console.info('running this...') document.documentElement.dataset.transitioning = 'true' const animations = main.getAnimations({ subtree: true }) console.info({ animations }) await Promise.allSettled([...main.getAnimations({ subtree: true }).map((a) => a.finished)]) document.documentElement.dataset.transitioning = 'false' } } const sync = (event) => { if ( !document.startViewTransition || event.target.controller.view.labelElement.innerText !== 'theme' ) return update() document.startViewTransition(() => update()) } const angle = ctrl.addBinding(config, 'angle', { min: 0, max: 359, step: 1, disabled: config.pointer, }) ctrl.addBinding(config, 'pointer').on('change', () => { angle.disabled = config.pointer }) ctrl.addBinding(config, 'explode') ctrl.on('change', sync) update() // make tweakpane panel draggable const tweakClass = 'div.tp-dfwv' const d = Draggable.create(tweakClass, { type: 'x,y', allowEventDefault: true, trigger: tweakClass + ' button.tp-rotv_b', }) document.querySelector(tweakClass).addEventListener('dblclick', () => { gsap.to(tweakClass, { x: `+=${d[0].x * -1}`, y: `+=${d[0].y * -1}`, onComplete: () => { gsap.set(tweakClass, { clearProps: 'all' }) }, }) }) // Calculate angle between pointer and center of .icon button const calculateAngle = (event) => { if (!config.pointer) return // Get the button's bounding rectangle const rect = iconButton.getBoundingClientRect() // Calculate the center of the button const centerX = rect.left + rect.width / 2 const centerY = rect.top + rect.height / 2 // Get pointer coordinates const pointerX = event.clientX const pointerY = event.clientY // Calculate the angle in radians const deltaX = pointerX - centerX const deltaY = pointerY - centerY const angleRadians = Math.atan2(deltaY, deltaX) // Convert to degrees, offset by +90 so 0° is at the top, and normalize to 0-360 range let angleDegrees = (angleRadians * 180) / Math.PI angleDegrees = (angleDegrees + 90 + 360) % 360 // Update CSS custom property with the angle iconButton.style.setProperty('--pointer-angle', angleDegrees.toFixed(2)) } // Add the event listener document.addEventListener('pointermove', calculateAngle)

