  width: 50px;
  height: 50px;
  border-radius: 0.5rem;
  background-color: #333333;
  z-index: 0;
  pointer-events: none;
}

.pad.active {
  pointer-events: all;
  cursor: pointer;
}

.card .row:nth-child(3) .pad:nth-child(1),
.card .row:nth-child(3) .pad:nth-child(2),
.card .row:nth-child(3) .pad:nth-child(3) {
  opacity: 0.35;
}

.card-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 2em;
  color: #fff;
  overflow-y: auto;
  pointer-events: none;
  z-index: 2;
  opacity: 0;
}

button {
  border: none;
  outline: none;
  border-radius: 4em;
  font-family: "Rubik";
  padding: 0.5em 1em;
  background-color: #fff;
  cursor: pointer;
}

.img {
  width: 100%;
  height: 200px;
  margin: 1em 0;
  border-radius: 1em;
  overflow: hidden;
}

.copy {
  margin: 0.5em 0;
  padding: 1.5em;
  border-radius: 1em;
  background: #fff;
  color: #000;
}

.copy h1 {
  font-size: 20px;
  font-weight: 500;
  margin-bottom: 0.35em;
}

.copy p {
  font-size: 14px;
  line-height: 150%;
  color: gray;
}

.copy.link {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 1em 0;
}

.card-item {
  position: relative;
}

@media (max-width: 900px) {
  nav {
    width: 100%;
    padding: 2em;
  }
  .card {
    width: 360px;
    height: 320px;
    gap: 0.25em;
  }

  .row {
    gap: 0.25em;
  }

  .pad {
    width: 40px;
    height: 40px;
  }
}

