ens when you change panel count ctrl.addBinding(config, 'panels', { min: 2, max: 6, step: 1, }).on('change', (event) => { if (event.last) { buildMarkup() slideshow.refresh() // Re-initialize grid and drag after refresh if (slideshow._initializeSlideshow) { slideshow._initializeSlideshow() } } }) buildMarkup() slideshow.refresh() slideshow._initializeSlideshow() const removeArrow = () => { document.querySelector('.arrow').remove() document.removeEventListener('pointerdown', removeArrow) } document.addEventListener('pointerdown', removeArrow)

