    flex-direction: column;
    gap: 0;
  }

  .sticky-card-content-wrapper {
    width: 100%;
  }

  .sticky-card-copy {
    flex-direction: column;
    gap: 0.5rem;
  }

  .sticky-card-index {
    flex: 1;
  }
}

