tion { background-attachment: scroll; } .footer-content { flex-direction: column; gap: 2rem; text-align: left; } .footer-right { max-width: 100%; text-align: left; } .footer-credits { text-align: center; letter-spacing: 0.3em; } .site-footer { padding: 1rem; } } /* Small Mobile Styles */ @media (max-width: 480px) { .header-container { grid-template-columns: 1fr; grid-template-rows: auto auto; gap: 0.5rem; text-align: center; } .logo-container { grid-column: 1; grid-row: 1; justify-self: center; } .main-nav { grid-column: 1; grid-row: 2; justify-self: center; } .main-nav ul { gap: 0.8rem; } .main-nav a { font-size: 9px; } .contact-link { display: none; } .site-header { height: 100px; } .geometric-text.text-top-left { font-size: 9px; } .geometric-text.text-bottom-left { font-size: 8px; } .glowing-circle { width: 40px; height: 40px; } .footer-left, .footer-right { font-size: 10px; } .footer-credits { font-size: 8px; letter-spacing: 0.2em; } .section-content { padding: 1rem; } } /* Very Small Mobile */ @media (max-width: 320px) { .main-nav ul { flex-direction: column; align-items: center; gap: 0.5rem; } .glowing-circle { width: 35px; height: 35px; } .footer-left, .footer-right { font-size: 9px; } }
