@import url("https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@400;500&display=swap");

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  width: 100%;
  height: 100%;
  font-family: "IBM Plex Mono", monospace;
  background: #000;
  cursor: grab;
  user-select: none;
  overflow: hidden;
}

body.dragging {
  cursor: grabbing;
}

#gallery {
  position: relative;
  width: 100vw;
  height: 100svh;
}

.vignette-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
