code.script.js:
import data from "./data.js";

document.addEventListener("DOMContentLoaded", () => {
  const activeColors = ["#5fa5f9", "#e879f9", "#a78bfa", "#2cd4bf"];

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
  };

  const generatePads = (card, activePadCount, items) => {
    const rowsConfig = [7, 7, 7, 7, Math.floor(Math.random() * 3) + 2];
    const clickablePads = [];

    rowsConfig.forEach((padCount, rowIndex) => {
      const row = document.createElement("div");
      row.classList.add("row");
      for (let i = 0; i < padCount; i++) {
        const pad = document.createElement("div");
        pad.classList.add("pad");
        row.appendChild(pad);
        if (rowIndex !== 0 && rowIndex !== rowsConfig.length - 1) {
          clickablePads.push(pad);
        }
      }
      card.appendChild(row);
    });

    shuffleArray(clickablePads); // Shuffle pads for random selection
    setActivePads(clickablePads, card, activePadCount, items);
  };

  const setActivePads = (clickablePads, card, activePadCount, items) => {
    clickablePads.slice(0, activePadCount).forEach((pad, i) => {
      pad.classList.add("active");
      pad.style.backgroundColor =
        activeColors[Math.floor(Math.random() * activeColors.length)];

      pad.addEventListener("click", () => {
        clickablePads.forEach((p) => (p.style.zIndex = "0"));
        pad.style.zIndex = "1";

        const item = items[i];
        const cardContent = card.querySelector(".card-content");
        cardContent.innerHTML = `
    <button>Back</button>
    <div class="card-item img">
      <img src="${item.img}" alt="" />
    </div>
    <div class="card-item copy">
      <h1>${item.h1}</h1>
      <p>${item.copy}</p>
    </div>
    <div class="card-item copy link">
      <a href="${item.linkSrc}">${item.linkLabel}</a>
      <ion-icon name="arrow-forward-outline"></ion-icon>
    </div>
  `;

        gsap.to(pad, {
          scale: 20,
          duration: 0.3,
          onComplete: () => {
            gsap.to(cardContent, {
              opacity: 1,
              pointerEvents: "all",
              duration: 0.075,
            });
            gsap.fromTo(
              cardContent.querySelectorAll(".card-item"),
              {
                y: 100,
                rotation: () => gsap.utils.random(-30, 30),
                opacity: 0,
              },
              {
                y: 0,
                rotation: 0,
                opacity: 1,
                duration: 2,
                ease: "elastic.out",
                stagger: 0.1,
              }
            );
          },
        });

        card.querySelector("button").addEventListener("click", () => {
          gsap.to(cardContent, {
            opacity: 0,
            pointerEvents: "none",
            duration: 0.2,
            onComplete: () => {
              gsap.to(pad, {
                scale: 1,
                duration: 0.3,
                onComplete: () => {
                  pad.style.zIndex = "0";
                  cardContent.style.opacity = "0";
                  cardContent.style.pointerEvents = "none";
                  gsap.set(cardContent.querySelectorAll(".card-item"), {
                    clearProps: "all",
                  });
                },
              });
            },
          });
        });
      });
    });
  };

  const container = document.querySelector(".container");

  data.forEach((monthData) => {
    const month = Object.keys(monthData)[0];
    const items = monthData[month];

    const card = document.createElement("div");
    card.classList.add("card");

    const cardTitle = document.createElement("div");
    cardTitle.classList.add("card-title");
    cardTitle.innerHTML = `<p>${month}</p>`;
    card.appendChild(cardTitle);

    const cardContent = document.createElement("div");
    cardContent.classList.add("card-content");
    card.appendChild(cardContent);

    generatePads(card, items.length, items);
    container.appendChild(card);
  });
});

code.styles.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  width: 100%;
  height: 150%;
  font-family: "Rubik";
  background-color: #1a1a1a;
}

.container {
  width: 100%;
  height: 100%;
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

nav {
  width: 30%;
  margin: 2em auto 0 auto;
  padding: 2em 0.25em;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.nav-items {
  display: flex;
  gap: 2em;
}

nav a {
  text-decoration: none;
  color: #fff;
}

.card {
  position: relative;
  width: 460px;
  height: 400px;
  margin: 2em auto;
  padding: 2em;
  border-radius: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.5em;
  background-color: #272727;
  overflow: hidden;
}

.card-title {
  color: #fff;
  padding-bottom: 1em;
}

.row {
  width: 100%;
  display: flex;
  gap: 0.5em;
}

.pad {
  position: relative;
  width: 50px;
  height: 50px;
  border-radius: 0.5rem;
  background-color: #333333;
  z-index: 0;
  pointer-events: none;
}

.pad.active {
  pointer-events: all;
  cursor: pointer;
}

.card .row:nth-child(3) .pad:nth-child(1),
.card .row:nth-child(3) .pad:nth-child(2),
.card .row:nth-child(3) .pad:nth-child(3) {
  opacity: 0.35;
}

.card-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 2em;
  color: #fff;
  overflow-y: auto;
  pointer-events: none;
  z-index: 2;
  opacity: 0;
}

button {
  border: none;
  outline: none;
  border-radius: 4em;
  font-family: "Rubik";
  padding: 0.5em 1em;
  background-color: #fff;
  cursor: pointer;
}

.img {
  width: 100%;
  height: 200px;
  margin: 1em 0;
  border-radius: 1em;
  overflow: hidden;
}

.copy {
  margin: 0.5em 0;
  padding: 1.5em;
  border-radius: 1em;
  background: #fff;
  color: #000;
}

.copy h1 {
  font-size: 20px;
  font-weight: 500;
  margin-bottom: 0.35em;
}

.copy p {
  font-size: 14px;
  line-height: 150%;
  color: gray;
}

.copy.link {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 1em 0;
}

.card-item {
  position: relative;
}

@media (max-width: 900px) {
  nav {
    width: 100%;
    padding: 2em;
  }
  .card {
    width: 360px;
    height: 320px;
    gap: 0.25em;
  }

  .row {
    gap: 0.25em;
  }

  .pad {
    width: 40px;
    height: 40px;
  }
}

