style.css: /* Base font */ @font-face { font-family: "TheGoodMonolith"; font-style: normal; font-weight: 500; src: local("TheGoodMonolith"), url("https://fonts.cdnfonts.com/s/32398/TheGoodMonolith.woff") format("woff"); } *, *::after, *::before { box-sizing: border-box; margin: 0; padding: 0; } :root { font-size: 16px; --color-text: rgba(255, 255, 255, 0.9); --color-bg: #111111; --color-link: #eeeeee; --color-link-hover: #ffffff; --color-title: rgba(255, 255, 255, 0.95); --color-switch: #aaaaaa; --color-switch-active: #ffffff; --grid-item-width: 50vw; --grid-item-height: 100vh; } html { width: 100%; height: 100%; scroll-behavior: smooth; } body { font-family: "TheGoodMonolith", monospace; color: var(--color-text); background-color: var(--color-bg); -webkit-font-smoothing: antialiased; -moz-osx-font-smoothing: grayscale; position: relative; width: 100%; overflow-x: hidden; overflow-y: hidden; } /* Animated noise effect */ body::before { content: ""; position: fixed; top: -50%; left: -50%; width: 200%; height: 200%; background: transparent url("http://assets.iceable.com/img/noise-transparent.png") repeat 0 0; background-size: 300px 300px; animation: noise 0.3s steps(5) infinite; opacity: 0.9; will-change: transform; z-index: 100; pointer-events: none; } @keyframes noise { 0% { transform: translate(0, 0); } 10% { transform: translate(-2%, -3%); } 20% { transform: translate(-4%, 2%); } 30% { transform: translate(2%, -4%); } 40% { transform: translate(-2%, 5%); } 50% { transform: translate(-4%, 2%); } 60% { transform: translate(3%, 0); } 70% { transform: translate(0, 3%); } 80% { transform: translate(-3%, 0); } 90% { transform: translate(2%, 2%); } 100% { transform: translate(1%, 0); } } /* Preloader */ .preloader { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: var(--color-bg); z-index: 10000; display: flex; justify-content: center; align-items: center; } .preloader-counter { font-size: 2rem; color: #ffffff; font-family: "TheGoodMonolith", monospace; } /* Hide main content initially */ main { width: 100%; position: relative; opacity: 0; visibility: hidden; } .unbutton { background: none; border: 0; padding: 0; margin: 0; font: inherit; } .unbutton:focus { outline: none; } .content-title { position: relative; overflow: hidden; } .hero-section { width: 100%; height: 100vh; position: relative; overflow: hidden; will-change: transform; } .columns { width: 100%; height: 100vh; position: relative; display: flex; justify-content: center; align-items: center; transform: scale(0.5, 0.4); opacity: 0; will-change: transform; } .column { position: relative; display: grid; will-change: transform; } .column-inner { position: relative; will-change: transform; } .column-item { margin: 0; position: relative; z-index: 1; width: var(--grid-item-width); height: var(--grid-item-height); } .column-item-imgwrap { width: 100%; height: 100%; position: relative; overflow: hidden; } .column-item-img { width: 100%; height: 100%; background-size: cover; background-position: 50% 50%; backface-visibility: hidden; will-change: transform, width; } /* Expanding image that will zoom to full width */ .expanding-image { position: fixed; top: 0; left: 0; width: 50vw; height: 100vh; background-size: cover; background-position: center; z-index: 85; opacity: 0; will-change: transform, width; } /* Content area */ .content { pointer-events: none; position: fixed; width: 100%; height: 100vh; top: 0; left: 0; display: grid; grid-template-rows: 1fr auto; z-index: 90; } .content--current { pointer-events: auto; } .content-item { width: 100%; height: 100%; position: absolute; top: 0; left: 0; } .title-wrapper { opacity: 0; text-transform: uppercase; align-self: center; margin: 0; position: relative; font-weight: 500; padding: 0 0.5rem; width: 100%; display: flex; pointer-events: none; justify-content: center; gap: 3vw; align-items: flex-start; color: var(--color-title); font-size: clamp(2rem, 13.85vw, 14rem); font-family: "TheGoodMonolith", sans-serif; z-index: 100; } .title-wrapper span { display: block; line-height: 1; padding-top: 0.075em; } .title-wrapper > span:nth-child(2) { margin-top: 40px; } /* Line wrapper for text animations */ .line-wrapper { overflow: hidden; position: relative; } .line { display: block; position: relative; } /* Caption and category */ .slide-caption { position: fixed; top: 40px; left: 40px; font-size: 0.85rem; letter-spacing: 1px; opacity: 0; z-index: 95; font-family: "TheGoodMonolith", monospace; } .slide-category { position: fixed; top: 140px; right: 40px; font-size: 0.85rem; letter-spacing: 1px; opacity: 0; z-index: 95; font-family: "TheGoodMonolith", monospace; } /* Thumbnails */ .thumbnails { position: fixed; bottom: 20px; right: 20px; display: flex; gap: 10px; z-index: 200; opacity: 0; } .thumbnail { width: 60px; height: 40px; border-radius: 4px; overflow: hidden; cursor: pointer; opacity: 0; transform: translateY(20px); transition: border 0.3s ease; } .thumbnail-img { width: 100%; height: 100%; background-size: cover; background-position: center; } .thumbnail:hover { border: 2px solid rgba(255, 255, 255, 0.7); } .thumbnail.active { border: 2px solid #fff; } /* Enter button */ .switch { background: rgba(34, 34, 34, 0.8); z-index: 1000; padding: 10px 20px; display: flex; position: fixed; bottom: 20px; left: 50%; transform: translateX(-50%); align-items: center; gap: 20px; border-radius: 4px; transition: padding 0.3s ease-in-out; } .switch-button { display: inline-block; color: var(--color-switch-active); margin: 0; cursor: pointer; font-size: 14px; letter-spacing: 1px; padding: 5px 10px; background: transparent; font-family: "TheGoodMonolith", monospace; position: relative; transition: all 0.3s ease-in-out; } /* Indicator dot */ .indicator-dot { position: absolute; width: 5px; height: 5px; background-color: #f0f0f0; border-radius: 50%; opacity: 0; transition: opacity 0.3s ease-in-out; top: 50%; transform: translateY(-50%); left: -8px; } .switch-button:hover .indicator-dot { opacity: 1; } /* Content paragraph */ .content-paragraph { text-align: left; font-family: "TheGoodMonolith", monospace; font-size: 1rem; color: #f0f0f0; max-width: 600px; line-height: 1.3; margin-bottom: 10%; opacity: 0; position: fixed; bottom: 20%; left: 10%; z-index: 100; } /* Scroll text */ .scroll-text { position: fixed; bottom: 20px; left: 20px; z-index: 95; opacity: 0; font-family: "TheGoodMonolith", monospace; font-size: 14px; letter-spacing: 1px; color: var(--color-text); transition: opacity 0.5s ease; } /* Content section below the hero */ .content-section { width: 100%; min-height: 200vh; background-color: var(--color-bg); position: relative; z-index: 80; margin-top: 100vh; } /* Footer section */ .footer-section { width: 100%; height: 50vh; background-color: var(--color-bg); position: relative; z-index: 80; display: flex; flex-direction: column; justify-content: center; align-items: center; text-align: center; } .footer-text { font-family: "TheGoodMonolith", monospace; font-size: 1.2rem; letter-spacing: 2px; margin-bottom: 15px; } .footer-email { font-family: "TheGoodMonolith", monospace; font-size: 1.5rem; letter-spacing: 1px; color: var(--color-text); text-decoration: none; transition: color 0.3s ease; } .footer-email:hover { color: var(--color-link-hover); } .word { overflow: hidden; } /* Debug info */ .debug-info { position: fixed; top: 10px; left: 10px; background: rgba(0, 0, 0, 0.7); color: white; padding: 10px; font-size: 12px; z-index: 9999; max-width: 300px; display: none; } @media screen and (min-width: 53em) { .title-wrapper { padding: 0; } }
script.js: // Create custom eases and register GSAP plugins CustomEase.create("smoothIn", "0.4, 0, 0.2, 1"); CustomEase.create("smoothOut", "0.8, 0, 0.6, 1"); CustomEase.create("textReveal", "0.25, 1, 0.5, 1"); gsap.registerPlugin(ScrollTrigger, CustomEase, SplitText); // Initialize variables let lenis = null; let splitTextInstances = []; let isWebEntered = false; let activeIndex = 0; let isAnimating = false; // DOM elements const preloader = document.getElementById("preloader"); const preloaderCounter = document.getElementById("preloader-counter"); const body = document.body; const mainContent = document.querySelector("main"); const expandingImage = document.getElementById("expandingImage"); const enterButton = document.getElementById("enterButton"); const switchContainer = document.getElementById("switchContainer"); const scrollText = document.getElementById("scrollText"); const contentSection = document.getElementById("contentSection"); const heroSection = document.querySelector(".hero-section"); const slideTitle = document.getElementById("slideTitle"); const contentParagraph = document.getElementById("content-paragraph"); const slideCaption = document.getElementById("slideCaption"); const slideCategory = document.getElementById("slideCategory"); const grid = document.querySelector(".columns"); const columnsInner = { up: grid.querySelectorAll(".column-moveup > .column-inner"), down: grid.querySelectorAll(".column-movedown > .column-inner") }; const content = document.querySelector(".content"); const thumbnails = document.querySelector(".thumbnails"); const thumbnailItems = thumbnails.querySelectorAll(".thumbnail"); // Lock scrolling initially document.body.style.overflow = "hidden"; // Image and content data const imageUrls = [ "https://cdn.cosmos.so/5eee2d2d-3d4d-4ae5-96d4-cdbae70a2387?format=jpeg", "https://cdn.cosmos.so/def30e8a-34b2-48b1-86e1-07ec5c28f225?format=jpeg", "https://cdn.cosmos.so/44d7cb23-6759-49e4-9dc1-acf771b3a0d1?format=jpeg", "https://cdn.cosmos.so/7712fe42-42ca-4fc5-9590-c89f2db99978?format=jpeg", "https://cdn.cosmos.so/cbee1ec5-01b6-4ffe-9f34-7da7980454cf?format=jpeg", "https://cdn.cosmos.so/2e91a9d1-db85-4499-ad37-6222a6fea23b?format=jpeg", "https://cdn.cosmos.so/7d47d4e2-0eff-4e2f-9734-9d24a8ba067e?format=jpeg" ]; const slideTitles = [ { first: "Between", second: "Worlds" }, { first: "Eternal", second: "Now" }, { first: "Shadow", second: "Self" }, { first: "Empty", second: "Space" }, { first: "Surface", second: "Depth" }, { first: "Visible", second: "Unseen" }, { first: "Moment", second: "Echo" } ]; const slideCaptions = [ "01 / PERCEPTION", "02 / PRESENCE", "03 / DUALITY", "04 / ESSENCE", "05 / TEXTURE", "06 / PATTERN", "07 / AWARENESS" ]; const slideCategories = [ "VISUAL ESSAY", "MEDITATION", "EXPLORATION", "CONTEMPLATION", "SENSATION", "OBSERVATION", "PRACTICE" ]; const slideContent = [ "The space between what we see and what exists is where truth resides. In stillness, we find motion. In silence, we hear everything. The photograph captures not just light, but the absence of noise.", "There is only this moment. The past and future are constructs. These images exist in an eternal now, suspended between breaths. When we truly see, time dissolves and we become present with what is.", "We are both the observer and the observed. The shadow self contains what we hide, even from ourselves. These images reveal the dance between what we show and what we conceal. The silhouette speaks louder than the portrait.", "Emptiness is not nothing. It is the canvas upon which everything appears. These spaces between forms hold the potential for It is the canvas upon which everything appears. These spaces between forms hold the potential for all creation. The negative space defines the subject more than the subject itself.", "The world speaks through texture. Rough against smooth. Soft against hard. These tactile qualities connect us to the physical realm. The eye can feel what the hand cannot touch. Sensation transcends the visual.", "Chaos contains order. Order contains chaos. The city, like nature, follows invisible patterns. These rhythms and repetitions reveal themselves when we step back and observe without judgment or expectation.", "Photography is not about capturing. It's about becoming aware. These images don't freeze moments; they extend them into eternity. The camera is not a tool but a practice of seeing what others miss." ]; // Preloader function function runPreloader() { gsap.set(mainContent, { opacity: 0, visibility: "hidden" }); let counter = 0; const duration = 2000; const interval = 20; const increment = 100 / (duration / interval); const counterInterval = setInterval(() => { counter += increment; if (counter >= 100) { counter = 100; clearInterval(counterInterval); gsap.to(preloader, { opacity: 0, duration: 0.5, onComplete: () => { preloader.style.display = "none"; gsap.to(mainContent, { opacity: 1, visibility: "visible", duration: 0.5 }); gsap.timeline().set(grid, { opacity: 1 }).fromTo( ".column", { opacity: 0, y: 30 }, { opacity: 1, y: 0, stagger: 0.08, duration: 0.6, ease: "power2.out" } ); } }); } preloaderCounter.textContent = Math.floor(counter); }, interval); } // Text animation function using GSAP SplitText function animateText(element, delay = 0, staggered = false) { gsap.set(element, { opacity: 1 }); // Clean up previous split instances splitTextInstances = splitTextInstances.filter((instance) => { if (instance.target === element) { instance.revert(); return false; } return true; }); // Create new SplitText instance - using chars instead of lines for better control const splitText = new SplitText(element, { type: "words,chars", charsClass: "char", wordsClass: "word" }); splitTextInstances.push(splitText); // Create animation timeline const tl = gsap.timeline(); // Animate based on whether it's staggered or not if (staggered && element.children.length > 1) { // First line (first child) tl.from(element.children[0].querySelectorAll(".char"), { duration: 0.9, yPercent: 100, opacity: 0.2, stagger: 0.03, ease: "textReveal", delay: delay }); // Second line (second child) with delay tl.from( element.children[1].querySelectorAll(".char"), { duration: 0.9, yPercent: 100, opacity: 0.2, stagger: 0.03, ease: "textReveal" }, "-=0.5" ); } else { // Regular animation for all chars tl.from(splitText.chars, { duration: 0.9, yPercent: 100, opacity: 0.2, stagger: 0.02, ease: "textReveal", delay: delay }); } return tl; } // Update slide content with animation function updateSlideContent(index) { // Clean up previous SplitText instances splitTextInstances.forEach((instance) => { if (instance.revert) instance.revert(); }); splitTextInstances = []; // Update content const title = slideTitles[index]; slideTitle.innerHTML = ` <span class="content-title">${title.first}</span> <span class="content-title">${title.second}</span> `; contentParagraph.textContent = slideContent[index]; slideCaption.textContent = slideCaptions[index]; slideCategory.textContent = slideCategories[index]; // Create animation timeline const masterTl = gsap.timeline(); // Animate elements masterTl.fromTo( [slideCaption, slideCategory], { opacity: 0, y: -20 }, { opacity: 1, y: 0, duration: 0.6, stagger: 0.1, ease: "power2.out" }, 0 ); const titleTl = animateText(slideTitle, 0.05, true); const paragraphTl = animateText(contentParagraph, 0.4); masterTl.add(titleTl, 0); masterTl.add(paragraphTl, 0); return masterTl; } // Find leftmost visible image function findLeftmostVisibleImage() { const images = document.querySelectorAll(".column-item-img"); let leftmostImage = null; let leftmostX = Infinity; images.forEach((img) => { const rect = img.getBoundingClientRect(); if ( rect.left < window.innerWidth && rect.right > 0 && rect.top < window.innerHeight && rect.bottom > 0 ) { if (rect.left < leftmostX) { leftmostX = rect.left; leftmostImage = img; } } }); return leftmostImage; } // Initialize Lenis smooth scrolling function initializeLenis() { if (!lenis) { lenis = new Lenis({ duration: 1.2, easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)), direction: "vertical", gestureDirection: "vertical", smooth: true, mouseMultiplier: 1, smoothTouch: false, touchMultiplier: 2, infinite: false }); lenis.on("scroll", ScrollTrigger.update); gsap.ticker.add((time) => { lenis.raf(time * 1000); }); gsap.ticker.lagSmoothing(0); } } // Enter the web animation const enterTheWeb = () => { if (isAnimating || isWebEntered) return; isAnimating = true; switchContainer.style.paddingLeft = "30px"; gsap.to(switchContainer, { opacity: 0, y: 20, duration: 0.3, ease: "power2.in", onComplete: () => { switchContainer.style.display = "none"; } }); content.classList.add("content--current"); const tl = gsap.timeline({ onComplete: () => { isAnimating = false; isWebEntered = true; gsap.set( [ ".expanding-image", ".content", ".content-paragraph", ".thumbnails", ".scroll-text", ".slide-caption", ".slide-category" ], { position: "absolute" } ); initializeLenis(); document.body.style.overflow = "auto"; gsap.to(scrollText, { opacity: 1, duration: 0.5, delay: 0.5 }); ScrollTrigger.create({ trigger: ".hero-section", start: "top top", end: "bottom top", scrub: true, onUpdate: (self) => { gsap.to(".hero-section", { height: 100 - self.progress * 40 + "vh", duration: 0, width: "100%", transformOrigin: "center top" }); gsap.to(".expanding-image", { scale: 1 + self.progress * 0.1, y: -self.progress * 25 + "%", duration: 0, width: "100vw", height: 100 + self.progress * 30 + "vh" }); const contentWrapper = document.querySelector(".content"); gsap.to(contentWrapper, { y: -self.progress * 50 + "%", opacity: 1 - self.progress * 0.7, duration: 0 }); gsap.to(".content-paragraph", { y: -self.progress * 50 + "%", opacity: 1 - self.progress * 0.7, duration: 0 }); gsap.to([".slide-caption", ".slide-category"], { y: -self.progress * 50 + "%", opacity: 1 - self.progress * 0.7, duration: 0 }); gsap.to(".thumbnails", { y: -self.progress * 50 + "%", opacity: 1 - self.progress * 0.7, duration: 0 }); gsap.to(".scroll-text", { opacity: 1 - self.progress * 2, y: -self.progress * 20 + "%", duration: 0 }); } }); } }); // Step 1: Zoom grid tl.to(grid, { scale: 1, duration: 0.8, ease: "power2.inOut" }, 0); tl.to( columnsInner.up, { y: "-200vh", duration: 0.8, ease: "power2.inOut" }, 0 ); tl.to( columnsInner.down, { y: "200vh", duration: 0.8, ease: "power2.inOut" }, 0 ); // Step 2: Set up expanding image tl.add(() => { const leftImg = findLeftmostVisibleImage(); if (leftImg) { const imgRect = leftImg.getBoundingClientRect(); const imgStyle = window.getComputedStyle(leftImg); const bgImage = imgStyle.backgroundImage; expandingImage.style.backgroundImage = bgImage; expandingImage.style.top = `${imgRect.top}px`; expandingImage.style.left = `${imgRect.left}px`; expandingImage.style.width = `${imgRect.width}px`; expandingImage.style.height = `${imgRect.height}px`; expandingImage.style.opacity = 1; activeIndex = parseInt(leftImg.getAttribute("data-index")); document.querySelector(".thumbnail.active").classList.remove("active"); document .querySelector(`.thumbnail[data-index="${activeIndex}"]`) .classList.add("active"); } }, 0.8); // Step 3: Expand image tl.to( expandingImage, { width: "100vw", duration: 0.7, ease: "power2.inOut" }, 1.0 ); tl.to(grid, { opacity: 0, duration: 0.3 }, 1.2); // Step 4: Show content tl.add(() => updateSlideContent(activeIndex), 1.8); tl.set(thumbnails, { opacity: 1 }, 2.2); tl.to( thumbnailItems, { opacity: 1, y: 0, duration: 0.4, stagger: 0.05, ease: "power2.out" }, 2.3 ); }; // Handle thumbnail clicks thumbnailItems.forEach((thumb, index) => { thumb.addEventListener("click", () => { if (!isWebEntered || isAnimating) return; document.querySelector(".thumbnail.active").classList.remove("active"); thumb.classList.add("active"); activeIndex = index; gsap.to(expandingImage, { opacity: 0.5, duration: 0.3, onComplete: () => { expandingImage.style.backgroundImage = `url(${imageUrls[index]})`; gsap.to(expandingImage, { opacity: 1, duration: 0.3 }); } }); gsap.set([slideCaption, slideCategory], { opacity: 0, y: -20 }); updateSlideContent(index); }); }); // Enter button hover effects enterButton.addEventListener("mouseenter", () => { enterButton.querySelector(".indicator-dot").style.opacity = "1"; switchContainer.style.paddingLeft = "30px"; }); enterButton.addEventListener("mouseleave", () => { enterButton.querySelector(".indicator-dot").style.opacity = "0"; switchContainer.style.paddingLeft = "20px"; }); // Enter button click enterButton.addEventListener("click", enterTheWeb); // Scroll event handler window.addEventListener("scroll", () => { if (isWebEntered) { const scrollY = window.scrollY; if (scrollY > 100) { gsap.to(scrollText, { opacity: 0, duration: 0.3 }); } else { gsap.to(scrollText, { opacity: 1, duration: 0.3 }); } } }); // Start preloader when page is loaded window.addEventListener("load", runPreloader);

