style.css: @import url("https://fonts.cdnfonts.com/css/thegoodmonolith"); @font-face { src: url("https://fonts.cdnfonts.com/css/pp-neue-montreal") format("woff2"); font-family: "PP Neue Montreal", sans-serif; font-weight: 400; } * { margin: 0; padding: 0; box-sizing: border-box; } body { font-family: "TheGoodMonolith", monospace; background-color: #111; color: #f0f0f0; overflow: hidden; height: 100vh; width: 100vw; } .preloader { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background-color: #111; display: flex; justify-content: center; align-items: center; z-index: 9999; transition: opacity 0.5s ease, visibility 0.5s ease; } .preloader-counter { font-family: "TheGoodMonolith", monospace; font-size: 3rem; color: #f0f0f0; opacity: 1; } .preloader-hidden { opacity: 0; visibility: hidden; } /* Main container */ .container { position: relative; width: 100%; height: 100vh; overflow: hidden; } /* Grid layout */ .grid-container { position: absolute; top: 0; left: 0; width: 100%; height: 100%; overflow: hidden; } .grid { display: grid; grid-template-columns: repeat(3, 1fr); grid-template-rows: repeat(2, 1fr); gap: 0; width: 100%; height: 100%; will-change: transform; } .grid-item { position: relative; overflow: hidden; } .grid-item-img { width: 100%; height: 100%; background-size: cover; background-position: center; } /* Target image (5th image) */ .grid-item.target { grid-column: 2; grid-row: 2; } /* Slide layers - stacking order is important */ /* Base layer - current slide */ .slider-image { position: fixed; top: 0; left: 0; width: 100%; height: 100vh; background-size: cover; background-position: center; z-index: 80; opacity: 0; visibility: hidden; will-change: transform, opacity; } /* Background parallax layer */ .slider-image-bg { position: fixed; top: 0; left: 0; width: 110%; height: 110vh; background-size: cover; background-position: center; z-index: 85; /* Higher than base layer */ opacity: 0; visibility: hidden; will-change: transform; transform-origin: center; } /* Top layer - next slide */ .slider-image-next { position: fixed; top: 0; left: 0; width: 100%; height: 100vh; background-size: cover; background-position: center; z-index: 90; /* Highest z-index to always appear on top */ opacity: 0; visibility: hidden; will-change: transform; transform-origin: center; } /* Transition overlay */ .transition-overlay { position: fixed; top: 0; left: 0; width: 100%; height: 100vh; background: #000; z-index: 95; opacity: 0; visibility: hidden; will-change: opacity; } /* Content area */ .content { position: fixed; top: 0; left: 0; width: 100%; height: 100%; pointer-events: none; z-index: 100; opacity: 0; padding: 10% 10%; display: flex; flex-direction: column; justify-content: flex-end; } .content-title { text-align: left; font-family: "PP Neue Montreal", sans-serif; font-size: 5rem; color: #f0f0f0; text-transform: uppercase; letter-spacing: -0.02em; margin-bottom: 1.5rem; overflow: hidden; } .content-title span { display: block; transform: translateY(100%); } /* Simple paragraph styling */ .content-paragraph { text-align: left; font-family: "TheGoodMonolith", monospace; font-size: 1rem; color: #f0f0f0; max-width: 600px; line-height: 1.3; margin-bottom: 10%; opacity: 0; /* Start hidden */ } /* Thumbnails */ .thumbnails { position: fixed; bottom: 20px; right: 20px; display: flex; gap: 10px; z-index: 200; } .thumbnail { width: 60px; height: 40px; border-radius: 4px; overflow: hidden; cursor: pointer; opacity: 0; transform: translateY(20px); transition: border 0.3s ease; } .thumbnail-img { width: 100%; height: 100%; background-size: cover; background-position: center; } .thumbnail:hover { border: 2px solid rgba(255, 255, 255, 0.7); } .thumbnail.active { border: 2px solid #fff; } /* Switch buttons */ .switch { position: fixed; bottom: 20px; left: 50%; transform: translateX(-50%); display: flex; gap: 20px; background: #222; padding: 10px 20px; border-radius: 4px; z-index: 1000; transition: padding 0.3s ease-in-out; } .switch-button { background: none; border: none; color: #666; cursor: pointer; font-family: "TheGoodMonolith", monospace; font-size: 14px; padding: 5px 10px; position: relative; transition: all 0.3s ease-in-out; } .switch-button-current { color: #f0f0f0; } /* Indicator dot - smaller and closer to text */ .indicator-dot { position: absolute; width: 5px; /* Smaller dot */ height: 5px; /* Smaller dot */ background-color: #f0f0f0; border-radius: 50%; opacity: 0; transition: opacity 0.3s ease-in-out; top: 50%; transform: translateY(-50%); } .switch-button-grid .indicator-dot { left: -8px; /* Closer to text */ } .switch-button-slider .indicator-dot { right: -8px; /* Closer to text */ } .switch-button:hover .indicator-dot { opacity: 1; }
script.js: document.addEventListener("DOMContentLoaded", () => { const preloader = document.querySelector(".preloader"); const preloaderCounter = document.querySelector(".preloader-counter"); // Simplified preloader that works reliably let count = 0; const duration = 2048; // 2048ms (2^11) - power of 2 const increment = 5; // Increment by 5 each time const interval = 128; // Update every 128ms (2^7) - power of 2 // Simple interval-based counter const counterInterval = setInterval(() => { count += increment; if (count <= 100) { preloaderCounter.textContent = count; } else { // Ensure we reach exactly 100 at the end preloaderCounter.textContent = "100"; clearInterval(counterInterval); // Hide preloader after a short delay setTimeout(() => { preloader.classList.add("preloader-hidden"); // Initialize the main application setTimeout(() => { initApp(); }, 256); // 256ms (2^8) - power of 2 }, 256); // 256ms (2^8) - power of 2 } }, interval); }); function initApp() { // Check if GSAP and Flip are loaded if (typeof gsap === "undefined" || typeof Flip === "undefined") { console.error("GSAP or Flip plugin not loaded"); return; } // Create the custom eases if (typeof CustomEase !== "undefined") { // Our primary cubic beziers CustomEase.create("mainEase", "M0,0 C0.65,0.05 0.36,1 1,1"); // cubic-bezier(0.65,0.05,0.36,1) CustomEase.create("sideEase", "M0,0 C0.86,0 0.07,1 1,1"); // cubic-bezier(0.86,0,0.07,1) // Keep other eases for different contexts CustomEase.create("natural", "M0,0 C0.34,0.01 0.2,1 1,1"); CustomEase.create("naturalOut", "M0,0 C0.43,0.13 0.23,0.96 1,1"); CustomEase.create("cinematic", "M0,0 C0.645,0.045 0.355,1 1,1"); } // Power of 2 timing constants (in seconds) const TIMING = { BASE: 0.512, // 512ms - base unit (2^9 ms) SHORTEST: 0.256, // 256ms - half base (2^8 ms) SHORT: 0.384, // 384ms - 3/4 of base LONG: 0.768, // 768ms - 1.5x base LONGEST: 1.024, // 1024ms - double base (2^10 ms) STAGGER_TINY: 0.032, // 32ms - 1/16 of base STAGGER_SMALL: 0.064, // 64ms - 1/8 of base STAGGER_MED: 0.128, // 128ms - 1/4 of base PAUSE: 1.536 // 1536ms - 3x base }; // Elements const grid = document.getElementById("grid"); const gridContainer = document.querySelector(".grid-container"); const gridItems = document.querySelectorAll(".grid-item"); const sliderImage = document.getElementById("slider-image"); const sliderImageNext = document.getElementById("slider-image-next"); const sliderImageBg = document.getElementById("slider-image-bg"); const transitionOverlay = document.getElementById("transition-overlay"); const content = document.getElementById("content"); const contentTitle = document.querySelector(".content-title"); const contentTitleSpan = contentTitle.querySelector("span"); const contentParagraph = document.getElementById("content-paragraph"); const thumbnailItems = document.querySelectorAll(".thumbnail"); const switchContainer = document.getElementById("switch"); // Switch buttons const switchGrid = document.querySelector(".switch-button-grid"); const switchSlider = document.querySelector(".switch-button-slider"); // State let currentMode = "grid"; let isAnimating = false; let activeIndex = 4; // Default to 5th image (index 4) let previousIndex = 4; // Track previous index for transitions let slideDirection = "right"; // Default slide direction // Store all image URLs for easy access const imageUrls = Array.from(gridItems).map( (item) => item.querySelector(".grid-item-img").style.backgroundImage ); // Content for each slide const slideContent = [ { title: "URBAN GEOMETRY", paragraph: "The city speaks in angles and lines. What appears chaotic is actually ordered. When we slow down enough to truly see, patterns emerge from noise. Photography is meditation—a practice of presence that reveals the hidden structure beneath the surface of our daily experience." }, { title: "NATURAL ABSTRACTIONS", paragraph: "Nature doesn't try to be beautiful. It simply is. These patterns exist whether we observe them or not. The creative act is not about making something new, but about seeing what's already there. True art emerges when we remove ourselves from the equation." }, { title: "SHADOW PLAY", paragraph: "Light creates shadow. Shadow defines light. Neither exists without the other. This duality mirrors our own inner landscape. The photograph captures not just the external world, but the photographer's state of mind. What we choose to frame reveals what we value." }, { title: "MINIMALIST FORMS", paragraph: "When we strip away the unnecessary, what remains? The essence. The truth. These images aren't about what's there, but what isn't. Silence between notes creates the music. The space between thoughts is where creativity lives. Simplicity is the ultimate sophistication." }, { title: "MONOCHROME SERIES", paragraph: "Without color, we see differently. Form and texture speak louder. The world simplified to light and dark reveals truths that color often conceals. In this space between extremes, we find clarity. The photograph becomes a mirror reflecting not just what we see, but how we see." }, { title: "TEXTURAL STUDIES", paragraph: "Touch with your eyes. Feel the rough and smooth. These surfaces tell stories of time and transformation. Our minds crave texture—it grounds us in the physical world. In an increasingly digital existence, these tactile reminders connect us to our primal nature and the healing power of sensory experience." } ]; // Get grid item by index const getGridItemByIndex = (index) => { return document.querySelector(`.grid-item[data-index="${index}"]`); }; // Update content based on active index const updateContent = (index) => { // Get the content for this index const content = slideContent[index]; // Update the title contentTitleSpan.textContent = content.title; // Update the paragraph contentParagraph.textContent = content.paragraph; }; // Toggle function const toggleView = (mode) => { if (isAnimating || currentMode === mode) return; isAnimating = true; // Update buttons document .querySelector(".switch-button-current") .classList.remove("switch-button-current"); document .querySelector(`.switch-button-${mode}`) .classList.add("switch-button-current"); // Set new mode currentMode = mode; // Run animation if (mode === "slider") { showSliderView().then(() => (isAnimating = false)); } else { showGridView().then(() => (isAnimating = false)); } }; // Show slider view with two-step animation (height first, then width) const showSliderView = () => { return new Promise((resolve) => { // Get the active item const activeItem = getGridItemByIndex(activeIndex); const activeItemRect = activeItem.getBoundingClientRect(); // Get the active image URL and set it on the slider image const activeImageUrl = imageUrls[activeIndex]; sliderImage.style.backgroundImage = activeImageUrl; sliderImageBg.style.backgroundImage = activeImageUrl; // Apply consistent styling sliderImage.style.backgroundSize = "cover"; sliderImage.style.backgroundPosition = "center"; sliderImageBg.style.backgroundSize = "cover"; sliderImageBg.style.backgroundPosition = "center"; sliderImageNext.style.backgroundSize = "cover"; sliderImageNext.style.backgroundPosition = "center"; // Update content for the active slide updateContent(activeIndex); // Position the slider image exactly over the active item gsap.set(sliderImage, { width: activeItemRect.width, height: activeItemRect.height, x: activeItemRect.left, y: activeItemRect.top, opacity: 1, visibility: "visible" }); // STEP 1: First expand the height to 100vh using FLIP const heightState = Flip.getState(sliderImage); // Set the intermediate position (full height, original width) gsap.set(sliderImage, { height: "100vh", y: 0, width: activeItemRect.width, x: activeItemRect.left }); // Animate the height expansion Flip.from(heightState, { duration: TIMING.BASE, // 512ms - our base duration ease: "mainEase", // Our custom cubic-bezier onComplete: () => { // STEP 2: Then expand the width to 100vw using FLIP const widthState = Flip.getState(sliderImage); /