Step 2: Add custom Javascript

function initNavigation() {
  if (!initNavigation._hasResizeListener) {
    initNavigation._hasResizeListener = true;
    window.addEventListener('resize', debounce(initNavigation, 200));
  }

  const isMobile = window.innerWidth < 768;
  if (isMobile && initNavigation._lastMode !== 'mobile') {
    initMobileMenu();
    initNavigation._lastMode = 'mobile';
  } else if (!isMobile && initNavigation._lastMode !== 'desktop') {
    initDesktopDropdowns();
    initNavigation._lastMode = 'desktop';
  }
}

function debounce(fn, delay) {
  let timer;
  return () => {
    clearTimeout(timer);
    timer = setTimeout(fn, delay);
  };
}

function initMobileMenu() {
  const btn = document.querySelector('[data-menu-button]');
  const nav = document.querySelector('[data-menu-status]');
  if (!btn || !nav) return;

  btn.setAttribute('aria-expanded', 'false');
  btn.setAttribute('aria-controls', 'mobile-navigation');
  nav.setAttribute('id', 'mobile-navigation');
  nav.setAttribute('role', 'navigation');
  nav.setAttribute('aria-label', 'Main navigation');

  if (!btn._mobileClick) {
    btn._mobileClick = true;
    btn.addEventListener('click', () => {
      const open = nav.dataset.menuStatus === 'open';
      nav.dataset.menuStatus = open ? 'closed' : 'open';
      btn.setAttribute('aria-expanded', !open);

      // Close all dropdowns when closing the menu
      if (open) {
        Array.from(document.querySelectorAll('[data-dropdown-toggle]')).forEach(toggle => {
          toggle.dataset.dropdownToggle = 'closed';
          toggle.setAttribute('aria-expanded', 'false');
        });
      }
    });
  }

  Array.from(document.querySelectorAll('[data-dropdown-toggle]')).forEach((toggle, i) => {
    const dd = toggle.nextElementSibling;
    if (!dd || !dd.classList.contains('nav-dropdown')) return;
    if (toggle._mobileDropdownInit) return;
    toggle._mobileDropdownInit = true;

    toggle.setAttribute('aria-expanded', 'false');
    toggle.setAttribute('aria-haspopup', 'true');
    toggle.setAttribute('aria-controls', `dropdown-${i}`);

    dd.setAttribute('id', `dropdown-${i}`);
    dd.setAttribute('role', 'menu');
    dd.querySelectorAll('.nav-dropdown__link')
      .forEach(link => link.setAttribute('role', 'menuitem'));

    toggle.addEventListener('click', () => {
      const open = toggle.dataset.dropdownToggle === 'open';
      Array.from(document.querySelectorAll('[data-dropdown-toggle]'))
        .forEach(other => {
          if (other !== toggle) {
            other.dataset.dropdownToggle = 'closed';
            other.setAttribute('aria-expanded', 'false');
            if (other === document.activeElement) other.blur();
          }
        });
      toggle.dataset.dropdownToggle = open ? 'closed' : 'open';
      toggle.setAttribute('aria-expanded', !open);
      if (open && toggle === document.activeElement) toggle.blur();
    });
  });
}

function initDesktopDropdowns() {
  const toggles = Array.from(document.querySelectorAll('[data-dropdown-toggle]'));
  const links = Array.from(document.querySelectorAll('.nav-link:not([data-dropdown-toggle])'));

  toggles.forEach((toggle, i) => {
    const dd = toggle.nextElementSibling;
    if (!dd || !dd.classList.contains('nav-dropdown') || toggle._desktopInit) return;
    toggle._desktopInit = true;

    toggle.setAttribute('aria-expanded', 'false');
    toggle.setAttribute('aria-haspopup', 'true');
    toggle.setAttribute('aria-controls', `desktop-dropdown-${i}`);

    dd.setAttribute('id', `desktop-dropdown-${i}`);
    dd.setAttribute('role', 'menu');
    dd.setAttribute('aria-hidden', 'true');
    dd.querySelectorAll('.nav-dropdown__link')
      .forEach(link => link.setAttribute('role', 'menuitem'));

    toggle.addEventListener('click', e => {
      e.preventDefault();
      toggles.forEach(other => {
        if (other !== toggle) {
          other.dataset.dropdownToggle = 'closed';
          other.setAttribute('aria-expanded', 'false');
          const otherDropdown = other.nextElementSibling;
          if (otherDropdown) otherDropdown.setAttribute('aria-hidden', 'true');
        }
      });
      const open = toggle.dataset.dropdownToggle !== 'open';
      toggle.dataset.dropdownToggle = 'open';
      toggle.setAttribute('aria-expanded', 'true');
      dd.setAttribute('aria-hidden', 'false');
      if (open) {
        const first = dd.querySelector('.nav-dropdown__link');
        if (first) first.focus();
      }
    });

    toggle.addEventListener('mouseenter', () => {
      const anyOpen = toggles.some(x => x.dataset.dropdownToggle === 'open');
      toggles.forEach(other => {
        if (other !== toggle) {
          other.dataset.dropdownToggle = 'closed';
          other.setAttribute('aria-expanded', 'false');
          const otherDropdown = other.nextElementSibling;
          if (otherDropdown) otherDropdown.setAttribute('aria-hidden', 'true');
        }
      });
      if (anyOpen) {
        setTimeout(() => {
          toggle.dataset.dropdownToggle = 'open';
          toggle.setAttribute('aria-expanded', 'true');
          dd.setAttribute('aria-hidden', 'false');
        }, 20);
      } else {
        toggle.dataset.dropdownToggle = 'open';
        toggle.setAttribute('aria-expanded', 'true');
        dd.setAttribute('aria-hidden', 'false');
      }
    });

    dd.addEventListener('mouseleave', () => {
      toggle.dataset.dropdownToggle = 'closed';
      toggle.setAttribute('aria-expanded', 'false');
      dd.setAttribute('aria-hidden', 'true');
    });

    toggle.addEventListener('keydown', e => {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        toggle.click();
      } else if (e.key === 'Escape') {
        toggle.dataset.dropdownToggle = 'closed';
        toggle.setAttribute('aria-expanded', 'false');
        dd.setAttribute('aria-hidden', 'true');
        toggle.focus();
      }
    });

    dd.addEventListener('keydown', e => {
      const items = Array.from(dd.querySelectorAll('.nav-dropdown__link'));
      const idx = items.indexOf(document.activeElement);
      if (e.key === 'ArrowDown') {
        e.preventDefault();
        items[(idx + 1) % items.length].focus();
      } else if (e.key === 'ArrowUp') {
        e.preventDefault();
        items[(idx - 1 + items.length) % items.length].focus();
      } else if (e.key === 'Escape') {
        e.preventDefault();
        toggle.dataset.dropdownToggle = 'closed';
        toggle.setAttribute('aria-expanded', 'false');
        dd.setAttribute('aria-hidden', 'true');
        toggle.focus();
      } else if (e.key === 'Tab' && !dd.contains(e.relatedTarget)) {
        toggle.dataset.dropdownToggle = 'closed';
        toggle.setAttribute('aria-expanded', 'false');
        dd.setAttribute('aria-hidden', 'true');
      }
    });
  });

  links.forEach(link => {
    link.addEventListener('mouseenter', () => {
      toggles.forEach(toggle => {
        toggle.dataset.dropdownToggle = 'closed';
        toggle.setAttribute('aria-expanded', 'false');
        const dd = toggle.nextElementSibling;
        if (dd) dd.setAttribute('aria-hidden', 'true');
      });
    });
  });

  document.addEventListener('click', e => {
    const inside = toggles.some(toggle => {
      const dd = toggle.nextElementSibling;
      return toggle.contains(e.target) || (dd && dd.contains(e.target));
    });
    if (!inside) {
      toggles.forEach(toggle => {
        toggle.dataset.dropdownToggle = 'closed';
        toggle.setAttribute('aria-expanded', 'false');
        const dd = toggle.nextElementSibling;
        if (dd) dd.setAttribute('aria-hidden', 'true');
      });
    }
  });
}

// Initialize Multilevel Navigation
document.addEventListener('DOMContentLoaded', () => {
  initNavigation();
});

Implementation

Documentation
This created with the aim of providing you with a thorough starting point for all your custom navigation bars. All of the animations are handled by CSS transition, so you're free to tweak any easing and duration as you want.

Responsiveness
To keep things organized, the elements that form the center nav links on desktop, are the same elements used for the mobile menu. So there's some pretty significant style changes happening on smaller breakpoints. In our example, we switch to a burger menu on screens smaller than 768px wide. You are free to change this to whatever breakpoint you want. Just make sure to:
- Update the CSS for the breakpoints accordingly
- Update the CSS media queries that decide between desktop/mobile animations
- Update the JS function for the dropdown toggles to match your new breakpoint

Edit dropdown content in Webflow
To make editing the dropdown contents in Webflow specifically a bit easier, you can set the [data-dropdown-toggle="open"] status on any dropdown toggle. Simply remove the "open" value again to continue editing the rest of the site.

code.script.js:
import gsap from "gsap";

const menuItems = [
  { label: "Vision", icon: "scan-sharp", href: "#vision" },
  { label: "Portfolio", icon: "layers-sharp", href: "#portfolio" },
  { label: "People", icon: "person-sharp", href: "#people" },
  { label: "Insights", icon: "browsers-sharp", href: "#insights" },
  { label: "Careers", icon: "stats-chart-sharp", href: "#careers" },
  { label: "About Us", icon: "reader-sharp", href: "#about" },
];

let isOpen = false;
let isMenuAnimating = false;
let responsiveConfig = {};

function getResponsiveConfig() {
  const viewportWidth = window.innerWidth;
  const viewportHeight = window.innerHeight;
  const isMobile = viewportWidth < 1000;

  const maxSize = Math.min(viewportWidth * 0.9, viewportHeight * 0.9);
  const menuSize = isMobile ? Math.min(maxSize, 480) : 700;

  return {
    menuSize,
    center: menuSize / 2,
    innerRadius: menuSize * 0.08,
    outerRadius: menuSize * 0.42,
    contentRadius: menuSize * 0.28,
  };
}

document.addEventListener("DOMContentLoaded", () => {
  responsiveConfig = getResponsiveConfig();

  const menu = document.querySelector(".circular-menu");
  const joystick = document.querySelector(".joystick");
  const menuOverlayNav = document.querySelector(".menu-overlay-nav");
  const menuOverlayFooter = document.querySelector(".menu-overlay-footer");

  menu.style.width = `${responsiveConfig.menuSize}px`;
  menu.style.height = `${responsiveConfig.menuSize}px`;

  gsap.set(joystick, { scale: 0 });
  gsap.set([menuOverlayNav, menuOverlayFooter], { opacity: 0 });

  menuItems.forEach((item, index) => {
    const segment = createSegment(item, index, menuItems.length);
    segment.addEventListener("mouseenter", () => {
      if (isOpen) {
        new Audio("/menu-select.mp3").play().catch(() => {});
      }
    });
    menu.appendChild(segment);
  });

  document
    .querySelector(".menu-toggle-btn")
    .addEventListener("click", toggleMenu);
  document.querySelector(".close-btn").addEventListener("click", toggleMenu);

  initCenterDrag();
});

function createSegment(item, index, total) {
  const segment = document.createElement("a");
  segment.className = "menu-segment";
  segment.href = item.href;

  const { menuSize, center, innerRadius, outerRadius, contentRadius } =
    responsiveConfig;

  const anglePerSegment = 360 / total;
  const baseStartAngle = anglePerSegment * index;
  const centerAngle = baseStartAngle + anglePerSegment / 2;
  const startAngle = baseStartAngle + 0.19;
  const endAngle = baseStartAngle + anglePerSegment - 0.19;

  const innerStartX =
    center + innerRadius * Math.cos(((startAngle - 90) * Math.PI) / 180);
  const innerStartY =
    center + innerRadius * Math.sin(((startAngle - 90) * Math.PI) / 180);
  const outerStartX =
    center + outerRadius * Math.cos(((startAngle - 90) * Math.PI) / 180);
  const outerStartY =
    center + outerRadius * Math.sin(((startAngle - 90) * Math.PI) / 180);
  const innerEndX =
    center + innerRadius * Math.cos(((endAngle - 90) * Math.PI) / 180);
  const innerEndY =
    center + innerRadius * Math.sin(((endAngle - 90) * Math.PI) / 180);
  const outerEndX =
    center + outerRadius * Math.cos(((endAngle - 90) * Math.PI) / 180);
  const outerEndY =
    center + outerRadius * Math.sin(((endAngle - 90) * Math.PI) / 180);

  const largeArcFlag = endAngle - startAngle > 180 ? 1 : 0;

  const pathData = [
    `M ${innerStartX} ${innerStartY}`,
    `L ${outerStartX} ${outerStartY}`,
    `A ${outerRadius} ${outerRadius} 0 ${largeArcFlag} 1 ${outerEndX} ${outerEndY}`,
    `L ${innerEndX} ${innerEndY}`,
    `A ${innerRadius} ${innerRadius} 0 ${largeArcFlag} 0 ${innerStartX} ${innerStartY}`,
    "Z",
  ].join(" ");

  segment.style.clipPath = `path('${pathData}')`;
  segment.style.width = `${menuSize}px`;
  segment.style.height = `${menuSize}px`;

  const contentX =
    center + contentRadius * Math.cos(((centerAngle - 90) * Math.PI) / 180);
  const contentY =
    center + contentRadius * Math.sin(((centerAngle - 90) * Math.PI) / 180);

  segment.innerHTML = `
    <div class="segment-content" 
      style="left: ${contentX}px; top: ${contentY}px; transform: translate(-50%, -50%);">
      <ion-icon name="${item.icon}"></ion-icon>
      <div class="label">${item.label}</div>
    </div>
  `;

  return segment;
}

function toggleMenu() {
  if (isMenuAnimating) return;

  const menuOverlay = document.querySelector(".menu-overlay");
  const menuSegments = document.querySelectorAll(".menu-segment");
  const joystick = document.querySelector(".joystick");
  const menuOverlayNav = document.querySelector(".menu-overlay-nav");
  const menuOverlayFooter = document.querySelector(".menu-overlay-footer");

  isMenuAnimating = true;

  if (!isOpen) {
    isOpen = true;
    new Audio("/menu-open.mp3").play();

    gsap.to(menuOverlay, {
      opacity: 1,
      duration: 0.3,
      ease: "power2.out",
      onStart: () => (menuOverlay.style.pointerEvents = "all"),
    });

    gsap.to(joystick, {
      scale: 1,
      duration: 0.4,
      delay: 0.2,
      ease: "back.out(1.7)",
    });

    gsap.set([menuOverlayNav, menuOverlayFooter], { opacity: 0 });
    gsap.to([menuOverlayNav, menuOverlayFooter], {
      opacity: 1,
      duration: 0.075,
      delay: 0.3,
      repeat: 3,
      yoyo: true,
      ease: "power2.inOut",
      onComplete: () =>
        gsap.set([menuOverlayNav, menuOverlayFooter], { opacity: 1 }),
    });

    [...Array(menuSegments.length).keys()]
      .sort(() => Math.random() - 0.5)
      .forEach((originalIndex, shuffledPosition) => {
        const segment = menuSegments[originalIndex];
        gsap.set(segment, { opacity: 0 });
        gsap.to(segment, {
          opacity: 1,
          duration: 0.075,
          delay: shuffledPosition * 0.075,
          repeat: 3,
          yoyo: true,
          ease: "power2.inOut",
          onComplete: () => {
            gsap.set(segment, { opacity: 1 });
            if (originalIndex === menuSegments.length - 1) {
              isMenuAnimating = false;
            }
          },
        });
      });
  } else {
    isOpen = false;
    new Audio("/menu-close.mp3").play();

    gsap.to([menuOverlayNav, menuOverlayFooter], {
      opacity: 0,
      duration: 0.05,
      repeat: 2,
      yoyo: true,
      ease: "power2.inOut",
      onComplete: () =>
        gsap.set([menuOverlayNav, menuOverlayFooter], { opacity: 0 }),
    });

    gsap.to(joystick, {
      scale: 0,
      duration: 0.3,
      delay: 0.2,
      ease: "back.in(1.7)",
    });

    [...Array(menuSegments.length).keys()]
      .sort(() => Math.random() - 0.5)
      .forEach((originalIndex, shuffledPosition) => {
        const segment = menuSegments[originalIndex];
        gsap.to(segment, {
          opacity: 0,
          duration: 0.05,
          delay: shuffledPosition * 0.05,
          repeat: 2,
          yoyo: true,
          ease: "power2.inOut",
          onComplete: () => gsap.set(segment, { opacity: 0 }),
        });
      });

    gsap.to(menuOverlay, {
      opacity: 0,
      duration: 0.3,
      delay: 0.6,
      ease: "power2.out",
      onComplete: () => {
        menuOverlay.style.pointerEvents = "none";
        isMenuAnimating = false;
      },
    });
  }
}

function initCenterDrag() {
  const joystick = document.querySelector(".joystick");
  let isDragging = false;
  let currentX = 0;
  let currentY = 0;
  let targetX = 0;
  let targetY = 0;
  let activeSegment = null;

  function animate() {
    currentX += (targetX - currentX) * 0.15;
    currentY += (targetY - currentY) * 0.15;

    gsap.set(joystick, {
      x: currentX,
      y: currentY,
    });

    if (
      isDragging &&
      Math.sqrt(currentX * currentX + currentY * currentY) > 20
    ) {
      const angle = Math.atan2(currentY, currentX) * (180 / Math.PI);
      const segmentIndex =
        Math.floor(((angle + 90 + 360) % 360) / (360 / menuItems.length)) %
        menuItems.length;
      const segment = document.querySelectorAll(".menu-segment")[segmentIndex];

      if (segment !== activeSegment) {
        if (activeSegment) {
          activeSegment.style.animation = "";
          activeSegment.querySelector(".segment-content").style.animation = "";
          activeSegment.style.zIndex = "";
        }
        activeSegment = segment;
        segment.style.animation = "flickerHover 350ms ease-in-out forwards";
        segment.querySelector(".segment-content").style.animation =
          "contentFlickerHover 350ms ease-in-out forwards";
        segment.style.zIndex = "10";
        if (isOpen) {
          new Audio("/menu-select.mp3").play().catch(() => {});
        }
      }
    } else {
      if (activeSegment) {
        activeSegment.style.animation = "";
        activeSegment.querySelector(".segment-content").style.animation = "";
        activeSegment.style.zIndex = "";
        activeSegment = null;
      }
    }

    requestAnimationFrame(animate);
  }

  joystick.addEventListener("mousedown", (e) => {
    isDragging = true;
    const rect = joystick.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    function drag(e) {
      if (!isDragging) return;
      const deltaX = (e.clientX || e.touches[0]?.clientX) - centerX;
      const deltaY = (e.clientY || e.touches[0]?.clientY) - centerY;
      const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);
      const maxDrag = 100 * 0.25;

      if (distance <= 20) {
        targetX = targetY = 0;
      } else if (distance > maxDrag) {
        const ratio = maxDrag / distance;
        targetX = deltaX * ratio;
        targetY = deltaY * ratio;
      } else {
        targetX = deltaX;
        targetY = deltaY;
      }
      e.preventDefault();
    }

    function endDrag() {
      isDragging = false;
      targetX = targetY = 0;
      document.removeEventListener("mousemove", drag);
      document.removeEventListener("mouseup", endDrag);
    }

    document.addEventListener("mousemove", drag);
    document.addEventListener("mouseup", endDrag);
    e.preventDefault();
  });

  animate();
}

