  background: #000;
}

p,
a {
  color: #fff;
  text-decoration: none;
  text-transform: uppercase;
  font-family: "IBM Plex Mono";
  font-size: 11px;
}

.menu-toggle-btn {
  position: fixed;
  bottom: -6rem;
  left: 50%;
  transform: translateX(-50%);
  width: 25rem;
  height: 10rem;
  padding-top: 2rem;
  border-radius: 50% 50% 0 0 / 90% 90% 0 0;
  background-color: #fff;
  color: #000;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  gap: 0.25rem;
  cursor: pointer;
}

.menu-toggle-btn .hamburger-bar {
  width: 2rem;
  height: 0.125rem;
  background-color: #000;
}

.menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100svh;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  z-index: 100;
}

.menu-bg {
  position: absolute;
  width: 100%;
  height: 100%;
  background: url(/menu.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.menu-overlay-nav,
.menu-overlay-footer {
  position: absolute;
  width: 100vw;
  padding: 1.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.menu-overlay-nav {
  top: 0;
}

.menu-overlay-footer {
  bottom: 0;
}

.close-btn {
  position: relative;
  width: 1.5rem;
  height: 1.5rem;
  cursor: pointer;
}

.close-btn .close-btn-bar {
  position: absolute;
  top: 50%;
  width: 1.5rem;
  height: 0.125rem;
  background-color: #fff;
}

.close-btn .close-btn-bar:nth-child(1) {
  transform: rotate(45deg);
}

.close-btn .close-btn-bar:nth-child(2) {
  transform: rotate(-45deg);
}

.menu-overlay-items {
  display: flex;
  gap: 1rem;
}

.menu-overlay-nav .menu-overlay-items a {
  font-size: 18px;
}

.circular-menu {
  position: relative;
  width: 600px;
  height: 600px;
  z-index: 10;
}

.joystick {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100px;
  height: 100px;
  background: #fff;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  user-select: none;
  cursor: grab;
  z-index: 100;
}

.center-icon {
  position: absolute;
  color: #000;
  font-size: 12px;
}

.center-main {
  font-size: 30px;
}

.center-up {
  top: 0.75rem;
  left: 50%;
  transform: translateX(-50%);
}

.center-down {
  bottom: 0.75rem;
  left: 50%;
  transform: translateX(-50%);
}

.center-left {
  left: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
}

.center-right {
  right: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
}

.menu-segment {
  position: absolute;
  width: 100%;
  height: 100%;
  color: #fff;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  cursor: pointer;
}

.segment-content {
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  text-align: center;
}

.segment-content ion-icon {
  font-size: 40px;
  margin-bottom: 10px;
}

.label {
  text-transform: none;
  font-family: "Host Grotesk";
  font-size: 15px;
  font-weight: 500;
}

.menu-segment:hover {
  animation: flickerHover 350ms ease-in-out forwards;
  z-index: 10;
}

.menu-segment:hover .segment-content {
  animation: contentFlickerHover 350ms ease-in-out forwards;
}

.menu-overlay {
  opacity: 0;
  pointer-events: none;
}

@media (max-width: 1000px) {
  .segment-content ion-icon {
    font-size: 20px;
  }
}

@keyframes flickerHover {
  0% {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
  }
  12% {
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(8px);
  }
  24% {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(18px);
  }
  36% {
    background: rgba(255, 255, 255, 0.85);
    backdrop-filter: blur(5px);
  }
  48% {
    background: rgba(255, 255, 255, 0.25);
    backdrop-filter: blur(15px);
  }
  60% {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(3px);
  }
  72% {
    background: rgba(255, 255, 255, 0.3);
    backdrop-filter: blur(12px);
  }
  84% {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(2px);
  }
  100% {
    background: rgba(255, 255, 255, 1);
    backdrop-filter: blur(0px);
  }
}

@keyframes contentFlickerHover {
  0% {
    color: white;
    opacity: 1;
  }
  12% {
    color: #333;
    opacity: 0.4;
  }
  24% {
    color: white;
    opacity: 0.9;
  }
  36% {
    color: #333;
    opacity: 0.3;
  }
  48% {
    color: white;
    opacity: 0.8;
  }
  60% {
    color: #333;
    opacity: 0.2;
  }
  72% {
    color: white;
    opacity: 0.7;
  }
  84% {
    color: #333;
    opacity: 0.1;
  }
  100% {
    color: #000;
    opacity: 1;
  }
}

code.script.js:
import { gsap } from "gsap";
import { ScrollTrigger, SplitText } from "gsap/all";
import Lenis from "lenis";

document.addEventListener("DOMContentLoaded", () => {
  gsap.registerPlugin(ScrollTrigger, SplitText);

  const lenis = new Lenis();
  lenis.on("scroll", ScrollTrigger.update);
  gsap.ticker.add((time) => {
    lenis.raf(time * 1000);
  });
  gsap.ticker.lagSmoothing(0);

  const container = document.querySelector(".container");
  const navToggle = document.querySelector(".nav-toggle");
  const menuOverlay = document.querySelector(".menu-overlay");
  const menuContent = document.querySelector(".menu-content");
  const menuImage = document.querySelector(".menu-img");
  const menuLinksWrapper = document.querySelector(".menu-links-wrapper");
  const linkHighlighter = document.querySelector(".link-highlighter");

  let currentX = 0;
  let targetX = 0;
  const lerpFactor = 0.05;

  let currentHighlighterX = 0;
  let targetHighlighterX = 0;
  let currentHighlighterWidth = 0;
  let targetHighlighterWidth = 0;

  let isMenuOpen = false;
  let isMenuAnimating = false;

  const menuLinks = document.querySelectorAll(".menu-link a");
  menuLinks.forEach((link) => {
    const chars = link.querySelectorAll("span");
    chars.forEach((char, charIndex) => {
      const split = new SplitText(char, { type: "chars" });
      split.chars.forEach((char) => {
        char.classList.add("char");
      });
      if (charIndex === 1) {
        gsap.set(split.chars, { y: "110%" });
      }
    });
  });

  gsap.set(menuContent, { y: "50%", opacity: 0.25 });
  gsap.set(menuImage, { scale: 0.5, opacity: 0.25 });
  gsap.set(menuLinks, { y: "150%" });
  gsap.set(linkHighlighter, { y: "150%" });

  const defaultLinkText = document.querySelector(
    ".menu-link:first-child a span"
  );
  if (defaultLinkText) {
    const linkWidth = defaultLinkText.offsetWidth;
    linkHighlighter.style.width = linkWidth + "px";
    currentHighlighterWidth = linkWidth;
    targetHighlighterWidth = linkWidth;

    const defaultLinkTextElement = document.querySelector(
      ".menu-link:first-child"
    );
    const linkRect = defaultLinkTextElement.getBoundingClientRect();
    const menuWrapperRect = menuLinksWrapper.getBoundingClientRect();
    const initialX = linkRect.left - menuWrapperRect.left;
    currentHighlighterX = initialX;
    targetHighlighterX = initialX;
  }

  function toggleMenu() {
    if (isMenuAnimating) return;
    isMenuAnimating = true;

    if (!isMenuOpen) {
      gsap.to(container, {
        y: "-40%",
        opacity: 0.25,
        duration: 1.25,
        ease: "expo.out",
      });

      gsap.to(menuOverlay, {
        clipPath: "polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%)",
        duration: 1.25,
        ease: "expo.out",
        onComplete: () => {
          gsap.set(container, { y: "40%" });
          gsap.set(".menu-link", { overflow: "visible" });

          isMenuOpen = true;
          isMenuAnimating = false;
        },
      });

      gsap.to(menuContent, {
        y: "0%",
        opacity: 1,
        duration: 1.5,
        ease: "expo.out",
      });

      gsap.to(menuImage, {
        scale: 1,
        opacity: 1,
        duration: 1.5,
        ease: "expo.out",
      });

      gsap.to(menuLinks, {
        y: "0%",
        duration: 1.25,
        stagger: 0.1,
        delay: 0.25,
        ease: "expo.out",
      });

      gsap.to(linkHighlighter, {
        y: "0%",
        duration: 1,
        delay: 1,
        ease: "expo.out",
      });
    } else {
      gsap.to(container, {
        y: "0%",
        opacity: 1,
        duration: 1.25,
        ease: "expo.out",
      });

      gsap.to(menuLinks, {
        y: "-200%",
        duration: 1.25,
        ease: "expo.out",
      });

      gsap.to(menuContent, {
        y: "-100%",
        opacity: 0.25,
        duration: 1.25,
        ease: "expo.out",
      });

      gsap.to(menuImage, {
        y: "-100%",
        opacity: 0.5,
        duration: 1.25,
        ease: "expo.out",
      });

      gsap.to(menuOverlay, {
        clipPath: "polygon(0% 0%, 100% 0%, 100% 0%, 0% 0%)",
        duration: 1.25,
        ease: "expo.out",
        onComplete: () => {
          gsap.set(menuOverlay, {
            clipPath: "polygon(0% 100%, 100% 100%, 100% 100%, 0% 100%)",
          });
          gsap.set(menuLinks, { y: "150%" });
          gsap.set(linkHighlighter, { y: "150%" });
          gsap.set(menuContent, { y: "50%", opacity: 0.25 });
          gsap.set(menuImage, { y: "0%", scale: 0.5, opacity: 0.25 });
          gsap.set(".menu-link", { overflow: "hidden" });

          gsap.set(menuLinksWrapper, { x: 0 });
          currentX = 0;
          targetX = 0;

          isMenuOpen = false;
          isMenuAnimating = false;
        },
      });
    }
  }

  navToggle.addEventListener("click", toggleMenu);

  const menuLinkContainers = document.querySelectorAll(".menu-link");
  menuLinkContainers.forEach((link) => {
    link.addEventListener("mouseenter", () => {
      if (window.innerWidth < 1000) return;

      const linkCopy = link.querySelectorAll("a span");
      const visibleCopy = linkCopy[0];
      const animatedCopy = linkCopy[1];

      const visibleChars = visibleCopy.querySelectorAll(".char");
      gsap.to(visibleChars, {
        y: "-110%",
        stagger: 0.03,
        duration: 0.5,
        ease: "expo.inOut",
      });

      const animatedChars = animatedCopy.querySelectorAll(".char");
      gsap.to(animatedChars, {
        y: "0%",
        stagger: 0.03,
        duration: 0.5,
        ease: "expo.inOut",
      });
    });

    link.addEventListener("mouseleave", () => {
      if (window.innerWidth < 1000) return;

      const linkCopy = link.querySelectorAll("a span");
      const visibleCopy = linkCopy[0];
      const animatedCopy = linkCopy[1];

      const animatedChars = animatedCopy.querySelectorAll(".char");
      gsap.to(animatedChars, {
        y: "110%",
        stagger: 0.03,
        duration: 0.5,
        ease: "expo.inOut",
      });

      const visibleChars = visibleCopy.querySelectorAll(".char");
      gsap.to(visibleChars, {
        y: "0%",
        stagger: 0.03,
        duration: 0.5,
        ease: "expo.inOut",
      });
    });
  });

  menuOverlay.addEventListener("mousemove", (e) => {
    if (window.innerWidth < 1000) return;

    const mouseX = e.clientX;
    const viewportWidth = window.innerWidth;
    const menuLinksWrapperWidth = menuLinksWrapper.offsetWidth;

    const maxMoveLeft = 0;
    const maxMoveRight = viewportWidth - menuLinksWrapperWidth;

    const sensitivityRange = viewportWidth * 0.5;
    const startX = (viewportWidth - sensitivityRange) / 2;
    const endX = startX + sensitivityRange;

    let mousePercentage;
    if (mouseX <= startX) {
      mousePercentage = 0;
    } else if (mouseX >= endX) {
      mousePercentage = 1;
    } else {
      mousePercentage = (mouseX - startX) / sensitivityRange;
    }

    targetX = maxMoveLeft + mousePercentage * (maxMoveRight - maxMoveLeft);
  });

  menuLinkContainers.forEach((link) => {
    link.addEventListener("mouseenter", () => {
      if (window.innerWidth < 1000) return;

      const linkRect = link.getBoundingClientRect();
      const menuWrapperRect = menuLinksWrapper.getBoundingClientRect();

      targetHighlighterX = linkRect.left - menuWrapperRect.left;

      const linkCopyElement = link.querySelector("a span");
      targetHighlighterWidth = linkCopyElement
        ? linkCopyElement.offsetWidth
        : link.offsetWidth;
    });
  });

  menuLinksWrapper.addEventListener("mouseleave", () => {
    const defaultLinkText = document.querySelector(".menu-link:first-child");
    const defaultLinkTextSpan = defaultLinkText.querySelector("a span");

    const linkRect = defaultLinkText.getBoundingClientRect();
    const menuWrapperRect = menuLinksWrapper.getBoundingClientRect();

    targetHighlighterX = linkRect.left - menuWrapperRect.left;
    targetHighlighterWidth = defaultLinkTextSpan.offsetWidth;
  });

  function animate() {
    currentX += (targetX - currentX) * lerpFactor;
    currentHighlighterX +=
      (targetHighlighterX - currentHighlighterX) * lerpFactor;
    currentHighlighterWidth +=
      (targetHighlighterWidth - currentHighlighterWidth) * lerpFactor;

    gsap.to(menuLinksWrapper, {
      x: currentX,
      duration: 0.3,
      ease: "power4.out",
    });

    gsap.to(linkHighlighter, {
      x: currentHighlighterX,
      width: currentHighlighterWidth,
      duration: 0.3,
      ease: "power4.out",
    });

    requestAnimationFrame(animate);
  }

  animate();
});

code.styles.css:
@import url("https://fonts.googleapis.com/css2?family=Anton&family=DM+Sans:ital,opsz,wght@0,9..40,100..1000;1,9..40,100..1000&display=swap");

:root {
  --dark: #1e1e1e;
  --light: #fefff8;
  --accent: #fca311;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "DM Sans", sans-serif;
  background-color: #000;
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

h1 {
  text-transform: uppercase;
  font-family: "Anton";
  font-size: 10rem;
  font-weight: 500;
  letter-spacing: -0.1rem;
  line-height: 0.9;
}

p,
a {
  text-decoration: none;
  text-transform: uppercase;
  font-size: 0.8rem;
  font-weight: 600;
  line-height: 1;
  user-select: none;
}

.container {
  position: relative;
  width: 100%;
  height: 100%;
  will-change: transform, opacity;
  z-index: 0;
}

section {
  position: relative;
  width: 100vw;
  height: 100svh;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  background-color: var(--light);
  color: var(--dark);
  padding: 2rem;
}

.hero h1 {
  width: 70%;
}

nav {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  padding: 1rem;
  color: var(--light);
  display: flex;
  justify-content: space-between;
  mix-blend-mode: difference;
  z-index: 2;
}

nav p {
  padding: 1rem;
  cursor: pointer;
}

.menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100svh;
  overflow: hidden;
  background-color: var(--dark);
  color: var(--light);
  z-index: 1;
  will-change: clip-path;
}

.menu-content {
  position: absolute;
  top: 45%;
  transform: translateY(-50%);
  width: 100%;
  padding: 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  will-change: transform, opacity;
}

.menu-col:nth-child(2) {
  text-align: right;
}

.menu-img {
  position: absolute;
  top: 45%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 150px;
  will-change: transform, opacity;
}

.menu-links-wrapper {
  position: absolute;
  left: 0;
  bottom: 0;
  width: max-content;
  padding: 2rem;
  display: flex;
  justify-content: space-between;
  gap: 2rem;
  will-change: transform;
}

.menu-link {
  position: relative;
  will-change: transform;
  overflow: hidden;
}

.menu-link a {
  position: relative;
  color: var(--light);
  font-family: "Anton";
  font-size: 10rem;
  font-weight: 500;
  letter-spacing: -0.2rem;
  display: inline-block;
  overflow: hidden;
}

.menu-link a span:nth-child(2) {
  position: absolute;
  top: 0;
  left: 0;
}

.link-highlighter {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 400px;
  height: 0.75rem;
  background-color: var(--accent);
  will-change: transform, width;
}

.menu-overlay {
  clip-path: polygon(0% 100%, 100% 100%, 100% 100%, 0% 100%);
}

.char {
  position: relative;
  display: inline-block;
  will-change: transform;
}

@media (max-width: 1000px) {
  .hero h1 {
    width: 100%;
    font-size: 4rem;
  }

  .menu-content {
    top: 25%;
  }

  .menu-img,
  .link-highlighter {
    display: none;
  }

  .menu-links-wrapper {
    flex-direction: column;
    gap: 0;
  }

  .menu-link a {
    font-size: 4rem;
    letter-spacing: -0.05rem;
  }
}

