  font-weight: 500;
}

.container {
  position: relative;
  transform: translateY(0svh);
  background-color: var(--bg);
  color: var(--fg);
}

section {
  position: relative;
  width: 100vw;
  height: 100svh;
  padding: 2rem;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  z-index: -1;
}

section h1 {
  width: 75%;
}

section img {
  opacity: 0.5;
}

nav {
  position: fixed;
  top: 0;
