document.addEventListener("DOMContentLoaded", function () {
  const menuOpen = document.querySelector(".menu-open");
  const menuClose = document.querySelector(".menu-close");

  let isOpen = false;
  const defaultEase = "power4.inOut";

  gsap.set(".menu-logo img", { y: 50 });
  gsap.set(".menu-link p", { y: 40 });
  gsap.set(".menu-sub-item p", { y: 12 });
  gsap.set(["#img-2, #img-3, #img-4"], { top: "150%" });

  const openMenu = () => {
    gsap.to(".menu", {
      clipPath: "polygon(0% 100%, 100% 100%, 100% 0%, 0% 0%)",
      pointerEvents: "all",
      duration: 1.25,
      ease: defaultEase,
    });

    gsap.to(".hero", {
      top: "-50%",
      opacity: 0,
      duration: 1.25,
      ease: defaultEase,
    });

    gsap.to(".menu-logo img", {
      y: 0,
      duration: 1,
      delay: 0.75,
      ease: "power3.out",
    });

    gsap.to(".menu-link p", {
      y: 0,
      duration: 1,
      stagger: 0.075,
      delay: 1,
      ease: "power3.out",
    });

    gsap.to(".menu-sub-item p", {
      y: 0,
      duration: 0.75,
      stagger: 0.05,
      delay: 1,
      ease: "power3.out",
    });

    gsap.to(["#img-2, #img-3, #img-4"], {
      top: "50%",
      duration: 1.25,
      ease: defaultEase,
      stagger: 0.1,
      delay: 0.25,
      onComplete: () => {
        gsap.set(".hero", { top: "50%" });
        isOpen = !isOpen;
      },
    });
  };

  const closeMenu = () => {
    gsap.to(".menu", {
      clipPath: "polygon(0% 0%, 100% 0%, 100% 0%, 0% 0%)",
      pointerEvents: "none",
      duration: 1.25,
      ease: defaultEase,
    });

    gsap.to(".menu-items", {
      top: "-300px",
      opacity: 0,
      duration: 1.25,
      ease: defaultEase,
    });

    gsap.to(".hero", {
      top: "0%",
      opacity: 1,
      duration: 1.25,
      ease: defaultEase,
      onComplete: () => {
        gsap.set(".menu", {
          clipPath: "polygon(0% 100%, 100% 100%, 100% 100%, 0% 100%)",
        });
        gsap.set(".menu-logo img", { y: 50 });
        gsap.set(".menu-link p", { y: 40 });
        gsap.set(".menu-sub-item p", { y: 12 });
        gsap.set(".menu-items", { opacity: 1, top: "0px" });
        gsap.set(["#img-2, #img-3, #img-4"], { top: "150%" });

        isOpen = !isOpen;
      },
    });
  };

  menuOpen.addEventListener("click", function () {
    if (isOpen) return;
    openMenu();
  });

  menuClose.addEventListener("click", function () {
    if (!isOpen) return;
    closeMenu();
  });
});

code.js/tilt.js:
document.addEventListener("DOMContentLoaded", function () {
  const menuImgContainer = document.querySelector(".menu-img");
  const images = document.querySelectorAll(".menu-img img");
  let mouse = { x: 0, y: 0 };
  let cx = window.innerWidth / 2;
  let cy = window.innerHeight / 2;

  const scales = [0.81, 0.84, 0.87, 0.9];

  function update() {
    let dx = mouse.x - cx;
    let dy = mouse.y - cy;

    let tiltx = (dy / cy) * 20;
    let tilty = (dx / cx) * 20;

    gsap.to(menuImgContainer, {
      duration: 2,
      transform: `rotate3d(${tiltx}, ${tilty}, 0, 15deg)`,
      ease: "power3.out",
    });

    images.forEach((img, index) => {
      let parallaxX = -(dx * (index + 1)) / 100;
      let parallaxY = -(dy * (index + 1)) / 100;

      let transformStyles = `translate(calc(-50% + ${parallaxX}px), calc(-50% + ${parallaxY}px)) scale(${scales[index]})`;
      gsap.to(img, {
        duration: 2,
        transform: transformStyles,
        ease: "power3.out",
      });
    });
  }

  document.body.addEventListener("mousemove", function (event) {
    mouse.x = event.clientX;
    mouse.y = event.clientY;
    update();
  });

  window.addEventListener("resize", function () {
    cx = window.innerWidth / 2;
    cy = window.innerHeight / 2;
  });
});

code.script.js:
document.addEventListener("DOMContentLoaded", function () {
    let tl = gsap.timeline({ paused: true });

    tl.to(".menu-overlay", {
      duration: 1,
      clipPath: "polygon(0 0, 100% 0, 100% 100%, 0 100%)",
      ease: "power2.out",
    });

    tl.from(
      ".menu-link, .btn",
      {
        opacity: 0,
        y: 60,
        stagger: 0.05,
        duration: 0.75,
        ease: "power1.inOut",
      },
      "<",
    );

    tl.to(
      ".video-preview",
      {
        duration: 1,
        height: "200px",
        ease: "power2.out",
      },
      "<",
    );

    tl.to(
      ".menu-divider",
      {
        duration: 2,
        width: "100%",
        ease: "power4.out",
      },
      "<",
    );

    function openMenu() {
      document.querySelector(".menu-overlay").style.pointerEvents = "all";
      tl.play();
    }

    function closeMenu() {
      document.querySelector(".menu-overlay").style.pointerEvents = "none";
      tl.reverse();
    }

    document.querySelector(".menu-open-btn").addEventListener("click", openMenu);
    document
      .querySelector(".menu-close-btn")
      .addEventListener("click", closeMenu);
    tl.reverse();
  });

code.styles.css:
:root {
    --color-menu-overlay: #a0e806;
    --color-menu-text: #000;
    --color-divider: #000000;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html, body{
    width: 100%;
    height: 100%;
    background: url(./assets/hero.jpg) no-repeat 50% 50%;
    background-size: cover;
    font-family: "Neue Montreal";
    overflow-x: hidden;
}

a {
    text-decoration: none;
    color: var(--color-menu-text);
}

i {
    position: relative;
    top: 1px;
}

.container {
    position: relative;
    width: 100%;
    height: 100%;
}

nav {
    position: fixed;
    top: 0;
    width: 100%;
    display: flex;
    justify-content: space-between;
    padding: 2em;
    color: #fff;
    z-index: 0;
}

.website-content {
    position: absolute;
    top: 0;
    width: 100%;
    margin-top: 10em;
    padding: 6em 2em;
    color: #fff;
    background: rgba(0, 0, 0, 0.5);
    z-index: 0;
}

.website-content h1 {
    font-size: 80px;
    font-weight: 500;
    margin-bottom: 0.25em;
}

.website-content p {
    font-size: 24px;
    font-weight: 400;
}

.menu-open-btn {
    cursor: pointer;
}

h1.header {
    position: absolute;
    bottom: 0;
    margin: 0.25em;
    font-weight: 400;
    color: #fff;
    font-size: 140px;
    line-height: 1;
}

.menu-overlay {
    position: fixed;
    top: 0;
    width: 100%;
    padding: 2em;
    background: var(--color-menu-overlay);
    pointer-events: none;
    clip-path: polygon(0 0, 100% 0, 100% 0, 0 0);
    z-index: 1000 !important;
}

.menu-close-btn {
    cursor: pointer;
}

.menu-nav {
   display: flex;
   justify-content: space-between;
   margin-bottom: 1em;
   color: var(--color-menu-text);
}

.menu-cols {
    display: flex;
}

.menu-cols > div {
    flex: 1;
    padding: 1em 0;
}

.video {
    width: 50%;
}

.video-preview {
    width: 100%;
    height: 0px;
    background: url(./assets/showreel-thumbnail.jpg) no-repeat 50% 50%;
    background-size: cover;
    border-radius: 4px;
}

.video-details {
    width: 100%;
    display: flex;
    justify-content: space-between;
    padding: 0.5em 0;
    color: var(--color-menu-text);
}

.menu-link {
    position: relative;
    width: max-content;
}

.menu-link a {
    font-size: 40px;
}

.menu-link:after {
    content: "";
    position: absolute;
    top: 100%;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--color-menu-text);
    transition: 0.3s all;
}

.menu-link:hover.menu-link:after {
    width: 100%;
}

.btn {
    position: relative;
    margin: 2em 0;
    border: 1px solid var(--color-menu-text);
    width: max-content;
    padding: 1.25em 2.5em;
    border-radius: 4px;
    overflow: hidden;
    cursor: pointer;
}

.btn:before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 0;
    height: 100%;
    background: var(--color-menu-text);
    transition: 0.3s all;
    z-index: -1;
}

.btn:hover a {
    color: var(--color-menu-overlay);
}

.btn:hover.btn:before {
    width: 100%;
}

.menu-footer {
    display: flex;
    flex-direction: column;
    color: var(--color-menu-text);
}

.menu-divider {
    width: 0%;
    height: 1px;
    background: var(--color-divider);
    margin: 1em 0;
}

.menu-footer-copy {
    display: flex;
    justify-content: space-between;
}

.socials {
    display: flex;
    gap: 1em;
}

@media(max-width: 900px) {
    .menu-overlay {
        height: 100vh;
    }

    .menu-cols {
        display: block;
    }

    .video-preview {
        height: 125px;
    }

    .menu-link a {
        font-size: 30px;
    }

    h1.header {
        font-size: 40px;
    }
}

