code.styles.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  width: 100%;
  height: 100vh;
  background: url("./hero.jpg") no-repeat 50% 50%;
  background-size: cover;
  color: #fff;
  font-family: "Neue Montreal", Helvetica, sans-serif;
}

nav {
  position: fixed;
  width: 100%;
}

.primary-nav,
.bottom-nav {
  width: 35%;
  margin: 0 auto;
}

/* primary-nav */

.primary-nav {
  background: #c20c15;
  height: 60px;
  padding: 0.8em 1.4em;
}

.primary-nav-container {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.primary-nav-container .nav-items {
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.primary-nav-container .nav-items .nav-item {
  flex: 1;
  display: flex;
  justify-content: center;
}

.primary-nav-container .nav-items .nav-item:first-child > span {
  margin-right: auto;
}

.primary-nav-container .nav-items .nav-item:last-child > span {
  margin-left: auto;
}

.logo {
  font-family: "Voyage", sans-serif;
  font-size: 24px;
  font-weight: bolder;
}

/* bottom-nav */

.bottom-nav {
  position: fixed;
  top: 85px;
  left: 50%;
  transform: translate(-50%, -50%);
  border: 1px solid rgba(255, 255, 255, 0.2);
  display: flex;
  z-index: -1;
}

.bottom-nav .bottom-nav-item {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-basis: 100%;
  font-size: 14px;
  background: #000;
}

.bottom-nav .bottom-nav-item:first-child {
  border-right: 1px solid rgba(255, 255, 255, 0.2);
}

.bottom-nav .bottom-nav-item:last-child {
  border-left: 1px solid rgba(255, 255, 255, 0.2);
}

.bottom-nav .bottom-nav-item-wrapper {
  padding: 0.6em;
}

.bottom-nav .bottom-nav-item-wrapper ion-icon {
  position: relative;
  top: 2px;
  left: 4px;
}

/* nav wrapper */

.nav-links .nav-info {
  width: max-content;
  margin: 20px auto 40px auto;
  padding: 0.2em 0.8em;
  font-size: 14px;
  font-weight: 400;
  text-align: center;
  border: 1px solid rgba(255, 255, 255, 0.4);
  border-radius: 30px;
}

.nav-links-wrapper .nav-link {
  width: 90%;
  margin: 0px auto;
  padding: 4px;
}

.nav-links-wrapper .nav-link a sup {
  position: relative;
  top: -5px;
  font-size: 10px;
}

.nav-links-wrapper .nav-link a {
  text-decoration: none;
  color: #fff;
  font-weight: 400;
  font-size: 28px;
}

.nav-links-wrapper .nav-link span ion-icon {
  position: relative;
  top: 4px;
  left: 5px;
}

.nav-links .nav-socials {
  display: flex;
  justify-content: space-between;
  width: 50%;
  margin: 20px auto 40px auto;
}

.nav-links .nav-socials a {
  text-align: none;
  color: #fff;
  font-size: 20px;
}

/* nav toggle stuff */

#menu-toggle-btn {
  margin-top: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 20px;
  width: 20px;
  position: relative;
  cursor: pointer;
  transition: all 0.3s;
  z-index: 10000;
}

#menu-toggle-btn span {
  height: 2px;
  background: #fff;
  width: 24px;
  display: inline-block;
  position: absolute;
  top: 50%;
  transition: all 0.3s;
}

span::before {
  content: " ";
  position: absolute;
  display: inline-block;
  height: 2px;
  background: #fff;
  transform: translateY(-4px);
  width: 24px;
  transition: all 0.3s;
}

span::before {
  top: -2px;
}

.active span {
  transform: rotate(45deg);
  background: #fff;
}

.active span::before {
  top: unset;
  transform: rotate(-90deg);
  background: #fff;
}

.active:hover span::before {
  top: unset;
}

/* okay already almost responsive but */

@media (max-width: 1080px) {
  .primary-nav,
  .bottom-nav {
    width: 100%;
  }
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  width: 100%;
  height: 100%;
  font-family: "PP Neue Montreal";
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

p {
  text-transform: uppercase;
  font-size: 12px;
  font-weight: 500;
  line-height: 100%;
}

.container {
  width: 100%;
  height: 100%;
  background: url(./assets/hero.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.menu-open {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1;
  color: #eee5d2;
}

.menu-close {
  position: fixed;
  top: 0;
  right: 0;
  z-index: 10;
  color: #a39b89;
}

.menu-open,
.menu-close {
  margin: 0.75em;
  padding: 0.75em;
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
  cursor: pointer;
}

.menu-open p,
.menu-close p {
  position: relative;
}

.menu {
  position: fixed;
  top: 0;
  right: 0;
  width: 20vw;
  height: 100vh;
  display: flex;
  z-index: 2;
}

.menu-item {
  position: relative;
  flex: 1;
  height: 100%;
  display: flex;
  justify-content: center;
  background: #eee5d2;
  border-left: 1.5px solid rgba(163, 155, 137, 0.35);
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
  margin-left: -1px;
}

.menu-item-index {
  height: 100%;
  padding: 1.5em 0.5em 2em 0.5em;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  color: #a39b89;
}

.menu-item-index p:nth-child(2) {
  position: relative;
  transform: rotate(-90deg);
}

.menu-link {
  position: absolute;
  bottom: 2.5%;
  left: 55%;
  transform: translate(-50%, -50%) rotate(-90deg);
}

.menu-item:nth-child(3) .menu-link {
  bottom: 7.5%;
}

.menu-link p {
  position: relative;
  display: flex;
  font-family: "OPTICaslon-ExtraCondensed";
  font-size: 200px;
  font-weight: lighter;
  transition: all 0.5s;
}

.menu-link p span {
  position: relative;
}

.menu-item .menu-link-main,
.menu-item .menu-link-hover {
  transition: all 0.5s;
}

.menu-link-main p {
  color: #2c221d;
}

.menu-item .menu-link-hover {
  left: 150%;
  color: rgba(163, 155, 137, 0.85);
}

.menu-item:hover .menu-link-main {
  left: -100%;
}
.menu-item:hover .menu-link-hover {
  left: 50%;
}

.menu-img {
  position: absolute;
  width: 75%;
  height: 35%;
  top: 25%;
  left: 50%;
  transform: translate(-50%, -50%);
  clip-path: polygon(50% 50%, 50% 50%, 50% 50%, 50% 50%);
  transition: 0.5s all cubic-bezier(0.165, 0.84, 0.44, 1);
}

.menu-item.menu-opened:hover .menu-img {
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

@media (max-width: 900px) {
  .menu {
    flex-direction: column;
  }

  .menu-link {
    bottom: unset;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(0deg);
  }

  .menu-item:nth-child(3) .menu-link {
    bottom: unset;
  }

  .menu-link p {
    font-size: 80px;
  }

  .menu-link-hover {
    display: none;
  }

  .menu-item:hover .menu-link-main {
    top: 50%;
    left: 50%;
  }

  .menu-item-index {
    align-items: center;
  }

  .menu-item-index p:nth-child(1) {
    padding: 0 0.5em;
  }

  .menu-img {
    display: none;
  }
}

code.css/styles.css:
:root {
  --bg: #e5e3dc;
  --link-color: #e2e2dc;
  --overlay-bg: #0a0a0a;
}

* {
  margin: 0;
  padding: 0;
}

html,
body {
  width: 100vw;
  height: 100vh;
  background: var(--bg);
  font-family: "Roslindale Display Condensed";
  overflow: hidden;
}

h1.hero-title {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  text-transform: uppercase;
  text-align: center;
  font-size: 10vw;
  font-weight: 400;
  z-index: 0;
}

/* overlay  */
.overlay {
  position: absolute;
  width: 100vw;
  height: 100vh;
  top: 0;
  left: 0;
  z-index: 0;
}

svg path {
  fill: var(--overlay-bg);
}

/* toggle btn */
#toggle-btn {
  position: absolute;
  top: 0;
  right: 0;
  width: 100px;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2em;
  z-index: 2;
  cursor: pointer;
}

.btn-outline {
  position: absolute;
  width: 100px;
  height: 100px;
  border: 1px solid var(--overlay-bg);
}

.btn-outline-1 {
  border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
  animation: morph 4s linear infinite;
}

.btn-outline-2 {
  border-radius: 53% 47% 43% 57% / 51% 39% 61% 49%;
}

@keyframes morph {
  0% {
    border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
  }

  50% {
    border-radius: 30% 60% 70% 40% / 50% 60% 30% 60%;
  }

  100% {
    border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
  }
}

#hamburger {
  position: relative;
  height: 20px;
  width: 20px;
  z-index: 2;
}

#hamburger span {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  display: inline-block;
  width: 24px;
  height: 1.25px;
  background: var(--overlay-bg);
  transition: transform 0.25s;
}

#hamburger span::before {
  position: absolute;
  right: 0;
  display: inline-block;
  content: " ";
  width: 18px;
  height: 1.25px;
  background: var(--overlay-bg);
  transform: translateY(-4px);
  transition: transform 0.25s;
}

#hamburger.active span {
  transform: rotate(45deg);
}

#hamburger.active span::before {
  top: unset;
  width: 24px;
  transform: rotate(-90deg);
}

/* menu items */
.menu {
  position: fixed;
  top: 0;
  width: 100vw;
  height: 100vh;
  display: flex;
}

.menu > div {
  height: 100%;
  display: flex;
}

/* common styles for <a> */
.menu a {
  position: relative;
  top: 120px;
  line-height: 70%;
  text-decoration: none;
  color: var(--link-color);
  transition: letter-spacing 0.75s cubic-bezier(0.075, 0.82, 0.165, 1);
}

.menu a span {
  font-size: 20px;
  margin-right: 2em;
  font-family: "Ayer";
}

.menu-item {
  position: relative;
}

.menu-item:after {
  content: "";
  position: absolute;
  top: 100px;
  left: -20px;
  width: 120%;
  height: 200%;
  background: var(--overlay-bg);
  margin: 0 auto;
}

.menu-container {
  width: 70%;
  height: 50%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

/* primary nav */
.primary-menu {
  flex: 3;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.primary-menu .menu-container .wrapper {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.primary-menu a {
  text-transform: uppercase;
  font-size: 125px;
  font-weight: 500;
}

.primary-menu .menu-container .wrapper .menu-item:nth-child(1) a,
.primary-menu .menu-container .wrapper .menu-item:nth-child(3) a {
  margin-left: 1em;
}

/* secondary nav */
.secondary-menu {
  flex: 2;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 8em;
}

.secondary-menu .menu-item a {
  font-family: "Ayer";
  font-size: 36px;
}

.secondary-menu .menu-container .wrapper:nth-child(1) {
  display: flex;
  flex-direction: column;
  gap: 30px;
}

