}

button {
  background: #fff;
  padding: 0.35em 0.75em 0.4em 0.75em;
  border: none;
  outline: none;
  clip-path: polygon(
    0 0,
    100% 0,
    100% 30%,
    100% 70%,
    90% 100%,
    30% 100%,
    0 100%,
    0% 30%
  );
  font-family: "PP Supply Sans";
  font-weight: 400;
  line-height: 1;
  cursor: pointer;
  font-size: 13.5px;
}

button:hover {
  background: #000;
  color: #fff;
}

.btn span {
  padding: 0.3em 0.4em;
  background: #fff;
  clip-path: polygon(
    0 0,
    100% 0,
    100% 30%,
    100% 70%,
    80% 100%,
    30% 100%,
    0 100%,
    0% 30%
  );
  font-size: 12px;
}

.logo-container {
  position: fixed;
  width: 320px;
  height: 125px;
  padding: 0 1em;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: #fff;
  border-radius: 0 0 20px 0;
  z-index: 2;
}

.logo {
  font-family: "Kroigs";
  font-size: 70px;
  font-weight: 600;
  line-height: 1;
  margin: 0;
}

.logo-bar {
  display: flex;
  width: 100%;
  font-family: "PP Supply Sans";
  font-weight: 400;
  justify-content: space-between;
}

.container {
  position: fixed;
  top: 0;
  left: 0;
  width: 45%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(20px);
  clip-path: polygon(0 0, 0 125px, 50% 125px, 50% 0);
}

.overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  color: #fff;
}

.whitespace {
  width: 100%;
  height: 135px;
}

.item {
  display: flex;
  border-top: 1px dashed #fff;
  padding: 1em;
  font-family: "PP Supply Sans";
  font-weight: 400;
}

.col-1 {
  flex: 2;
}

.col-2 {
  flex: 4;
}

.buy,
.connect,
.legal,
.newsletter {
  flex: 1;
}

.discover {
  flex: 10;
}

.link {
  position: relative;
  top: 40px;
  opacity: 0;
}

.discover .col-2 a {
  font-family: "The Neue";
  text-transform: uppercase;
  text-decoration: none;
  font-size: 42px;
}

.buy .col-2 {
  display: flex;
  width: 100%;
  gap: 2em;
}

@media (max-width: 900px) {
  .container {
    width: 100%;
    clip-path: polygon(0 0, 0 125px, 70% 125px, 70% 0);
  }
  .col-1 {
    flex: 1;
  }
  .col-2 .link a {
    font-size: 30px;
  }
}

