style.css: /* New attr() */ .menu { --anchor-id: attr(data-anchor type(<custom-ident>)); button { anchor-name: var(--anchor-id); } [popover] { position-anchor: var(--anchor-id); } } /* rest */ .card { border-radius: 2rem; overflow: hidden; position: relative; max-width: 250px; height: max-content; } img { width: 100%; scale: 1.05; } .card { &:focus, &:hover, &:focus-within { .overlay { opacity: 1; } } } .overlay { width: 100%; height: 100%; position: absolute; top: 0; left: 0; background: rgb(0 0 0 / 0.4); opacity: 0; transition: opacity 0.25s; } .menu { position: absolute; bottom: 1rem; right: 1rem; } /* import button */ :root { --btn-size: 3rem; --extra-space: 1.5rem; } /* Where the magic happens */ .item { --radius: calc(var(--btn-size) + var(--extra-space)); background-color: var(--bg); transform: translateX(calc(cos(var(--angle)) * var(--radius))) 		 translateY(calc(sin(var(--angle) * -1) * var(--radius))) rotate(0deg); opacity: 0; transition: all 0.3s var(--delay) ease; } /* Adding for popover base */ .menu-items:not(:popover-open) .item { --radius: 0; --angle: 0; rotate: 45deg; } /* rotate the "plus" */ .menu-toggle > div { transition: transform 0.3s; } .menu:has(:popover-open) .menu-toggle > div { transform: rotate(45deg); } .material-icons { display: flex; align-items: center; } .menu-items { justify-self: anchor-center; position-area: center; /* popover override */ background: none; } .hidden-close { transform: rotate(45deg); transition: opacity 0.1s; transition-delay: 1s; width: var(--btn-size); aspect-ratio: 1; } :popover-open .item { opacity: 1; } /* Every item gets a background, angle, and delay */ /* This gets updated when the popover is open */ .item:nth-child(1) { --bg: pink; --angle: 0deg; --delay: 0s; } .item:nth-child(2) { --bg: thistle; --angle: 45deg; --delay: 0.1s; } .item:nth-child(3) { --bg:	paleturquoise; --angle: 90deg; --delay: 0.2s; } .item:nth-child(4) { --bg: lightgreen; --angle: 135deg; --delay: 0.3s; } .item:nth-child(5) { --bg: peachpuff; --angle: 180deg; --delay: 0.4s; } /* Not related to demo, just styling */ .item { border-radius: 50%; width: var(--btn-size); aspect-ratio: 1; } .menu-toggle { border-radius: 50%; width: var(--btn-size); aspect-ratio: 1; background: #eee; z-index: 1; } /* Grid piles */ .menu, .menu-items, body, .item { display: grid; place-content: center; } .menu > *, .menu-items > *, body > * .item button { grid-area: 1/1; } /* Resets, etc. */ /* visually-hidden ala https://www.scottohara.me/blog/2017/04/14/inclusively-hidden.html */ .sr-only { clip: rect(0 0 0 0); clip-path: inset(50%); height: 1px; overflow: hidden; position: absolute; white-space: nowrap; width: 1px; } button { border: none; background: none; font-family: 'Noto Emoji'; color: #222; font-size: 1.25rem; } button:focus-visible { outline: 2px dashed deeppink; border-radius: 50%; aspect-ratio: 1/1; } .menu, .menu-items { overflow: unset; } /* etc */ body { height: calc(100dvh - 2rem); padding: 1rem; } .container { display: flex; gap: 2rem } @media (max-width: 600px) { .container { gap: 1rem; } }
