style.css: /* See https://brm.us/css-scroll-velocity for details */ @property --scroll-position { 	syntax: "<number>"; 	inherits: true; 	initial-value: 0; } @property --scroll-position-delayed { 	syntax: "<number>"; 	inherits: true; 	initial-value: 0; } @keyframes adjust-pos { 	to { 		--scroll-position: 1; 		--scroll-position-delayed: 1; 	} } :root { 	animation: adjust-pos 0.01s linear both; /* Duration added to cater for Firefox */ 	animation-timeline: scroll(root); } body { 	transition: --scroll-position-delayed 0.15s linear; 	--scroll-velocity: calc( 		var(--scroll-position) - var(--scroll-position-delayed) 	); } html { 	background: #666; } html { 	scroll-snap-type: y mandatory; } * { 	box-sizing: border-box; 	margin: 0; 	padding: 0; } ul { 	list-style: none; 	pointer-events: none; 	line-height: 1; 	display: block; } ul li { 	display: block; 	line-height: 1; } .panel { 	scroll-snap-align: center; 	height: 100dvh; 	border: 2px solid #333; 	background: rgb(0 0 0 / 0.15); 	z-index: -1; } .panel img { 	display: block; 	width: 100%; 	height: 100%; 	object-fit: cover; 	filter: brightness(30%); } @keyframes slide-up { 	to { 		translate: 0 var(--end-pos); 	} } .titles { 	position: fixed; 	z-index: 2; 	top: 50dvh; 	font-size: 25dvh; 	left: 0; 	right: 0; 	text-align: center; 	animation: slide-up 0.01s linear; /* Duration added to cater for Firefox */ 	animation-timeline: scroll(root block); 	font-family: impact; 	text-transform: uppercase; 	letter-spacing: 1px; 	/* Calculate the part each child can occupy. 	 For 8 children, that’s 12.5% 	*/ 	--num-children: 8; 	--num-children-1: calc(var(--num-children) - 1); 	--size-per-child: calc(100% / var(--num-children)); 	/* Because the text needs to be in the dead center, 	 we need to shift up the whole thing by half the size per child. 	 If not, it would start with the top edge of the whole box 	 aligned to the center. We want the text to be centered. 	 So in the case of 8 items that is from -6.25% to to -(100% - 6.25%). 	 This ra