style.css: @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700&display=swap'); * { 	margin: 0; 	padding: 0; 	box-sizing: border-box; } body, html { 	background-color: black; 	font-size: 62.5%; } section { 	position: relative; 	height: 100svh; 	width: 100%; 	background-color: var(--bg); 	overflow: hidden; 	&.section2 { 		height: min-content; 	} } .loop-images { 	display: flex; 	align-items: center; 	justify-content: center; 	flex-direction: column; } .carousel-track { 	--left: -300rem; 	min-width: calc(10rem * var(--total)); 	height: 30rem; } .carousel-track .carousel-item { 	position: absolute; 	width: 30rem; 	height: 30rem; 	left: 100%; 	display: flex; 	justify-content: center; 	perspective: 1000px; 	transform-style: preserve-3d; 	animation: scroll-left var(--time) linear infinite; 	animation-delay: calc(var(--time) / var(--total) * (var(--i) - 1) - var(--time)); 	will-change: left; 	transition: 0.5s ease-in-out; 	cursor: pointer; 	img { 		width: 100%; 		height: 100%; 		object-fit: cover; 		background-color: white; 		transform: rotateY(-45deg); 		transition: 0.5s ease-in-out; 		mask: linear-gradient(black 70%, transparent 100%); 	} } .carousel-track .carousel-item:hover img { 	transform: rotateY(0deg) translateY(-1rem); } /*.carousel-track:hover .carousel-item { animation-play-state: paused; }*/ @keyframes scroll-left { 	to { 		left: var(--left); 	} } .image-motion { 	width: 100%; 	height: 100%; 	border-radius: inherit; 	object-position: center; 	transform: rotatex(90deg); 	transform-origin: 50% 0; 	picture { 		display: block; 		width: 100%; 		height: 100%; 		img { 			width: 100%; 			height: 100%; 			object-fit: cover; 			background-color: white; 		} 	} } .scroll-down { 	position: absolute; 	bottom: 5rem; 	left: 0; 	right: 0; 	font-family: 'Poppins', sans-serif; 	text-align: center; 	font-size: 1.6rem; 	color: black; 	display: flex; 	flex-direction: column; 	align-items: center; 	text-decoration: none; } .section3 { 	--bg-color: #000000; 	--text-primary: #ccc; 	--text-secondary: #aaa; 	--text-white: #ffffff; 	--accent-primary: #ff6b6b; 	--accent-secondary: #ff8a80; 	--accent-tertiary: #ffab40; 	--accent-quaternary: #ff7043; 	--accent-quinary: #ff5722; 	--border-radius: 25px; 	--transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275); 	position: relative; 	display: flex; 	justify-content: center; 	align-items: center; 	color: var(--text-white); 	padding: 6rem 2rem; 	min-height: 100vh; 	background: var(--bg-color); 	overflow: hidden; 	.container { 		width: 100%; 		max-width: 1200px; 		margin: auto; 		text-align: center; 		position: relative; 		z-index: 2; 		.title { 			font-size: 5rem; 			font-family: 'Poppins', sans-serif; 			font-weight: 800; 			margin-bottom: 2.5rem; 			background: linear-gradient(135deg, var(--accent-primary) 0%, var(--accent-secondary) 25%, var(--accent-tertiary) 50%, var(--accent-quaternary) 75%, var(--accent-quinary) 100%); 			-webkit-background-clip: text; 			-webkit-text-fill-color: transparent; 			background-clip: text; 			line-height: 1.1; 			letter-spacing: -2px; 			position: relative; 			&::after { 				content: ''; 				position: absolute; 				bottom: -10px; 				left: 50%; 				transform: translateX(-50%); 				width: 100px; 				height: 4px; 				background: linear-gradient(90deg, var(--accent-primary), var(--accent-secondary)); 				border-radius: 2px; 			} 		} 		.subtitle { 			position: relative; 			width: fit-content; 			margin-inline: auto; 			font-size: 1.6rem; 			font-family: 'Poppins', sans-serif; 			font-weight: 300; 			color: var(--text-primary); 			margin-bottom: 2rem; 			letter-spacing: 3px; 			text-transform: uppercase; 			&::before { 				content: '◆'; 				position: absolute; 				left: -30px; 				top: 50%; 				transform: translateY(-50%); 				color: var(--accent-primary); 				font-size: 1.2rem; 			} 			&::after { 				content: '◆'; 				position: absolute; 				right: -30px; 				top: 50%; 				transform: translateY(-50%); 				color: var(--accent-primary); 