(--ease); &.active { color: var(--color-text-highlight); background-size: 100% 100%; } [data-highlight="background"] & { background-image: linear-gradient( var(--bg-color-highlight), var(--bg-color-highlight) ); } [data-highlight="half"] & { --line-size: 0.5em; background-image: linear-gradient( transparent calc(100% - var(--line-size)), var(--bg-color-highlight) var(--line-size) ); } [data-highlight="underline"] & { --line-size: 0.15em; background-image: linear-gradient( transparent calc(100% - var(--line-size)), var(--color-text) var(--line-size) ); } &::before, &::after { position: absolute; clip-path: inset(100%); clip: rect(1px, 1px, 1px, 1px); width: 1px; height: 1px; overflow: hidden; white-space: nowrap; user-select: none; } &::before { content: " [highlight start] "; } &::after { content: " [highlight end] "; } }
script.js: const highlight = document.getElementById("highlight-style"); const mode = document.getElementById("mode"); gsap.registerPlugin(ScrollTrigger); gsap.utils.toArray(".text-highlight").forEach((highlight) => { ScrollTrigger.create({ trigger: highlight, start: "-100px center", onEnter: () => highlight.classList.add("active") }); }); const setHighlightStyle = (value) => document.body.setAttribute("data-highlight", value); mode.addEventListener("click", (e) => document.body.classList.toggle("dark-mode") ); highlight.addEventListener("change", (e) => setHighlightStyle(e.target.value)); setHighlightStyle(highlight.value);
