* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  width: 100vw;
  height: 500vh;
  font-family: "Neue Montreal";
  font-size: 24px;
}

nav,
footer {
  position: fixed;
  width: 100%;
  padding: 1.5rem;
  color: #fff;
  mix-blend-mode: difference;
  font-size: 12px;
  display: flex;
  justify-content: space-between;
  z-index: 2;
  cursor: pointer;
  text-transform: uppercase;
}

footer {
  bottom: 0;
}

.slider {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: white;
  z-index: 1;
}

.slide {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

.slide:not(#slide-1) {
  transform-origin: center;
  z-index: 2;
}

.slide-img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: 0;
}

