Step 2: Add custom Javascript

gsap.registerPlugin(Draggable);

function initStackedCardsSlider() {
  document.querySelectorAll('[data-stacked-cards]').forEach(function(container) {

    // animation presets
    let easeBeforeRelease = { duration: 0.2, ease: 'Power2.easeOut' };
    let easeAfterRelease  = { duration: 1, ease: 'elastic.out(1,0.75)' };

    let activeDeg   = 4;
    let inactiveDeg = -4;

    const list = container.querySelector('[data-stacked-cards-list]');
    if(!list) return;

    // check minimum cards
    const initialItems = Array.from(list.querySelectorAll(':scope > [data-stacked-cards-item]'));
    if (initialItems.length < 3) {
      console.error('[StackedCards] Minimum of 3 cards required. Found:', initialItems.length, list);
      return;
    }

    // Draggable instances & cached elements
    let dragFirst, dragSecond;
    let firstItem, secondItem, firstEl, secondEl;
    let full, t;

    function restack() {
      const items = Array.from(list.querySelectorAll('[data-stacked-cards-item]'));
      items.forEach(function(item) {
        item.classList.remove('is--active', 'is--second');
      });
      items[0].style.zIndex = 3;
      items[0].style.transform = `rotate(${activeDeg}deg)`;
      items[0].style.pointerEvents = 'auto';
      items[0].classList.add('is--active');

      items[1].style.zIndex = 2;
      items[1].style.transform = `rotate(${inactiveDeg}deg)`;
      items[1].style.pointerEvents = 'none';
      items[1].classList.add('is--second');

      items[2].style.zIndex = 1;
      items[2].style.transform = `rotate(${activeDeg}deg)`;

      items.slice(3).forEach(function(item) {
        item.style.zIndex = 0;
        item.style.transform = `rotate(${inactiveDeg}deg)`;
      });
    }

    function setupDraggables() {
      restack();

      // cache top two cards
      const items = Array.from(list.querySelectorAll(':scope > [data-stacked-cards-item]'));
      firstItem   = items[0];
      secondItem  = items[1];
      firstEl     = firstItem.querySelector('[data-stacked-cards-card]');
      secondEl    = secondItem.querySelector('[data-stacked-cards-card]');

      // compute thresholds
      const width = firstEl.getBoundingClientRect().width;
      full = width * 1.15;
      t    = width * 0.1;

      // kill old Draggables
      dragFirst?.kill();
      dragSecond?.kill();

      // --- First card draggable ---
      dragFirst = Draggable.create(firstEl, {
        type: 'x',
        onPress() {
          firstEl.classList.add('is--dragging');
        },
        onRelease() {
          firstEl.classList.remove('is--dragging');
        },
        onDrag() {
          let raw = this.x;
          if (Math.abs(raw) > full) {
            const over = Math.abs(raw) - full;
            raw = (raw > 0 ? 1 : -1) * (full + over * 0.1);
          }
          gsap.set(firstEl, { x: raw, rotation: 0 });
        },
        onDragEnd() {
          const x   = this.x;
          const dir = x > 0 ? 'right' : 'left';

          // hand control to second card
          this.disable?.();
          dragSecond?.enable?.();
          firstItem.style.pointerEvents = 'none';
          secondItem.style.pointerEvents = 'auto';

          if (Math.abs(x) <= t) {
            // small drag: just snap back
            gsap.to(firstEl, {
              x: 0, rotation: 0,
              ...easeBeforeRelease,
              onComplete: resetCycle
            });
          }
          else if (Math.abs(x) <= full) {
            flick(dir, false, x);
          }
          else {
            flick(dir, true);
          }
        }
      })[0];

      // --- Second card draggable ---
      dragSecond = Draggable.create(secondEl, {
        type: 'x',
        onPress() {
          secondEl.classList.add('is--dragging');
        },
        onRelease() {
          secondEl.classList.remove('is--dragging');
        },
        onDrag() {
          let raw = this.x;
          if (Math.abs(raw) > full) {
            const over = Math.abs(raw) - full;
            raw = (raw > 0 ? 1 : -1) * (full + over * 0.2);
          }
          gsap.set(secondEl, { x: raw, rotation: 0 });
        },
        onDragEnd() {
          gsap.to(secondEl, {
            x: 0, rotation: 0,
            ...easeBeforeRelease
          });
        }
      })[0];

      // start with first card active
      dragFirst?.enable?.();
      dragSecond?.disable?.();
      firstItem.style.pointerEvents = 'auto';
      secondItem.style.pointerEvents = 'none';
    }

    function flick(dir, skipHome = false, releaseX = 0) {
      if (!(dir === 'left' || dir === 'right')) {
        dir = activeDeg > 0 ? 'right' : 'left';
      }
      dragFirst?.disable?.();

      const item = list.querySelector('[data-stacked-cards-item]');
      const card = item.querySelector('[data-stacked-cards-card]');
      const exitX = dir === 'right' ? full : -full;

      if (skipHome) {
        const visualX = gsap.getProperty(card, 'x');
        list.appendChild(item);
        [activeDeg, inactiveDeg] = [inactiveDeg, activeDeg];
        restack();
        gsap.fromTo(
          card,
          { x: visualX, rotation: 0 },
          { x: 0, rotation: 0, ...easeAfterRelease, onComplete: resetCycle }
        );

      } else {
        gsap.fromTo(
          card,
          { x: releaseX, rotation: 0 },
          {
            x: exitX,
            ...easeBeforeRelease,
            onComplete() {
              gsap.set(card, { x: 0, rotation: 0 });
              list.appendChild(item);
              [activeDeg, inactiveDeg] = [inactiveDeg, activeDeg];
              resetCycle();
              const newCard = item.querySelector('[data-stacked-cards-card]');
              gsap.fromTo(
                newCard,
                { x: exitX },
                { x: 0, ...easeAfterRelease, onComplete: resetCycle }
              );
            }
          }
        );
      }
    }

    function resetCycle() {
      list.querySelectorAll('[data-stacked-cards-card].is--dragging').forEach(function(el) {
        el.classList.remove('is--dragging');
      });
      setupDraggables();
    }

    setupDraggables();

    // “Next” button support
    container.querySelectorAll('[data-stacked-cards-control="next"]').forEach(function(btn) {
      btn.onclick = function() { flick(); };
    });
  });
}

// Initialize Stacked Cards Slider
document.addEventListener("DOMContentLoaded", () => {
  initStackedCardsSlider();
});

Implementation

Container
Place the [data-stacked-cards] attribute on the outermost wrapper (the “slider container”). The script will look for every element with this attribute and initialize a separate slider instance for each.

List
Add the [data-stacked-cards-list] attribute to the element that contains all card items. The script treats its direct children as a stack, cycling them in and out.

Item
Each direct child of [data-stacked-cards-list] must have the [data-stacked-cards-item] attribute. This represents a single “slot” in the stack. The script will assign CSS classes (.is--active, .is--second) and set each item’s rotation/position based on its index.
Note: A minimum of 3 cards is required per stack.

Card 
Inside every card there must be an element with the [data-stacked-cards-card] attribute. This is the actual draggable card—its styling determines its appearance. The javascript attaches GSAP Draggable behavior directly to the first elements in the list.

Next (Shuffle) Button
Add the[data-stacked-cards-control="next"] attribute to a . Clicking it will  “flick” the top card out (as if you swiped it) and cycle the stack forward. You can include multiple “Next” buttons (e.g. for mobile + desktop); the script will bind them all.

Customization options

Rotation
Change the values below (in degrees) to adjust how much each card rotates.
let activeDeg   = 4;
let inactiveDeg = -4;
Easing
We tweaked these easing settings to match the playful style of the cards, but feel free to replace them with whatever easing curves or durations fit your design.
let easeBeforeRelease = { duration: 0.2, ease: 'Power2.easeOut' };
let easeAfterRelease  = { duration: 1,   ease: 'elastic.out(1,0.75)' };

code.script.js:
import * as THREE from "three";
import { vertexShader, fragmentShader } from "./shaders.js";
import { slides } from "./slides.js";

const container = document.querySelector(".container");
const projectTitle = document.getElementById("project-title");
const projectLink = document.getElementById("project-link");

let scrollIntensity = 0;
let targetScrollIntensity = 0;
const maxScrollIntensity = 1.0;
const scrollSmoothness = 0.5;

let scrollPosition = 0;
let targetScrollPosition = 0;
const scrollPositionSmoothness = 0.05;

let isMoving = false;
const movementThreshold = 0.001;
let isSnapping = false;

let stableCurrentIndex = 0;
let stableNextIndex = 1;
let isStable = false;

let titleHidden = false;
let titleAnimating = false;
let currentProjectIndex = 0;

projectTitle.textContent = slides[0].title;
projectLink.href = slides[0].url;

const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(
  75,
  window.innerWidth / window.innerHeight,
  0.1,
  1000
);
camera.position.z = 5;

const renderer = new THREE.WebGLRenderer({ antialias: true });
renderer.setSize(window.innerWidth, window.innerHeight);
renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
renderer.setClearColor(0xffffff, 0);
container.appendChild(renderer.domElement);

const calculatePlaneDimensions = () => {
  const fov = camera.fov * (Math.PI / 180);
  const viewportHeight = 2 * Math.tan(fov / 2) * camera.position.z;
  const viewportWidth = viewportHeight * camera.aspect;

  const widthFactor = window.innerWidth < 900 ? 0.9 : 0.5;
  const planeWidth = viewportWidth * widthFactor;
  const planeHeight = planeWidth * (9 / 16);

  return { width: planeWidth, height: planeHeight };
};

const dimensions = calculatePlaneDimensions();

const loadTextures = () => {
  const textureLoader = new THREE.TextureLoader();

  return slides.map((slide) => {
    const texture = textureLoader.load(
      slide.image,
      undefined,
      undefined,
      () => {
        console.log(`Using fallback for ${slide.image}`);
      }
    );
    texture.minFilter = THREE.LinearFilter;
    texture.magFilter = THREE.LinearFilter;
    return texture;
  });
};

const textures = loadTextures();

function preloadAllTextures() {
  textures.forEach((texture) => {
    texture.needsUpdate = true;
  });
}

preloadAllTextures();

const geometry = new THREE.PlaneGeometry(
  dimensions.width,
  dimensions.height,
  32,
  32
);

const material = new THREE.ShaderMaterial({
  vertexShader,
  fragmentShader,
  side: THREE.DoubleSide,
  uniforms: {
    uScrollIntensity: { value: scrollIntensity },
    uScrollPosition: { value: scrollPosition },
    uCurrentTexture: { value: textures[0] },
    uNextTexture: { value: textures[1] },
  },
});

const plane = new THREE.Mesh(geometry, material);
scene.add(plane);

function determineTextureIndices(position) {
  const totalImages = slides.length;

  const baseIndex = Math.floor(position % totalImages);
  const positiveBaseIndex =
    baseIndex >= 0 ? baseIndex : (totalImages + baseIndex) % totalImages;

  const nextIndex = (positiveBaseIndex + 1) % totalImages;

  let normalizedPosition = position % 1;
  if (normalizedPosition < 0) normalizedPosition += 1;

  return {
    currentIndex: positiveBaseIndex,
    nextIndex: nextIndex,
    normalizedPosition: normalizedPosition,
  };
}

function updateTextureIndices() {
  if (isStable) {
    material.uniforms.uCurrentTexture.value = textures[stableCurrentIndex];
    material.uniforms.uNextTexture.value = textures[stableNextIndex];
    return;
  }

  const indices = determineTextureIndices(scrollPosition);

  material.uniforms.uCurrentTexture.value = textures[indices.currentIndex];
  material.uniforms.uNextTexture.value = textures[indices.nextIndex];
}

function snapToNearestImage() {
  if (!isSnapping) {
    isSnapping = true;
    const roundedPosition = Math.round(scrollPosition);
    targetScrollPosition = roundedPosition;

    const indices = determineTextureIndices(roundedPosition);
    stableCurrentIndex = indices.currentIndex;
    stableNextIndex = indices.nextIndex;

    currentProjectIndex = indices.currentIndex;

    showTitle();
  }
}

function hideTitle() {
  if (!titleHidden && !titleAnimating) {
    titleAnimating = true;
    projectTitle.style.transform = "translateY(20px)";

    setTimeout(() => {
      titleAnimating = false;
      titleHidden = true;
    }, 500);
  }
}

function showTitle() {
  if (titleHidden && !titleAnimating) {
    projectTitle.textContent = slides[currentProjectIndex].title;
    projectLink.href = slides[currentProjectIndex].url;

    titleAnimating = true;
    projectTitle.style.transform = "translateY(0px)";

    setTimeout(() => {
      titleAnimating = false;
      titleHidden = false;
    }, 500);
  }
}

window.addEventListener("resize", () => {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();

  renderer.setSize(window.innerWidth, window.innerHeight);
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));

  const newDimensions = calculatePlaneDimensions();
  plane.geometry.dispose();
  plane.geometry = new THREE.PlaneGeometry(
    newDimensions.width,
    newDimensions.height,
    32,
    32
  );
});

window.addEventListener(
  "wheel",
  (event) => {
    event.preventDefault();

    isSnapping = false;
    isStable = false;

    hideTitle();

    targetScrollIntensity += event.deltaY * 0.001;
    targetScrollIntensity = Math.max(
      -maxScrollIntensity,
      Math.min(maxScrollIntensity, targetScrollIntensity)
    );

    targetScrollPosition += event.deltaY * 0.001;

    isMoving = true;
  },
  { passive: false }
);

function animate() {
  requestAnimationFrame(animate);

  scrollIntensity +=
    (targetScrollIntensity - scrollIntensity) * scrollSmoothness;
  material.uniforms.uScrollIntensity.value = scrollIntensity;

  scrollPosition +=
    (targetScrollPosition - scrollPosition) * scrollPositionSmoothness;

  let normalizedPosition = scrollPosition % 1;
  if (normalizedPosition < 0) normalizedPosition += 1;

  if (isStable) {
    material.uniforms.uScrollPosition.value = 0;
  } else {
    material.uniforms.uScrollPosition.value = normalizedPosition;
  }

  updateTextureIndices();

  const baseScale = 1.0;
  const scaleIntensity = 0.1;

  if (scrollIntensity > 0) {
    const scale = baseScale + scrollIntensity * scaleIntensity;
    plane.scale.set(scale, scale, 1);
  } else {
    const scale = baseScale - Math.abs(scrollIntensity) * scaleIntensity;
    plane.scale.set(scale, scale, 1);
  }

  targetScrollIntensity *= 0.98;

  const scrollDelta = Math.abs(targetScrollPosition - scrollPosition);

  if (scrollDelta < movementThreshold) {
    if (isMoving && !isSnapping) {
      snapToNearestImage();
    }

    if (scrollDelta < 0.0001) {
      if (!isStable) {
        isStable = true;
        scrollPosition = Math.round(scrollPosition);
        targetScrollPosition = scrollPosition;
      }

      isMoving = false;
      isSnapping = false;
    }
  }

  renderer.render(scene, camera);
}

animate();

code.shaders.js:
export const vertexShader = `
  uniform float uScrollIntensity;
  varying vec2 vUv;

  void main() {
    vUv = uv;
    vec3 pos = position;

    float sideDistortion = sin(uv.y * 3.14159) * uScrollIntensity * 0.5;
    float topBottomDistortion = sin(uv.x * 3.14159) * uScrollIntensity * 0.2;
    pos.z += sideDistortion + topBottomDistortion;

    gl_Position = projectionMatrix * modelViewMatrix * vec4(pos, 1.0);
  }
`;

export const fragmentShader = `
  uniform sampler2D uCurrentTexture;
  uniform sampler2D uNextTexture;
  uniform float uScrollPosition;
  varying vec2 vUv;

  void main() {
    float normalizedPosition = fract(uScrollPosition);

    vec2 currentUv = vec2(vUv.x, mod(vUv.y - normalizedPosition, 1.0));
    vec2 nextUv = vec2(vUv.x, mod(vUv.y + 1.0 - normalizedPosition, 1.0));

    if (vUv.y < normalizedPosition) {
      gl_FragColor = texture2D(uNextTexture, nextUv);
    } else {
      gl_FragColor = texture2D(uCurrentTexture, currentUv);
    }
  }
`;

