osition: relative; max-width:100%; } .search-input { border: none; border-radius: 20px; height: 40px; width: 40px; font-size: 0px; background: #ffffff; padding:0; max-width: 100%; text-indent: 15px; } .search-input:focus { outline:none; } ::placeholder { color:rgba(100, 98, 100, 0.85); font-family:'Antonio'; transform: translate3d(0,2px,0px); } ::-webkit-input-placeholder { transform: translate3d(0,2px,0px); } .search-btn { position:absolute; top: 0px; right:0px; margin:0; } .menu-btn:before,.menu-btn:after { position:absolute; content:""; height:2px; background:black; top:calc(50% - 3.5px); left:calc(50% - 10px); width:20px; } .menu-btn:after { top:calc(50% + 2px); } .search-btn:before,.search-btn:after { position:absolute; content:""; background:black; } .search-btn:before { width:10px; height:10px; border:2px solid black; background:white; z-index:1; border-radius:50%; top:12px; right:14px; } .search-btn:after { height:2px; width:15px; transform:rotate(45deg); top:22px; right:11px; z-index:0; } .align-left,.align-right { display: flex; align-items: center; width:30%; } .align-right { justify-content:flex-end; } /* SLIDER */ .slide-title { display:inline-block; font-size:160px; margin: 0px; letter-spacing:-2px; color:transparent; -webkit-text-stroke-width: 2px; -webkit-text-stroke-color: white; position:relative; overflow:hidden; } .slide-title:before { position: absolute; content: attr(data-text); top: 0; left: 0; width: 100%; height: 100%; color: white; clip-path: inset(80% 0 0 0); transition: 0.55s clip-path cubic-bezier(0.45, 0.05, 0.55, 0.95); } .slide-title.active:before { clip-path: inset(10% 0 0 0); transition: 0.425s clip-path cubic-bezier(0.45, 0.05, 0.55, 0.95) 0.425s; } .slider-controls { width: 40%; text-align:center; position: absolute; transform: translate(-50%); left: 50%; } .slider-next-btn, .slider-prev-btn { width: 40px; height: 40px; background:rgba(255,255,255,0.35); background:rgba(100, 98, 100, 0.85); border: none; margin: 0 25px; position: relative; padding: 0; } .slider-prev-btn { margin-left:0px; } .slider-next-btn { margin-right: 0px; } .slider-next-btn:before,.slider-prev-btn:before, .slider-next-btn:after,.slider-prev-btn:after { position:absolute; content:""; height:2px; background:white; width:12px; border-radius:2px; left:calc(50% - 5px); } .slider-next-btn:before { transform:rotate(45deg); top:calc(50% - 4px); } .slider-next-btn:after{ transform:rotate(-45deg); top:calc(50% + 3px); } .slider-prev-btn:before { transform:rotate(45deg); top:calc(50% + 3px); left:calc(50% - 7px); } .slider-prev-btn:after { transform:rotate(135deg); top:calc(50% - 4px); left:calc(50% - 7px); } .slide-progress-bar { height:2px; width:150px; background:rgba(255,255,255,0.5); display:inline-block; position:relative; overflow:hidden; } .slide-progress-bar:after { content:""; position:absolute; top:0; left:0; height:100%; width:100%; background:white; transform:translateX(-100%); } .slide-indicator { font-size:24px; margin-left:15px; color:rgba(255,255,255,0.5); } .slide-indicator-container { height: 24px; overflow: hidden; line-height: 24px; } .slide-indicator:nth-child(1) { margin: 0px 0px 0px 0px; margin-right: 15px; color: white; display: block; } .slide-indicator>li { display: block; margin: 0; } .bottom-bar { position: absolute; bottom: 0; left: 0; right: 0; display: flex; flex-direction: row; justify-content: space-between; align-items: center; } .align-left.bot { padding-left:35px; } .btn-container { display: flex; align-items: center; background: white; color: black; padding: 15px 35px; } .route-text { margin-right:100px; font-weight:lighter; letter-spacing:-1.5px; } .route-btn { width:40px; height:40px; background:white; border:1px solid lightgrey; position:relative; } .route-btn:before { position:absolute; content:""; width:100px; height:2px; background:black; top:50%; right:28px; } .route-btn:after { position: absolute; content: ""; top: calc(50% - 4px); border-style: solid; border-color: transparent transparent transparent black; border-width: 5px 5px 5px 6px; left: calc(50% - 2px); } .slide { width: 100%; height: 100%; display: flex; flex-direction: column; align-items: center; justify-content: center; position: absolute; top: 0; left: 0; } .slide:first-child { transform:translateX(0%) } .slide:nth-child(2) { transform: translateX(100%); } .slide:nth-child(3) { transform: translateX(200%); } .slide:nth-child(4) { transform: translateX(300%); } .slide-description-list { position: absolute; overflow-y: hidden; height: 23px; line-height: 23px; left: 50%; top: 60%; transform: translate(-50%,-50%); text-align:center; } .slide-description { position:relative; display:block; margin:0; padding-left:20px; font-size:16px; } .slide-description:before { position:absolute; content:""; top:1px; left:-25px; width:12px; height:12px; border-radius:50% 50% 50% 0; border:2px solid white; transform:rotate(-45deg); } .slide-description:after { position:absolute; content:""; top:6px; left:-20px; width:2px; height:2px; border:2px solid white; border-radius:50%; } .slide-description:before { left: 13px; } .slide-description:after { left: 18px; } li.slide-description:nth-child(3):before { left: 0px; } li.slide-description:nth-child(3):after { left: 5px; } li.slide-description:nth-child(4):before { left: 42px; } li.slide-description:nth-child(4):after { left: 47px; } .slider { height: 100vh; text-align: center; position: relative; z-index: -1; will-change:transform; } .slider-inner { height: 100%; width: 100%; position: relative; } .slider img { position:absolute; top:0; left:0; z-index:-10; opacity:0; } /* MEDIA QUERIES */ @media screen and (max-width:1600px) { .slide-title { font-size:120px; } .slide-description-list { margin: 0; } } @media screen and (max-width:1190px) { .route-text { margin-right:15px; } .route-btn:before { display:none; } .slide-title { font-size:100px; } .slide-description-list { top:58%; } } @media screen and (max-width:990px) { .slide-title { font-size:80px; } .slide-description-list { top:57%; } } @media screen and (max-width:800px) { .nav-links,.social-links { display:none; } .slide-title { font-size:60px; } .route-text { font-size: 20px; } } @media screen and (max-width:768px) { .route-text { font-size: 18px; } .slider-prev-btn { margin-right:15px; } .slider-next-btn { margin-left:15px; } .slide-description-list { top:56%; } } @media screen and (max-width:560px) { .btn-container { padding: 5px 20px; } .route-text { font-size: 16px; } .slide-progress-bar { width: 50px; } .slide-title { font-size:40px; letter-spacing:0px; -webkit-text-stroke-width:1px; } } @media screen and (max-width:420px) { .slide-progress-bar { display:none; } .slide-indicator { display:none; } .route-text { display:none; } .slide-description-list { top:55%; } .slide-description { font-size:14px; } .search-container { max-width:none; } li.slide-description:nth-child(4):before { left: 37px; } li.slide-description:nth-child(4):after { left: 42px; } } @media screen and (max-width:360px) { .slide-description-list { top:55%; } .slide-description { display:none; } } /* media query for cp iframe preview */ @media screen and (max-height:456px) { .slide-description-list { top:62% } }
script.js: const sliderContainer = document.querySelector('.slider'); var vertex = ` uniform mat4 projectionMatrix; uniform mat4 viewMatrix; uniform mat4 modelMatrix; attribute vec3 position; attribute vec2 uv; varying vec2 vUv; void main() { vUv = uv; gl_Position = projectionMatrix * viewMatrix * modelMatrix * vec4( position, 1.0 ); } `; var fragment = ` precision mediump float; varying vec2 vUv; uniform sampler2D currentImage; uniform sampler2D nextImage; uniform sampler2D disp; uniform float dispFactor; float intensity = 0.25; void main() { vec2 uv = vUv; vec4 disp = texture2D(disp, uv); vec2 distortedPosition = vec2(uv.x + dispFactor * (disp.r*intensity), uv.y); vec2 distortedPosition2 = vec2(uv.x - (1.0 - dispFactor) * (disp.r*intensity), uv.y); vec4 _currentImage = texture2D(currentImage, distortedPosition); vec4 _nextImage = texture2D(nextImage, distortedPosition2); vec4 finalTexture = mix(_currentImage, _nextImage, dispFactor); gl_FragColor = finalTexture; } `; // Scene let scene = new THREE.Scene(); const imgs = Array.from(document.querySelectorAll('.slide-img')); const sliderImages=[] let renderWidth = Math.max(document.documentElement.clientWidth, window.innerWidth || 0); let renderHeight = Math.max(document.documentElement.clientHeight, window.innerHeight || 0); const camera = new THREE.PerspectiveCamera( 60, renderWidth / renderHeight, 1, 100 ) camera.position.z = 1 // Renderer let renderer = new THREE.WebGLRenderer({ antialias: true, }); // Renderer opts renderer.setPixelRatio( window.devicePixelRatio ); renderer.setClearColor( 0x23272A, 1.0 ); renderer.setSize( renderWidth, renderHeight ); // add the renderer to the dom sliderContainer.appendChild( renderer.domElement ); let textureLoader = new THREE.TextureLoader(); textureLoader.crossOrigin = "anonymous"; imgs.forEach( ( img ) => { let image = textureLoader.load( img.getAttribute( 'src' )); image.magFilter = image.minFilter = THREE.LinearFilter; image.anisotropy = renderer.capabilities.getMaxAnisotropy(); sliderImages.push( image ); }); let dispImg = textureLoader.load('https://assets.codepen.io/590856/freesolo-texture.jpg'); // let dispImg = textureLoader.load('https://assets.codepen.io/590856/freesolo-texture2.jpg'); // let dispImg = textureLoader.load('https://assets.codepen.io/590856/freesolo-texture3.jpg'); dispImg.wrapS = dispImg.wrapT = THREE.RepeatWrapping; let mat = new THREE.RawShaderMaterial({ uniforms: { dispFactor: { value: 0.0 }, currentImage: { value: sliderImages[0] }, nextImage: { value: sliderImages[1] }, disp: { value: dispImg }, }, vertexShader: vertex, fragmentShader: fragment, transparent: true, opacity:1.0 }); let geometry = new THREE.PlaneBufferGeometry( 2.4,1.16 ); let object = new THREE.Mesh(geometry, mat); object.position.set(0, 0, 0); scene.add(object); window.addEventListener('resize',() => { renderer.setSize(window.innerWidth,window.innerHeight); camera.aspect = window.innerWidth / window.innerHeight; camera.updateProjectionMatrix(); }) let animate = () => { requestAnimationFrame(animate); renderer.render(scene, camera); }; animate(); let index = 0; const slider = document.querySelector('.slider-inner'); const slide = document.querySelectorAll('.slide') const slideImg = document.querySelector('.slide-background-img') const next = document.querySelector(".slider-next-btn"); const prev = document.querySelector(".slider-prev-btn"); const slideNum = document.querySelector('.slide-indicator'); const description = document.querySelectorAll('.slide-description'); const title = document.querySelectorAll('.slide-title'); const descriptionArray = Array.from(description); // Position all description elements TweenMax.set(descriptionArray[2],{y:"-100%"}) TweenMax.set(descriptionArray[3],{y:"-200%"}) // Slide to the right let autoPlay = true; let isSliding = false; let slideTl = (transformVal,transformVal2,elIndex) => { const tl = new TimelineMax({ onStart:()=> { isSliding = true }, onComplete:() => { isSliding = false } }); tl.to(slider,0.85,{x:transformVal,ease:Sine.easeInOut},'slide') .to(slideNum,0.85,{y:transformVal2,ease:Sine.easeInOut},'slide') .fromTo(descriptionArray[elIndex],0.25,{y:0},{y:"+=100%",ease:Circ.easeIn},'slide') .fromTo(descriptionArray[index],0.3,{y:0},{y:"-=100%"},'slide+=0.75') .set(title[elIndex],{className:"-=active"},'slide') .set(title[index],{className:"+=active"},'slide') return tl; } const animateDisplace = () => { mat.uniforms.nextImage.value = sliderImages[index]; mat.uniforms.nextImage.needsUpdate = true; TweenLite.to( mat.uniforms.dispFactor, 1, { value: 1, ease: 'Sine.easeInOut', onComplete: () => { mat.uniforms.currentImage.value = sliderImages[index]; mat.uniforms.currentImage.needsUpdate = true; mat.uniforms.dispFactor.value = 0.0; } }); } const nextSlide = () => { if (isSliding == false) { index ++ slideTl("-=100%","-=25%",index - 1); animateDisplace(); } } const prevSlide = () => { if (isSliding == false) { index -- slideTl("+=100%","+=25%",index + 1); animateDisplace(); } } // ANIMATE SLIDE INDICATOR const progressEase = "M0,0 C0.092,0.01 0.162,0.108 0.212,0.21 0.328,0.448 0.458,1 1,1"; const progressBar = CSSRulePlugin.getRule('.slide-progress-bar:after'); const progressTl = (delay) => { const tl = new TimelineMax({ repeat:-1, repeatDelay:1.5, paused:true, delay:delay }); tl.fromTo(progressBar,2,{cssRule:{x:"-100%"}},{cssRule:{x:"0%"},ease:CustomEase.create("custom",progressEase)}) .add(() => { if (index <= 2 && autoPlay === true) { nextSlide(); TweenMax.to(progressBar,1,{cssRule:{x:"100%"},ease:CustomEase.create("custom",progressEase)}) } if (index === 3 && autoPlay === true) { tl.stop(); } }) return tl; } const animateProgress = progressTl(0.4); // Reset progress bar on hover const resetProgress = () => { if (index < 3 && autoPlay === true) { TweenMax.to(animateProgress, 1 * animateProgress.progress(),{ progress:0, onComplete:()=> { animateProgress.progress(0) animateProgress.stop(); } } ) } } // Change Timeline state const changeState = (timeline) => { if (timeline.progress() == 1) { timeline.reverse() } else { timeline.play() } } // ANIMATE SEARCH ICON const searchBtn = document.querySelector('.search-btn') const searchInput = document.querySelector('.search-input') const searchTl = () => { const tl = new TimelineLite({paused:true}); tl.to(searchInput,0.15,{width:"225px",fontSize:"16px"},'in') return tl; } const animateSearch = searchTl() // ANIMATE MENU ICON const menuBtn = document.querySelector('.menu-btn') const menu = document.querySelector('.menu'); const menuTl = () => { const tl = new TimelineLite({paused:true}) tl.to(menu,0.2,{x:0,ease:Sine.easeInOut},'in') return tl; } const animateMenu = menuTl() // Click Event listener slider buttons next.addEventListener('click',() => { if (index < 3) { nextSlide(); } }) prev.addEventListener('click',() => { if (index > 0) { prevSlide(); } }) // Hover Event listeners for buttons // reset or restart progress bar animation on enter/leave next.addEventListener('mouseenter',() => { resetProgress(); }) prev.addEventListener('mouseenter',() => { resetProgress(); }) next.addEventListener('mouseleave',() => { animateProgress.progress(0) animateProgress.restart() }) prev.addEventListener('mouseleave',() => { animateProgress.progress(0) animateProgress.restart() }) // Search event listener // toggle search searchBtn.addEventListener('click',() =>{ changeState(animateSearch) }) // Menu event Listener // toggle menu menuBtn.addEventListener('click',() => { changeState(animateMenu) }) // IMAGESLOADED const loader = document.querySelector('.preloader'), loaderOutline = document.querySelector('#preloader-outline'), loaderBot = document.querySelector('#preloader-bot'), snow = document.querySelectorAll('#snowGroup path'); TweenMax.set('svg',{visibility:"visible"}) TweenMax.set(loaderOutline,{drawSVG:"0% 0%"}) TweenMax.set(snow,{drawSVG:"0% 0%"}) TweenMax.set(loaderBot,{drawSVG:"50% 50%"}) imgToLoad = document.querySelectorAll('img , canvas'); imgLoad = imagesLoaded(imgToLoad) let loadedCo