script.js: // Direction constants const NEXT = 1; const PREV = -1; // Slide titles array (global) const slideTitles = [ "Cosmic Harmony", "Astral Journey", "Ethereal Vision", "Quantum Field", "Celestial Path", "Cosmic Whisper" ]; // Global variable to track currently hovered thumbnail let currentHoveredThumb = null; // Global variable to track mouse position over thumbnails let mouseOverThumbnails = false; let lastHoveredThumbIndex = null; // Global animation state management let isAnimating = false; let pendingNavigation = null; // Function to visually update navigation elements based on animation state function updateNavigationUI(disabled) { // Update navigation arrows const navButtons = document.querySelectorAll(".counter-nav"); navButtons.forEach((btn) => { btn.style.opacity = disabled ? "0.3" : ""; btn.style.pointerEvents = disabled ? "none" : ""; }); // Update thumbnails const thumbs = document.querySelectorAll(".slide-thumb"); thumbs.forEach((thumb) => { thumb.style.pointerEvents = disabled ? "none" : ""; }); } // Global functions for slide management function updateSlideCounter(index) { const currentSlideEl = document.querySelector(".current-slide"); if (currentSlideEl) { currentSlideEl.textContent = String(index + 1).padStart(2, "0"); } } function updateSlideTitle(index) { const titleContainer = document.querySelector(".slide-title-container"); const currentTitle = document.querySelector(".slide-title"); if (!titleContainer || !currentTitle) return; // Create a new title element const newTitle = document.createElement("div"); newTitle.className = "slide-title enter-up"; newTitle.textContent = slideTitles[index]; // Add it to the container titleContainer.appendChild(newTitle); // Add exit animation class to old title currentTitle.classList.add("exit-up"); // Force reflow void newTitle.offsetWidth; // Start entrance animation setTimeout(() => { newTitle.classList.remove("enter-up"); }, 10); // Remove old title after animation completes setTimeout(() => { currentTitle.remove(); }, 500); } // Updated updateDragLines function for continuous lines function updateDragLines(activeIndex, forceUpdate = false) { const lines = document.querySelectorAll(".drag-line"); if (!lines.length) return; // Reset all lines immediately lines.forEach((line) => { line.style.height = "var(--line-base-height)"; line.style.backgroundColor = "rgba(255, 255, 255, 0.3)"; }); // If no active index is provided, return if (activeIndex === null) { return; } const slideCount = document.querySelectorAll(".slide").length; const lineCount = lines.length; // Calculate the center position of the active thumbnail const thumbWidth = 720 / slideCount; // Total width divided by number of slides const centerPosition = (activeIndex + 0.5) * thumbWidth; // Calculate the width of one line section const lineWidth = 720 / lineCount; // Apply the wave pattern to all lines based on distance from center for (let i = 0; i < lineCount; i++) { // Calculate the center position of this line const linePosition = (i + 0.5) * lineWidth; // Calculate distance from the center of the active thumbnail const distFromCenter = Math.abs(linePosition - centerPosition); // Calculate the maximum distance for influence (half a thumbnail width plus a bit) const maxDistance = thumbWidth * 0.7; // Only affect lines within the influence range if (distFromCenter <= maxDistance) { // Calculate normalized distance (0 at center, 1 at edge of influence) const normalizedDist = distFromCenter / maxDistance; // Create a cosine wave pattern (1 at center, 0 at edge) const waveHeight = Math.cos((normalizedDist * Math.PI) / 2); // Scale the height based on the wave pattern (taller in center) const height = parseInt( getComputedStyle(document.documentElement).getPropertyValue( "--line-base-height" ) ) + waveHeight * 35; // Calculate opacity based on distance (more opaque at center) const opacity = 0.3 + waveHeight * 0.4; // Stagger the animations slightly based on distance from center const delay = normalizedDist * 100; // If forceUpdate is true, apply immediately without checking hover state if (forceUpdate) { lines[i].style.height = `${height}px`; lines[i].style.backgroundColor = `rgba(255, 255, 255, ${opacity})`; } else { setTimeout(() => { // Only apply if this is still the current hovered thumbnail // or if we're forcing an update if ( currentHoveredThumb === activeIndex || (mouseOverThumbnails && lastHoveredThumbIndex === activeIndex) ) { lines[i].style.height = `${height}px`; lines[i].style.backgroundColor = `rgba(255, 255, 255, ${opacity})`; } }, delay); } } } } class Slideshow { DOM = { el: null, slides: null, slidesInner: null }; current = 0; slidesTotal = 0; constructor(DOM_el) { this.DOM.el = DOM_el; this.DOM.slides = [...this.DOM.el.querySelectorAll(".slide")]; this.DOM.slidesInner = this.DOM.slides.map((item) => item.querySelector(".slide__img") ); this.DOM.slides[this.current].classList.add("slide--current"); this.slidesTotal = this.DOM.slides.length; } next() { this.navigate(NEXT); } prev() { this.navigate(PREV); } // Method to navigate to a specific slide index goTo(index) { // If already animating, store this as pending navigation if (isAnimating) { pendingNavigation = { type: "goto", index }; return false; } // Don't navigate if it's the current slide if (index === this.current) return false; // Set animation state isAnimating = true; updateNavigationUI(true); const previous = this.current; this.current = index; // Update active thumbnail const thumbs = document.querySelectorAll(".slide-thumb"); thumbs.forEach((thumb, i) => { thumb.classList.toggle("active", i === index); }); // Update counter and title updateSlideCounter(index); updateSlideTitle(index); // Show drag lines for active thumbnail updateDragLines(index, true); // Determine direction for the animation const direction = index > previous ? 1 : -1; // Get slides and perform animation const currentSlide = this.DOM.slides[previous]; const currentInner = this.DOM.slidesInner[previous]; const up