4 }, { duration: 0.7, ease: "expo", scaleY: 1 }, "start" ) .fromTo( currentInner, { filter: "contrast(100%) saturate(100%)" }, { duration: 0.7, ease: "expo", filter: "contrast(120%) saturate(140%)" }, "start" ) .addLabel("middle", "start+=0.6") .to( upcomingSlide, { duration: 1, ease: "power4.inOut", scale: 1 }, "middle" ) .to( currentSlide, { duration: 1, ease: "power4.inOut", scale: 0.98, autoAlpha: 0 }, "middle" ); } } // Initialize document.addEventListener("DOMContentLoaded", () => { // Create slideshow instance const slides = document.querySelector(".slides"); const slideshow = new Slideshow(slides); // Create thumbnails const thumbsContainer = document.querySelector(".slide-thumbs"); const slideImgs = document.querySelectorAll(".slide__img"); const slideCount = slideImgs.length; // Clear thumbs container first (in case it had any previous content) if (thumbsContainer) { thumbsContainer.innerHTML = ""; slideImgs.forEach((img, index) => { const bgImg = img.style.backgroundImage; const thumb = document.createElement("div"); thumb.className = "slide-thumb"; thumb.style.backgroundImage = bgImg; if (index === 0) { thumb.classList.add("active"); } // Animation for clicking on thumbnails - use goTo method thumb.addEventListener("click", () => { // Store the clicked thumbnail index for later lastHoveredThumbIndex = index; // Use the new goTo method which handles animation state slideshow.goTo(index); }); // Add hover effect to thumbnails with global tracking thumb.addEventListener("mouseenter", () => { // Update the global variable to track which thumbnail is hovered currentHoveredThumb = index; lastHoveredThumbIndex = index; mouseOverThumbnails = true; // Only update lines if not animating if (!isAnimating) { updateDragLines(index, true); } }); thumb.addEventListener("mouseleave", () => { // Only reset if we're leaving this specific thumbnail // This prevents resetting when moving directly to another thumbnail if (currentHoveredThumb === index) { currentHoveredThumb = null; // Don't reset lastHoveredThumbIndex here } }); thumbsContainer.appendChild(thumb); }); } // Create continuous drag indicator lines const dragIndicator = document.querySelector(".drag-indicator"); if (dragIndicator) { dragIndicator.innerHTML = ""; // Create a container for the lines to ensure consistent positioning const linesContainer = document.createElement("div"); linesContainer.className = "lines-container"; dragIndicator.appendChild(linesContainer); // Create evenly spaced lines across the entire width const totalLines = 60; // Increased number of lines for smoother appearance for (let i = 0; i < totalLines; i++) { const line = document.createElement("div"); line.className = "drag-line"; linesContainer.appendChild(line); } } // Set total slides const totalSlidesEl = document.querySelector(".total-slides"); if (totalSlidesEl) { totalSlidesEl.textContent = String(slideCount).padStart(2, "0"); } // Add navigation handlers - use direct methods instead of throttled versions const prevButton = document.querySelector(".prev-slide"); const nextButton = document.querySelector(".next-slide"); if (prevButton) { prevButton.addEventListener("click", () => slideshow.prev()); } if (nextButton) { nextButton.addEventListener("click", () => slideshow.next()); } // Initialize counters and lines updateSlideCounter(0); updateDragLines(0, true); // Initialize the first thumbnail's lines // Add global mouse leave handler for the entire thumbnails area const thumbsArea = document.querySelector(".thumbs-container"); if (thumbsArea) { thumbsArea.addEventListener("mouseenter", () => { mouseOverThumbnails = true; }); thumbsArea.addEventListener("mouseleave", () => { // Reset all lines when mouse leaves the entire thumbnails area mouseOverThumbnails = false; currentHoveredThumb = null; updateDragLines(null); }); } // Initialize GSAP Observer for scroll/drag with animation state check try { // First try using it directly if (typeof Observer !== "undefined") { Observer.create({ type