@import url("https://fonts.googleapis.com/css2?family=Inter:ital,opsz,wght@0,14..32,100..900;1,14..32,100..900&display=swap");

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Inter";
}

h1 {
  text-transform: uppercase;
  font-size: 7vw;
  font-weight: 700;
  line-height: 1;
}

p {
  font-size: 0.95rem;
}

.slider {
  position: relative;
  width: 100vw;
  height: 100svh;
  color: #fff;
  overflow: hidden;
}

canvas {
  display: block;
  width: 100%;
  height: 100%;
}

.slider-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  user-select: none;
  z-index: 2;
}

.slide-title {
  position: absolute;
  top: 45%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  text-align: center;
}

.slide-description {
  position: absolute;
  top: 60%;
  left: 60%;
  transform: translate(-50%, -50%);
  width: 25%;
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.slide-info p {
  text-transform: uppercase;
}

.slide-title h1 {
  display: flex;
  justify-content: center;
  gap: 0.2em;
}

.slide-title h1 .word {
  display: flex;
}

.slide-title h1 .char {
  display: block;
}

.char,
.line {
  overflow: hidden;
}

.char span,
.line span {
  display: inline-block;
  will-change: transform;
  position: relative;
}

@media (max-width: 1000px) {
  .slide-title {
    top: 50%;
  }

  .slide-description {
    width: 75%;
    text-align: center;
    top: unset;
    bottom: 5%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
}

