old + ww) && end > -threshold const progress = gsap.utils.clamp(0, 1, 1 - (translate + left + width) / (ww + width)) return { translate, isVisible, progress } } clampTarget() { const state = this.state state.target = gsap.utils.clamp(state.max, 0, state.target) } getPos({ changedTouches, clientX, clientY, target }) { const x = changedTouches ? changedTouches[0].clientX : clientX const y = changedTouches ? changedTouches[0].clientY : clientY return { x, y, target } } onDown(e) { const { x, y } = this.getPos(e) const { flags, on } = this.state flags.dragging = true on.x = x on.y = y } onUp() { const state = this.state state.flags.dragging = false state.off = state.target } onMove(e) { const { x, y } = this.getPos(e) const state = this.state if (!state.flags.dragging) return const { off, on } = state const moveX = x - on.x const moveY = y - on.y if ((Math.abs(moveX) > Math.abs(moveY)) && e.cancelable) { e.preventDefault() e.stopPropagation() } state.target = off + (moveX * this.opts.speed) } } /***/ /*** GL STUFF ****/ /***/ const backgroundCoverUv = ` vec2 backgroundCoverUv(vec2 screenSize, vec2 imageSize, vec2 uv) { float screenRatio = screenSize.x / screenSize.y; float imageRatio = imageSize.x / imageSize.y; vec2 newSize = screenRatio < imageRatio ? vec2(imageSize.x * screenSize.y / imageSize.y, screenSize.y) : vec2(screenSize.x, imageSize.y * screenSize.x / imageSize.x); vec2 newOffset = (screenRatio < imageRatio ? vec2((newSize.x - screenSize.x) / 2.0, 0.0) : vec2(0.0, (newSize.y - screenSize.y) / 2.0)) / newSize; return uv * screenSize / newSize + newOffset; } ` const vertexShader = ` precision mediump float; uniform float uVelo; varying vec2 vUv; #define M_PI 3.1415926535897932384626433832795 void main(){ vec3 pos = position; pos.x = pos.x + ((sin(uv.y * M_PI) * uVelo) * 0.125); vUv = uv; gl_Position = projectionMatrix * modelViewMatrix * vec4(pos,1.); } ` const fragmentShader = ` precision mediump float; ${backgroundCoverUv} uniform sampler2D uTexture; uniform vec2 uMeshSize; uniform vec2 uImageSize; uniform float uVelo; uniform float uScale; varying vec2 vUv; void main() { vec2 uv = vUv; vec2 texCenter = vec2(0.5); vec2 texUv = backgroundCoverUv(uMeshSize, uImageSize, uv); vec2 texScale = (texUv - texCenter) * uScale + texCenter; vec4 texture = texture2D(uTexture, texScale); texScale.x += 0.15 * uVelo; if(uv.x < 1.) texture.g = texture2D(uTexture, texScale).g; texScale.x += 0.10 * uVelo; if(uv.x < 1.) texture.b = texture2D(uTexture, texScale).b; gl_FragColor = texture; } ` const loader = new THREE.TextureLoader() loader.crossOrigin = 'anonymous' class Gl { constructor() { this.scene = new THREE.Scene() this.camera = new THREE.OrthographicCamera( store.ww / - 2, store.ww / 2, store.wh / 2, store.wh / - 2, 1, 10 ) this.camera.lookAt(this.scene.position) this.camera.position.z = 1 this.renderer = new THREE.WebGLRenderer({ alpha: true, antialias: true }) this.renderer.setPixelRatio(1.5) this.renderer.setSize(store.ww, store.wh) this.renderer.setClearColor(0xffffff, 0) this.init() } render() { this.renderer.render(this.scene, this.camera) } init() { const domEl = this.renderer.domElement domEl.classList.add('dom-gl') document.body.appendChild(domEl) } } class GlObject extends THREE.Object3D { init(el) { this.el = el this.resize() } resize() { this.rect = this.el.getBoundingClientRect() const { left, top, width, height } = this.rect this.pos = { x: (left + (width / 2)) - (store.ww / 2), y: (top + (height / 2)) - (store.wh / 2) } this.position.y = this.pos.y this.position.x = this.pos.x this.updateX() } updateX(current) { current && (this.position.x = current + this.pos.x) } } const planeGeo = new THREE.PlaneBufferGeometry(1, 1, 32, 32) const planeMat = new THREE.ShaderMaterial({ transparent: true, fragmentShader, vertexShader }) class Plane extends GlObject { init(el) { super.init(el) this.geo = planeGeo this.mat = planeMat.clone() this.mat.uniforms = { uTime: { value: 0 }, uTexture: { value: 0 }, uMeshSize: { value: new THREE