eft: 50%; 			transform: translate(-50%, -50%); 			width: 100%; 			height: 100%; 			border-radius: 100%; 			border: 1px solid rgba(255, 255, 255, 0.2); 			opacity: 0; 			transition: opacity 0.4s ease-in-out, width 0.4s ease-in-out, height 0.4s ease-in-out; 		} 	} } /* Page Loader */ .loading { 	&:before { 		content: ''; 		position: fixed; 		z-index: 100000; 		top: 0; 		left: 0; 		width: 100%; 		height: 100%; 		background: black; 	} 	&:after { 		content: ''; 		position: fixed; 		z-index: 100000; 		top: 50%; 		left: 50%; 		width: 60px; 		height: 60px; 		margin: -30px 0 0 -30px; 		pointer-events: none; 		border-radius: 50%; 		opacity: 0.4; 		background: white; 		animation: loaderAnim 0.7s linear infinite alternate forwards; 	} } @keyframes loaderAnim { 	to { 		opacity: 1; 		transform: scale3d(0.5,0.5,1); 	} }
