style.css: * { box-sizing: border-box; margin: 0; padding: 0; } body { font-family: "Montserrat", sans-serif; background: #0d0d0d; color: #eee; display: flex; justify-content: center; align-items: center; height: 100vh; overflow: hidden; } .slideshow-container { position: relative; width: 90%; max-width: 900px; aspect-ratio: 16/9; overflow: hidden; border-radius: 16px; box-shadow: 0 0 40px rgba(0, 0, 0, 0.7); } .slide { position: absolute; inset: 0; opacity: 0; pointer-events: none; transition: opacity 1.5s ease; } .slide.active { opacity: 1; pointer-events: auto; z-index: 1; } .slide img { width: 100%; height: 100%; object-fit: cover; transform: scale(1.1); filter: grayscale(100%) contrast(1.1) brightness(0.9); transition: transform 7s ease, opacity 2s ease; will-change: transform, opacity; } .slide.active img { transform: scale(1); opacity: 1; } .overlay { position: absolute; inset: 0; background: linear-gradient(to top, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.1)); z-index: 1; } .caption { position: absolute; bottom: 40px; left: 40px; font-family: "Montserrat", sans-serif; font-size: clamp(1.2rem, 3vw, 1.8rem); letter-spacing: 0.5px; opacity: 0; transform: translateY(10px); transition: all 1s ease 0.5s; z-index: 2; } .slide.active .caption { opacity: 1; transform: translateY(0); } /* navigation */ .prev, .next { cursor: pointer; position: absolute; top: 50%; transform: translateY(-50%); color: #fff; font-size: 2rem; padding: 14px; border-radius: 50%; background: rgba(255, 255, 255, 0.05); backdrop-filter: blur(4px); transition: all 0.3s ease; opacity: 0.4; z-index: 3; user-select: none; } .prev:hover, .next:hover { opacity: 1; background: rgba(255, 255, 255, 0.15); } .prev { left: 15px; } .next { right: 15px; } /* indicator dots */ .dots-container { position: absolute; bottom: 15px; left: 50%; transform: translateX(-50%); display: flex; gap: 12px; z-index: 3; } .dot { width: 12px; height: 12px; border-radius: 50%; background: rgba(255, 255, 255, 0.3); cursor: pointer; transition: all 0.3s ease; } .dot.active { background: rgba(255, 255, 255, 0.8); transform: scale(1.3); } .dot:hover { background: rgba(255, 255, 255, 0.6); } /* mobile-friendly */ @media (max-width: 600px) { .caption { bottom: 20px; left: 20px; font-size: 1rem; } .prev, .next { font-size: 1.6rem; padding: 10px; } }
script.js: const slides = document.querySelectorAll(".slide"); const dots = document.querySelectorAll(".dot"); let index = 0; function showSlide(n) { slides[index].classList.remove("active"); dots[index].classList.remove("active"); index = (n + slides.length) % slides.length; slides[index].classList.add("active"); dots[index].classList.add("active"); } // auto-play every 6 sec let slideInterval = setInterval(() => showSlide(index + 1), 6000); // nav buttons document .querySelector(".prev") .addEventListener("click", () => showSlide(index - 1)); document .querySelector(".next") .addEventListener("click", () => showSlide(index + 1)); // dots click dots.forEach((dot) => { dot.addEventListener("click", () => showSlide(parseInt(dot.dataset.index))); });

