  will-change: transform;
  transform: translateX(0);
}

.marquee-text h1 {
  font-size: 12rem;
  text-align: center;
}

.showreel {
  width: 100%;
  height: 75vh;
  background: black;
}

.about {
  height: max-content;
  padding: 4em 0;
}

.about .container {
  display: flex;
  gap: 2em;
}

.about .container > div {
  flex: 1;
}

.about-copy p {
  font-size: 2rem;
  font-weight: 300;
  line-height: 1.5;
  text-indent: 2em;
}

.about-col:nth-child(2) {
  display: flex;
  justify-content: flex-end;
}

.about-col:nth-child(2) {
  justify-content: flex-end;
}

.gallery {
  overflow: hidden;
}

.gallery-wrapper {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(30deg) scale(1.125);
}

.row {
  position: relative;
  width: 150vw;
  height: 350px;
  display: flex;
  justify-content: center;
  padding: 0 2em 2em 2em;
  gap: 2em;
  will-change: transform;
}

.team {
  height: max-content;
  overflow: hidden;
}

.team-intro {
  width: 75%;
}

.team-intro h1 {
  font-size: 5rem;
  font-weight: 300;
}

.team-member {
  display: flex;
  flex-direction: column;
  margin: 4em 0;
}

.team-member-position p {
  text-transform: uppercase;
  margin-bottom: 0.75em;
}

.team-member-index p {
  text-transform: uppercase;
  margin-top: 0.75em;
}

.team-member-profile {
  display: flex;
  align-items: center;
}

.team-member-img {
  width: 350px;
  height: 500px;
  overflow: hidden;
}
.team-member-info {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 4em;
  left: -2em;
  width: calc(100% - 500px);
}
.team-member-details {
  display: flex;
  gap: 2em;
}

.team-member-name p {
  font-size: 3rem;
}

.team-member-toggle {
  width: 5rem;
  height: 5rem;
  aspect-ratio: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid rgba(255, 255, 255, 0.35);
  border-radius: 100%;
}

.team-member-copy {
  width: 35%;
}

.team-member-copy p {
  color: var(--light2);
  line-height: 1.5;
}

.team-member-index h1 {
  display: none;
}

.team-member {
  position: relative;
}

.tm-2 {
  left: 40%;
}

.tm-3 {
  left: 15%;
}

.footer {
  height: max-content;
}

.footer-header {
  margin-bottom: 2em;
}

.footer .container {
  padding: 2em 1em 1em 1em;
}

.footer-title h1 {
  font-size: 5rem;
  font-weight: 300;
}

.footer-email {
  padding: 4em 0;
}

.footer-email p,
.footer-col-header p {
  color: var(--light2);
}

.footer-email h2 {
  font-size: 2.5rem;
  background: #958151;
  background: linear-gradient(
    to left,
    #958151 0%,
    #b99c5c 30%,
    #a8b177 70%,
    #647756 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.footer-content {
  display: flex;
  gap: 4em;
}

.footer-col:nth-child(1) {
  flex: 4;
}

.footer-col:nth-child(2) {
  flex: 2;
}

.footer-col-content {
  display: flex;
  gap: 4em;
}

.footer-sub-col {
  flex: 1;
  display: flex;
  flex-direction: column;
}

.location {
  margin-bottom: 4em;
}

.location h3 {
  font-weight: 400;
  margin-bottom: 0.75em;
}

.location p {
  margin-bottom: 0.25em;
  color: var(--light2);
}

.location p:last-child {
  margin: 2em 0 1em 0;
  color: var(--light1);
  display: flex;
  gap: 1em;
}

.footer-col-header {
  margin-bottom: 1em;
}

.footer-col-header p {
  margin-bottom: 3em;
}

.footer-col:nth-child(2) .footer-sub-col {
  display: flex;
  flex-direction: column;
  gap: 1.25em;
}

.footer-col:nth-child(2) .footer-sub-col p {
  font-size: 1.25rem;
}

/* light mode */
.team,
.footer {
  transition: background-color 0.5s ease, color 0.5s ease;
}

.team-member-toggle {
  transition: border-color 0.5s ease;
}

.footer .footer-email p,
.footer .footer-col-header p,
.footer .location p {
  transition: color 0.5s ease;
}

.team.light {
  background-color: #fff;
  color: #000;
}

.team.light .team-member-toggle {
  border: 1px solid rgba(0, 0, 0, 0.15);
}

.footer.light {
  background-color: #fff;
  color: #000;
}

.footer.light .footer-email p,
.footer.light .footer-col-header p {
  color: var(--light3);
}

.footer.light .location p {
  color: var(--light3);
}

@media (max-width: 900px) {
  .header {
    flex-direction: column;
    text-align: center;
  }

  .projects {
    flex-direction: column;
  }

  .project {
    opacity: 1 !important;
  }

  .project-col:nth-child(2) {
    margin-top: 0;
  }

  .cta {
    padding: 8em 1em;
    gap: 4em;
  }

  .cta-header {
    width: 90%;
  }

  .cta-header h2 {
    font-size: 1.75rem;
  }

  .manifesto-title h1 {
    font-size: 3.5rem;
  }

  .process-info {
    flex-direction: column;
    gap: 2em;
  }

  .process-icon {
    justify-content: flex-start;
  }

  .marquee-text {
    width: 400vw;
  }

  .marquee-text h1 {
    font-size: 4rem;
  }

  .about .container {
    flex-direction: column;
  }

  .about .container {
    gap: 4em;
  }

  .about-col:nth-child(2) {
    justify-content: flex-start;
  }

  .gallery {
    height: 75vh;
  }

  .gallery-wrapper {
    transform: translate(-50%, -50%) rotate(30deg) scale(1.125);
  }

  .row {
    width: 200vw;
    height: 200px;
    padding: 0 1em 1em 1em;
    gap: 1em;
  }

  .team-intro {
    width: 100%;
  }

  .team-intro h1 {
    font-size: 3rem;
  }

  .team-member-info {
    display: none;
  }

  .team-member-img {
    width: 100%;
  }

  .team-member-index p {
    display: none;
  }

  .team-member-index h1 {
    display: block;
    margin-top: 0.5em;
  }

  .tm-2,
  .tm-3 {
    left: 0%;
  }

  .footer-content,
  .footer-col-content {
    flex-direction: column;
    gap: 0em;
  }

  .footer .container {
    padding: 2em 1em 8em 1em;
  }

  .footer-title h1 {
    font-size: 3.5rem;
  }

  .footer-email h2 {
    font-size: 2rem;
  }
}

import { useEffect, useState, useRef } from "react";
import "./Home.css";
import { Link } from "react-router";

import HeroGradient from "../../components/HeroGradient/HeroGradient";
import VideoPlayer from "../../components/VideoPlayer/VideoPlayer";
import NavBar from "../../components/NavBar/NavBar";
import Cursor from "../../components/Cursor/Cursor";
import Transition from "../../components/Transition/Transition";

import { projects } from "./projects";

import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
import SplitType from "split-type";
import ReactLenis from "@studio-freight/react-lenis";

import { HiArrowRight } from "react-icons/hi";
import { RiArrowRightDownLine } from "react-icons/ri";

const Home = () => {
  const manifestoRef = useRef(null);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const scrollTimeout = setTimeout(() => {
      window.scrollTo({
        top: 0,
        behavior: "instant",
      });
    }, 0);

    return () => clearTimeout(scrollTimeout);
  }, []);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 900);
    };

    checkMobile();

    window.addEventListener("resize", checkMobile);

    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    ScrollTrigger.create({
      trigger: ".footer",
      start: "top 80%",
      onEnter: () => {
        document.querySelector(".team").classList.add("light");
        document.querySelector(".footer").classList.add("light");
      },
      onLeaveBack: () => {
        document.querySelector(".team").classList.remove("light");
        document.querySelector(".footer").classList.remove("light");
      },
    });

    if (!isMobile) {
      gsap.set(".project", { opacity: 0.35 });
    }

    if (!isMobile) {
      const projects = document.querySelectorAll(".project");

      projects.forEach((project) => {
        const projectImg = project.querySelector(".project-img img");

        project.addEventListener("mouseenter", () => {
          gsap.to(project, {
            opacity: 1,
            duration: 0.5,
            ease: "power2.out",
          });

          gsap.to(projectImg, {
            scale: 1.2,
            duration: 0.5,
            ease: "power2.out",
          });
        });

        project.addEventListener("mouseleave", () => {
          gsap.to(project, {
            opacity: 0.35,
            duration: 0.5,
            ease: "power2.out",
          });

          gsap.to(projectImg, {
            scale: 1,
            duration: 0.5,
            ease: "power2.out",
          });
        });
      });
    }

    const manifestoText = new SplitType(".manifesto-title h1", {
      types: ["words", "chars"],
      tagName: "span",
      wordClass: "word",
      charClass: "char",
    });

    const style = document.createElement("style");
    style.textContent = `
       .word {
         display: inline-block;
         margin-right: 0em;
       }
       .char {
         display: inline-block;
       }
     `;
    document.head.appendChild(style);

    gsap.set(manifestoText.chars, {
      opacity: 0.25,
    });

    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: ".manifesto",
        start: "top 35%",
        end: "bottom 75%",
        scrub: true,
        markers: false,
      },
    });

    manifestoText.chars.forEach((char, index) => {
      tl.to(
        char,
        {
          opacity: 1,
          duration: 0.1,
          ease: "none",
        },
        index * 0.1
      );
    });

    gsap.to(".marquee-text", {
      scrollTrigger: {
        trigger: ".marquee",
        start: "top bottom",
        end: "bottom top",
        scrub: 1,
        markers: false,
        onUpdate: (self) => {
          const moveAmount = self.progress * -1000;
          gsap.set(".marquee-text", {
            x: moveAmount,
          });
        },
      },
    });

    return () => {
      tl.kill();
      ScrollTrigger.getAll().forEach((trigger) => trigger.kill());
      manifestoText.revert();
      style.remove();
    };
  }, [isMobile]);

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    const rows = document.querySelectorAll(".row");
    const isMobileView = window.innerWidth <= 900;

    const getStartX = (index) => {
      const direction = index % 2 === 0 ? 1 : -1;
      return direction * (isMobileView ? 150 : 300);
    };

    if (rows.length > 0) {
      rows.forEach((row, index) => {
        const existingTrigger = ScrollTrigger.getAll().find(
          (st) => st.trigger === ".gallery" && st.vars?.targets === row
        );
        if (existingTrigger) {
          existingTrigger.kill();
        }

        const startX = getStartX(index);

        gsap.set(row, { x: startX });

        gsap.to(row, {
          scrollTrigger: {
            trigger: ".gallery",
            start: "top bottom",
            end: "bottom top",
            scrub: isMobileView ? 0.5 : 1,
            onUpdate: (self) => {
              const moveAmount = startX * (1 - self.progress);
              gsap.set(row, {
                x: moveAmount,
              });
            },
          },
        });
      });
    }

    return () => {
      ScrollTrigger.getAll().forEach((trigger) => trigger.kill());
    };
  }, [isMobile]);

  return (
    <ReactLenis root>
      <div className="home">
        <Cursor />
        <NavBar />
        <section className="hero" id="hero">
          <HeroGradient />
          <div className="header-container">
            <div className="header h-1">
              <h1>Made to Move,</h1>
              <h1>Built to Inspire</h1>
            </div>
            <div className="header h-2">
              <h1>Ideas Born,</h1>
              <h1>Boundaries Broken</h1>
            </div>
            <div className="header h-3">
              <h1>Ni sng to,</h1>
              <h1>Khng gii hn</h1>
            </div>
            <div className="header h-4">
              <h1>Where Vision Meets,</h1>
              <h1>Limitless Design</h1>
            </div>
          </div>
        </section>

        <section className="work" id="work">
          <div className="container">
            <div className="work-header">
              <HiArrowRight size={13} />
              <p>Selected projects</p>
            </div>

            <div className="projects">
              <div className="project-col">
                {projects
                  .filter((project) => project.column === 1)
                  .map((project) => (
                    <Link to="/work" key={project.id}>
                      <div className="project">
                        <div className="project-img">
                          <img src={project.image} alt="Project Thumbnail" />
                        </div>
                        <div className="project-name">
                          <h2>{project.title}</h2>
                        </div>
                        <div className="project-description">
                          <p>{project.description}</p>
                        </div>
                      </div>
                    </Link>
                  ))}
              </div>

              <div className="project-col">
                {projects
                  .filter((project) => project.column === 2)
