.vinyl-container {
  width: 70%;
  aspect-ratio: 1;
  margin: 0 auto 24px;
  background: #fff;
  perspective: 1000px;
}

.disk-wrapper {
  position: relative;
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
}

.disk-side {
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;
}

.disk-side.front {
  transform: rotateY(0deg);
}

.disk-side.back {
  transform: rotateY(180deg);
}

.vinyl-container img {
  width: 100%;
  height: 100%;
  display: block;
}

.vinyl-container img.spinning {
  animation: spin 20s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

