.article-date {
  position: absolute;
  left: 0;
  bottom: 0;
  padding: 1em;
  background-color: var(--color-dark);
}

.article-title {
  margin: 0.5em 0 0.75em 0;
}

.article-link a {
  color: var(--color-accent);
  font-family: "FK Screamer";
  font-size: 24px;
  font-weight: 300;
}

@media (max-width: 900px) {
  .articles-row {
    flex-direction: column;
  }
}
