"use client";
import "./StorySlides.css";
import { useEffect, useRef } from "react";
import { stories } from "./stories.js";
import { gsap } from "gsap";
import Button from "../Button/Button.jsx";

export default function StorySlides() {
  const storiesContainerRef = useRef(null);

  useEffect(() => {
    if (typeof window === "undefined" || !storiesContainerRef.current) return;

    let activeStory = 0;
    let storyTimeout;
    let isAnimating = false;

    const storyDuration = 4000;
    const contentUpdateDelay = 0.4;

    const storiesContainer = storiesContainerRef.current;

    function resetIndexHighlight(index) {
      const highlight = storiesContainer.querySelectorAll(
        ".index .index-highlight"
      )[index];
      if (!highlight) return;

      gsap.killTweensOf(highlight);
      gsap.to(highlight, {
        width: "100%",
        duration: 0.3,
        onStart: () => {
          gsap.to(highlight, {
            transformOrigin: "right center",
            scaleX: 0,
            duration: 0.3,
          });
        },
      });
    }

    function animateIndexHighlight(index) {
      const highlight = storiesContainer.querySelectorAll(
        ".index .index-highlight"
      )[index];
      if (!highlight) return;

      gsap.set(highlight, {
        width: "0%",
        scaleX: 1,
        transformOrigin: "right center",
      });
      gsap.to(highlight, {
        width: "100%",
        duration: storyDuration / 1000,
        ease: "none",
      });
    }

    function animateNewImage(imgContainer) {
      gsap.set(imgContainer, {
        clipPath: "polygon(100% 0%, 100% 0%, 100% 100%, 100% 100%)",
      });
      gsap.to(imgContainer, {
        clipPath: "polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%)",
        duration: 1,
        ease: "power4.out",
      });
    }

    function animateImageScale(currentImg, upcomingImg) {
      const tl = gsap.timeline({
        onComplete: () => {
          isAnimating = false;
        },
      });

      tl.fromTo(
        currentImg,
        { scale: 1, rotate: 0 },
        {
          scale: 2,
          rotate: -25,
          duration: 1,
          ease: "power4.out",
          onComplete: () => {
            if (currentImg.parentElement) {
              currentImg.parentElement.remove();
            }
          },
        }
      );

      tl.fromTo(
        upcomingImg,
        { scale: 2, rotate: 25 },
        { scale: 1, rotate: 0, duration: 1, ease: "power4.out" },
        "<"
      );
    }

    function cleanUpElements() {
      const profileNameDiv = storiesContainer.querySelector(".profile-name");
      const titleRows = storiesContainer.querySelectorAll(".title-row");

      if (profileNameDiv) {
        while (profileNameDiv.childElementCount > 2) {
          profileNameDiv.removeChild(profileNameDiv.firstChild);
        }
      }

      if (titleRows) {
        titleRows.forEach((titleRow) => {
          while (titleRow.childElementCount > 2) {
            titleRow.removeChild(titleRow.firstChild);
          }
        });
      }
    }

    function changeStory() {
      if (isAnimating) {
        return;
      }

      isAnimating = true;

      const previousStory = activeStory;
      activeStory = (activeStory + 1) % stories.length;

      const story = stories[activeStory];

      const profileNameElements =
        storiesContainer.querySelectorAll(".profile-name p");
      if (profileNameElements.length > 0) {
        gsap.to(profileNameElements, {
          y: -24,
          duration: 0.75,
          delay: contentUpdateDelay,
          ease: "power3.out",
        });
      }

      const titleElements = storiesContainer.querySelectorAll(".title-row h1");
      if (titleElements.length > 0) {
        gsap.to(titleElements, {
          y: -48,
          duration: 0.75,
          delay: contentUpdateDelay,
          ease: "power3.out",
        });
      }

      const currentImgContainer =
        storiesContainer.querySelector(".story-img .img");
      if (!currentImgContainer) {
        isAnimating = false;
        return;
      }

      const currentImg = currentImgContainer.querySelector("img");
      if (!currentImg) {
        isAnimating = false;
        return;
      }

      setTimeout(() => {
        const profileNameDiv = storiesContainer.querySelector(".profile-name");
        if (profileNameDiv) {
          const newProfileName = document.createElement("p");
          newProfileName.innerText = story.profileName;
          newProfileName.style.transform = "translateY(24px)";

          profileNameDiv.appendChild(newProfileName);

          gsap.to(newProfileName, {
            y: 0,
            duration: 0.5,
            delay: contentUpdateDelay,
            ease: "power3.out",
          });
        }

        const titleRows = storiesContainer.querySelectorAll(".title-row");
        if (titleRows.length > 0) {
          story.title.forEach((line, index) => {
            if (titleRows[index]) {
              const newTitle = document.createElement("h1");
              newTitle.innerText = line;
              newTitle.style.transform = "translateY(48px)";
              titleRows[index].appendChild(newTitle);

              gsap.to(newTitle, {
                y: 0,
                duration: 0.75,
                delay: contentUpdateDelay,
                ease: "power3.out",
              });
            }
          });
        }

        const storyImgDiv = storiesContainer.querySelector(".story-img");
        if (storyImgDiv) {
          const newImgContainer = document.createElement("div");
          newImgContainer.classList.add("img");

          const newStoryImg = document.createElement("img");
          newStoryImg.src = story.storyImg;
          newStoryImg.alt = story.profileName;

          newImgContainer.appendChild(newStoryImg);
          storyImgDiv.appendChild(newImgContainer);

          animateNewImage(newImgContainer);
          animateImageScale(currentImg, newStoryImg);
        }

        resetIndexHighlight(previousStory);
        animateIndexHighlight(activeStory);

        cleanUpElements();

        clearTimeout(storyTimeout);
        storyTimeout = setTimeout(() => changeStory(), storyDuration);
      }, 500);

      setTimeout(() => {
        const profileImg = storiesContainer.querySelector(".profile-icon img");
        if (profileImg) {
          profileImg.src = story.profileImg;
        }

        const link = storiesContainer.querySelector(".link a");
        if (link) {
          link.href = story.linkSrc;
          const labelEl = link.querySelector(".button-label");
          if (labelEl) {
            labelEl.textContent = story.linkLabel;
          } else {
            link.textContent = story.linkLabel;
          }
        }
      }, 600);
    }

    function handleClick() {
      if (isAnimating) {
        return;
      }

      clearTimeout(storyTimeout);
      resetIndexHighlight(activeStory);
      changeStory();
    }

    storiesContainer.addEventListener("click", handleClick);

    storyTimeout = setTimeout(() => changeStory(), storyDuration);
    animateIndexHighlight(activeStory);

    return () => {
      storiesContainer.removeEventListener("click", handleClick);
      clearTimeout(storyTimeout);
      gsap.killTweensOf(storiesContainer.querySelectorAll("*"));
    };
  }, []);

  return (
    <div className="stories-container stories" ref={storiesContainerRef}>
      <div className="story-img">
        <div className="img">
          <img src="/stories/story-1.jpg" alt="" />
        </div>
      </div>

      <div className="stories-footer">
        <div className="container">
          <p className="sm">Creative Index</p>
          <p className="sm">( Since 2025 )</p>
        </div>
      </div>

      <div className="story-content">
        <div className="row">
          <div className="indices">
            {stories.map((_, index) => (
              <div className="index" key={`index-${index}`}>
                <div className="index-highlight"></div>
              </div>
            ))}
          </div>

          <div className="profile">
            <div className="profile-icon">
              <img src="/stories/profile-1.png" alt="" />
            </div>

            <div className="profile-name">
              <p>Behance</p>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="title">
            {stories[0].title.map((line, index) => (
              <div className="title-row" key={`title-${index}`}>
                <h1>{line}</h1>
              </div>
            ))}
          </div>

          <div className="link">
            <Button variant="light" href={stories[0].linkSrc}>
              {stories[0].linkLabel}
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

.sticky {
  position: relative;
  width: 100%;
  height: 100svh;
  overflow: hidden;
  background-color: var(--base-100);
}

.sticky-header {
  position: relative;
  top: 0;
  left: 0;
  width: 250vw;
  height: 100svh;
  display: flex;
  justify-content: center;
  align-items: center;
  will-change: transform;
}

.sticky-header h1 {
  margin: 0;
  color: var(--base-400);
  font-size: 28vw;
  letter-spacing: -0.5rem;
  line-height: 100%;
}

.card {
  position: absolute;
  top: 10%;
  left: 100%;
  width: 325px;
  height: 500px;
  background-color: var(--base-400);
  border-radius: 1rem;
  padding: 1rem;
  will-change: transform;
  z-index: 2;
}

#card-1 {
  background-color: var(--accent-1);
}

#card-2 {
  background-color: var(--accent-2);
}

#card-3 {
  background-color: var(--accent-3);
}

#card-4 {
  background-color: var(--accent-4);
}

#card-5 {
  background-color: var(--accent-5);
}

.card .card-img {
  width: 100%;
  height: 200px;
  border-radius: 0.5em;
  overflow: hidden;
}

.card .card-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.card-content {
  width: 100%;
  height: 275px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  color: #fff;
  padding: 1rem;
}

.card-content h2 {
  font-size: 2.5rem;
  letter-spacing: -0.05rem;
}

@media (max-width: 999px) {
  .team-desktop.sticky {
    display: none;
  }

  .team-mobile {
    display: block;
    z-index: 0;
  }

  .team-mobile {
    position: relative;
    width: 100%;
    height: auto;
    overflow: visible;
    background-color: var(--base-100);
    padding: 2rem 1rem;
    display: flex;
    flex-direction: column;
    gap: 1.25rem;
  }

  .mobile-header {
    position: static;
    width: 100%;
    height: auto;
    display: block;
    will-change: auto;
    margin-bottom: 0.5rem;
  }

  .mobile-header h1 {
    margin: 0 0 0.5rem 0;
    color: var(--base-400);
    font-size: 2.25rem;
    letter-spacing: -0.06rem;
    line-height: 110%;
  }

  .card {
    position: static;
    top: auto;
    left: auto;
    width: 100%;
    height: auto;
    max-width: 680px;
    margin: 0 auto;
    will-change: auto;
    transform: none !important;
    opacity: 1 !important;
  }

  .card .card-img {
    height: 240px;
    margin-bottom: 1rem;
  }

  .card-content {
    height: auto;
    display: flex;
    flex-direction: column;
    gap: 3rem;
  }
}

@media (min-width: 1000px) {
  .team-mobile {
    display: none;
  }
}

"use client";
import "./TeamCards.css";
import { teamMembers } from "./teamMembers.js";
import { useRef } from "react";
import gsap from "gsap";
import { useGSAP } from "@gsap/react";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(useGSAP, ScrollTrigger);

export default function TeamCards() {
  const stickyRef = useRef(null);
  const headerRef = useRef(null);
  const cardsRef = useRef([]);

  useGSAP(
    () => {
      const mm = gsap.matchMedia();

      mm.add("(min-width: 1000px)", () => {
        let scrollTriggerInstance = null;

        const stickySection = stickyRef.current;
        const stickyHeader = headerRef.current;
        const cards = cardsRef.current;

        if (!stickySection || !stickyHeader || !cards || cards.length === 0) {
          return () => {};
        }

        let stickyHeight = 0;
        let maxTranslate = 0;
        let cardWidth = 325;
        let cardHeight = 500;
        let viewportWidth = window.innerWidth;
        let viewportHeight = window.innerHeight;
        let cardStartX = 25;
        let cardEndX = -650;

        const measure = () => {
          stickyHeight = window.innerHeight * 5;
          const headerWidth = stickyHeader ? stickyHeader.offsetWidth : 0;
          maxTranslate = Math.max(0, headerWidth - window.innerWidth);
          viewportWidth = window.innerWidth;
          viewportHeight = window.innerHeight;
          if (cards && cards.length > 0 && cards[0]) {
            const cardRect = cards[0].getBoundingClientRect();
            cardWidth = cardRect.width || 325;
            cardHeight = cardRect.height || 500;
          }

          const standardViewportWidth = 1920;
          const standardEndXPercent = -650;
          const standardTravelPixels = Math.abs(
            (standardEndXPercent / 100) * cardWidth
          );

          const viewportScale = viewportWidth / standardViewportWidth;
          const requiredTravelPixels =
            standardTravelPixels * 1.25 * Math.max(1, viewportScale);

          cardStartX = 25;
          cardEndX = -(requiredTravelPixels / cardWidth) * 100;
        };
        measure();

        const transforms = [
          [
            [10, 50, -10, 10],
            [20, -10, -45, 20],
          ],
          [
            [0, 47.5, -10, 15],
            [-25, 15, -45, 30],
          ],
          [
            [0, 52.5, -10, 5],
            [15, -5, -40, 60],
          ],
          [
            [0, 50, 30, -80],
            [20, -10, 60, 5],
          ],
          [
            [0, 55, -15, 30],
            [25, -15, 60, 95],
          ],
        ];

        scrollTriggerInstance = ScrollTrigger.create({
          trigger: stickySection,
          start: "top top",
          end: () => `+=${stickyHeight}px`,
          pin: true,
          pinSpacing: true,
          onUpdate: (self) => {
            const progress = self.progress;

            const translateX = -progress * maxTranslate;
            gsap.set(stickyHeader, { x: translateX });

            cards.forEach((card, index) => {
              const delay = index * 0.1125;
              const cardProgress = Math.max(
                0,
                Math.min((progress - delay) * 2, 1)
              );

              if (cardProgress > 0) {
                const yPos = transforms[index][0];
                const rotations = transforms[index][1];

                const cardX = gsap.utils.interpolate(
                  cardStartX,
                  cardEndX,
                  cardProgress
                );

                const yProgress = cardProgress * 3;
                const yIndex = Math.min(Math.floor(yProgress), yPos.length - 2);
                const yInterpolation = yProgress - yIndex;
                const cardY = gsap.utils.interpolate(
                  yPos[yIndex],
                  yPos[yIndex + 1],
                  yInterpolation
                );

                const cardRotation = gsap.utils.interpolate(
                  rotations[yIndex],
                  rotations[yIndex + 1],
                  yInterpolation
                );

                gsap.set(card, {
                  xPercent: cardX,
                  yPercent: cardY,
                  rotation: cardRotation,
                  opacity: 1,
                });
              } else {
                gsap.set(card, { opacity: 0 });
              }
            });
          },
        });

        const onRefreshInit = () => measure();
        ScrollTrigger.addEventListener("refreshInit", onRefreshInit);

        const handleResize = () => {
          measure();
          ScrollTrigger.refresh();
        };
        window.addEventListener("resize", handleResize, { passive: true });

        ScrollTrigger.refresh();

        return () => {
          if (scrollTriggerInstance) scrollTriggerInstance.kill();
          ScrollTrigger.removeEventListener("refreshInit", onRefreshInit);
          window.removeEventListener("resize", handleResize);
        };
      });

      mm.add("(max-width: 999px)", () => {
        const stickySection = stickyRef.current;
        const stickyHeader = headerRef.current;
        if (stickySection) gsap.set(stickySection, { clearProps: "all" });
        if (stickyHeader) gsap.set(stickyHeader, { clearProps: "all" });
        cardsRef.current.forEach((card) => {
          if (card) gsap.set(card, { clearProps: "all", opacity: 1 });
        });

        ScrollTrigger.refresh();

        const refreshHandler = () => {
          ScrollTrigger.refresh();
        };

        window.addEventListener("orientationchange", refreshHandler);
        const onLoad = () => ScrollTrigger.refresh();
        window.addEventListener("load", onLoad, { passive: true });

        return () => {
          window.removeEventListener("orientationchange", refreshHandler);
          window.removeEventListener("load", onLoad);
        };
      });

      return () => {
        mm.revert();
      };
    },
    { scope: stickyRef }
  );

  return (
    <>
      {/* desktop animated section */}
      <section className="sticky team-desktop" ref={stickyRef}>
        <div className="sticky-header" ref={headerRef}>
          <h1>Minds at Work</h1>
        </div>
        {teamMembers.map((m, idx) => (
          <div
            className="card"
            id={m.id}
            key={m.id}
            ref={(el) => (cardsRef.current[idx] = el)}
          >
            <div className="card-img">
              <img src={m.img} alt={m.alt} />
            </div>
            <div className="card-content">
              <div className="card-title">
                <h2>{m.name}</h2>
              </div>
              <div className="card-description">
                <p>{m.description}</p>
              </div>
            </div>
          </div>
        ))}
      </section>

      {/* mobile static section */}
      <section className="team-mobile">
        <div className="mobile-header">
          <h1>Minds at Work</h1>
        </div>
        {teamMembers.map((m) => (
          <div className="card" id={m.id} key={`m-${m.id}`}>
            <div className="card-img">
              <img src={m.img} alt={m.alt} />
            </div>
            <div className="card-content">
              <div className="card-title">
                <h2>{m.name}</h2>
              </div>
              <div className="card-description">
                <p>{m.description}</p>
              </div>
            </div>
          </div>
        ))}
      </section>
    </>
  );
}

