/* film: hero section */
.film-hero {
  position: relative;
  width: 100%;
  height: 100svh;
  background-color: var(--tone-200);
  overflow: hidden;
}

.film-hero .container {
  display: flex;
  justify-content: center;
  align-items: flex-end;
}

.film-hero .container .film-hero-copy {
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-bottom: 4rem;
  text-align: center;
  color: var(--tone-400);
}

.film-hero .container .film-hero-copy .pixelated-text h1 {
  padding: 2rem 2rem 1rem 2rem;
  font-size: 15vw;
}

.film-hero .container .film-hero-copy .film-hero-description {
  width: 50%;
  display: flex;
  flex-direction: column;
  gap: 4rem;
  margin: -2rem auto 0 auto;
}

/* film: banner section */
.banner.film-banner {
  background-color: var(--tone-100);
}

.banner.film-banner .banner-content .container {
  color: var(--tone-400);
}

.banner.film-banner .banner-img img {
  opacity: 0.5;
}

/* film: snapshots section */
.film-snapshots {
  position: relative;
  width: 100%;
  height: 100%;
  padding: 8rem 0;
  background-color: var(--tone-200);
}

.film-snapshots .container {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.film-snapshots .snap-row {
  width: 100%;
  display: flex;
  gap: 2rem;
}

.film-snapshots .snap-img {
  position: relative;
  flex: 1;
  width: 100%;
  height: 100%;
  aspect-ratio: 4/5;
}

.film-snapshots .mask {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
}

.film-snapshots .img-1 .mask {
  background: url(/sample-film/film-snapshot-1.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.film-snapshots .img-2 .mask {
  background: url(/sample-film/film-snapshot-2.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.film-snapshots .img-3 .mask {
  background: url(/sample-film/film-snapshot-3.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.film-snapshots .img-4 .mask {
  background: url(/sample-film/film-snapshot-4.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.film-snapshots .img-5 .mask {
  background: url(/sample-film/film-snapshot-5.jpg) no-repeat 50% 50%;
  background-size: cover;
}

.film-snapshots .img-6 .mask {
  background: url(/sample-film/film-snapshot-6.jpg) no-repeat 50% 50%;
  background-size: cover;
}

@media (max-width: 1000px) {
  .film-hero .container .film-hero-copy .pixelated-text h1 {
    padding: 0 0 5rem 0;
    font-size: 20vw;
  }

  .film-hero .container .film-hero-copy .film-hero-description {
    width: 100%;
  }

  .film-snapshots .container,
  .film-snapshots .snap-row {
    flex-direction: column;
    gap: 1rem;
  }

  .snap-img:not([class*="img-"]) {
    display: none;
  }
}

/* footer */
footer {
  position: relative;
  width: 100%;
  height: 100svh;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding-top: 2rem;
  background-color: var(--tone-100);
  color: var(--tone-400);
}

.footer-row {
  width: 100%;
}

.footer-row.form .container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  padding: 4rem 0;
  text-align: center;
}

.footer-row .footer-name {
  color: var(--tone-500);
}

.footer-input {
  width: 75%;
  max-width: 500px;
}

.footer-input input {
  width: 100%;
  padding: 1rem;
  font-family: "Cossette Titre";
  font-size: 1rem;
  font-weight: 500;
  text-align: center;
  text-transform: uppercase;
  background-color: var(--tone-400);
  border: none;
  outline: none;
}

.footer-submit-btn a {
  color: var(--tone-400);
}

.footer-row.meta {
  border-top: 1px solid var(--tone-200);
}

.footer-row.meta .container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  gap: 10rem;
}

.footer-row.meta .footer-meta-row {
  display: flex;
  justify-content: space-between;
  gap: 2rem;
}

.footer-row.meta .footer-meta-row a {
  display: block;
  width: 6rem;
  color: var(--tone-400);
}

@media (max-width: 1000px) {
  footer {
    height: 100%;
    padding-top: 4rem;
  }

  .footer-row.form .container {
    justify-content: flex-start;
    align-items: flex-start;
    padding: 1rem;
    margin-bottom: 2rem;
    text-align: left;
  }

  .footer-name h3 {
    font-size: 4rem;
  }

  .footer-row.meta .container {
    gap: 4rem;
  }

  .footer-row.meta .footer-meta-row {
    flex-direction: column;
    padding-top: 2rem;
  }

  .footer-row.meta .footer-meta-row:nth-child(1) .meta-info:nth-child(3),
  .footer-row.meta .footer-meta-row:nth-child(1) .meta-info:nth-child(4) {
    display: none;
  }

  .footer-row.meta .footer-meta-row:nth-child(2) {
    flex-direction: row;
  }
}

code.CGMWTSEPT2025/negative-films/css/home.cs:
/* home: about callout section */
.about-callout {
  position: relative;
  width: 50%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
  padding: 8rem 0 0 0;
  text-align: center;
}

/* home: about copy section */
.about-copy .container {
  display: flex;
  flex-direction: column;
  gap: 4rem;
}

.about-copy .container h3 {
  width: 75%;
  margin: 0 auto;
  text-align: center;
}

.about-copy .container .about-copy-info {
  width: 60%;
  display: flex;
  flex-direction: column;
  gap: 4rem;
  margin: 0 auto 8rem auto;
  text-align: center;
}

.about-copy .container .about-copy-info a {
  width: max-content;
  margin: 0 auto;
  color: var(--tone-500);
}

/* home: banner section */
.banner {
  position: relative;
  width: 100%;
  height: 150svh;
  overflow: hidden;
}

.banner .banner-img {
