  width: 100%;
  height: 100%;
}

.banner .banner-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  will-change: transform;
}

.banner .banner-content {
  position: relative;
  width: 100%;
  height: 100%;
}

.banner .banner-content .container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  color: var(--tone-500);
}

.banner .banner-content .container h2 {
  width: 75%;
}

.banner .banner-content .container .banner-copy {
  width: 50%;
}

/* home: clients section */
.clients {
  position: relative;
  width: 100%;
  height: 100svh;
  background-color: var(--tone-100);
  color: var(--tone-500);
  overflow: hidden;
}

.clients .clients-header {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.clients .clients-header p {
  color: var(--tone-400);
}

.clients .container {
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 4rem;
}

.clients .grid {
  position: relative;
  width: 90%;
  height: 60%;
  display: flex;
  flex-direction: column;
  margin: 0 auto;
  border: 1px solid var(--tone-200);
}

.clients .grid-row,
.clients .grid-item {
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
