/* menu */
nav {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
  width: 100vw;
  mix-blend-mode: difference;
  overflow-x: hidden;
}

nav .container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

nav .logo,
nav .menu-toggle-btn {
  padding: 0.25rem;
  cursor: pointer;
}

nav .logo a,
nav .menu-toggle-btn p {
  color: var(--tone-400);
  user-select: none;
}

nav .logo a {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
}

nav .logo a span {
  position: relative;
  display: inline-block;
  width: 0.5rem;
  height: 0.5rem;
  background-color: var(--tone-500);
  animation: blink 1s infinite;
}

@keyframes blink {
  0%,
  50% {
    opacity: 1;
  }
  51%,
  100% {
    opacity: 0;
  }
}

.nav-overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10;
  width: 100vw;
  height: 100svh;
