  // handle resize
  window.addEventListener("resize", () => {
    ScrollTrigger.killAll();
    gsap.set(".team-img", { clearProps: "all" });
    initTeamAnimation();
    initHeaderPin();
    ScrollTrigger.refresh();
  });
