/* interactive-grid */
.interactive-grid {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100svh;
  z-index: -1;
  pointer-events: none;
  overflow: hidden;
}

.interactive-grid .block {
