function initFaqItemsAnimation() {
  const faqItems = document.querySelectorAll(".faq-item");
  if (!faqItems.length) return;

  const faqSection = document.querySelector(".faq-container");
  if (!faqSection) return;

  gsap.set(faqItems, {
