import gsap from "gsap";

const TRACES_DATA = [
  {
    name: "Haven",
    img: "/traces/trace_img_01.jpg",
    tag: "Dome Settlement",
  },
  {
    name: "Longstrider",
    img: "/traces/trace_img_02.jpg",
    tag: "Field Transport",
  },
  {
    name: "Verdant Colonies",
    img: "/traces/trace_img_03.jpg",
    tag: "Eco Habitat",
  },
  {
    name: "Suncross",
    img: "/traces/trace_img_04.jpg",
    tag: "Surface Journey",
  },
  {
    name: "Red Bluffs",
    img: "/traces/trace_img_05.jpg",
    tag: "Highland District",
  },
  {
    name: "Moonshore",
