"use client";
import { useEffect, useRef } from "react";
import gsap from "gsap";
import "./Marquee.css";

const ANIMATION_DURATION = 15;

const Marquee = () => {
  const wrapperRef = useRef(null);
  const animationRef = useRef(null);
  const directionRef = useRef(-1);

  useEffect(() => {
    const wrapper = wrapperRef.current;
    if (!wrapper) return;

    const content = wrapper.children[0];

    for (let i = 0; i < 3; i++) {
      const clone = content.cloneNode(true);
      wrapper.appendChild(clone);
    }

    const singleWidth = content.offsetWidth;
    const totalWidth = singleWidth * 2;

    const createAnimation = () => {
      if (animationRef.current) {
        animationRef.current.kill();
      }

      let currentX = gsap.getProperty(wrapper, "x");

      if (currentX <= -totalWidth) {
        currentX = currentX % singleWidth;
        gsap.set(wrapper, { x: currentX });
      } else if (currentX >= 0) {
        currentX = -singleWidth + (currentX % singleWidth);
        gsap.set(wrapper, { x: currentX });
      }

      const targetX =
        directionRef.current === -1
          ? currentX - singleWidth
          : currentX + singleWidth;

      const remainingDistance = Math.abs(targetX - currentX);
      const remainingDuration =
        (remainingDistance / singleWidth) * ANIMATION_DURATION;

      animationRef.current = gsap.to(wrapper, {
        x: targetX,
        duration: remainingDuration,
        ease: "none",
        repeat: -1,
        onRepeat: () => {
          let resetX = gsap.getProperty(wrapper, "x");

          if (directionRef.current === -1 && resetX <= -totalWidth) {
            resetX = resetX % singleWidth;
          } else if (directionRef.current === 1 && resetX >= 0) {
            resetX = -singleWidth + (resetX % singleWidth);
          }

          gsap.set(wrapper, { x: resetX });
        },
      });
    };

    createAnimation();

    let lastScrollTop = 0;
    const handleScroll = () => {
      const st = window.pageYOffset || document.documentElement.scrollTop;
      const newDirection = st > lastScrollTop ? -1 : 1;

      if (newDirection !== directionRef.current) {
        directionRef.current = newDirection;
        createAnimation();
      }

      lastScrollTop = st <= 0 ? 0 : st;
    };

    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
      if (animationRef.current) {
        animationRef.current.kill();
      }
    };
  }, []);

  return (
    <div className="marquee">
      <div className="marquee-wrapper" ref={wrapperRef}>
        <div className="marquee-content">
          <h1>
            The Evolution of AI  The Evolution of AI  The Evolution of AI 
            The Evolution of AI 
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Marquee;

import React, { useState, useEffect, useRef } from "react";
import dynamic from "next/dynamic";

const MusicToggle = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [lottie, setLottie] = useState(null);
  const audioRef = useRef(null);
  const lottieRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    import("lottie-web").then((lottieModule) => {
      setLottie(lottieModule.default);
    });
  }, []);

  useEffect(() => {
    if (!lottie || !containerRef.current) return;

    const animation = lottie.loadAnimation({
      container: containerRef.current,
      renderer: "svg",
      loop: true,
      autoplay: false,
      path: "https://assets5.lottiefiles.com/packages/lf20_jJJl6i.json",
    });

    lottieRef.current = animation;

    if (typeof window !== "undefined") {
      audioRef.current = new Audio("/demoted.mp3");
    }

    return () => {
      animation.destroy();
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, [lottie]);

  const toggleMusic = () => {
    if (!audioRef.current || !lottieRef.current) return;

    if (!isPlaying) {
      audioRef.current.play();
      lottieRef.current.playSegments([0, 120], true);
    } else {
      audioRef.current.pause();
      lottieRef.current.stop();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="music-toggle">
      <div className="music-toggle-btn" onClick={toggleMusic}>
        <div
          ref={containerRef}
          className="sound-bars"
          style={{ width: "20px", height: "20px" }}
        />
        <p>{isPlaying ? "on" : "off"}</p>
      </div>
    </div>
  );
};

export default dynamic(() => Promise.resolve(MusicToggle), {
  ssr: false,
});

